import React from "react";
import HeaderPage from "./header";
import FooterPage from "./footer";
import SidebarPage from "./sidebar";
import firebase from '../config';
import {Form} from 'reactstrap';
import FileUploader from "react-firebase-file-uploader";
import SimpleReactValidator from "simple-react-validator";
import ReactQuill from "react-quill";
import 'react-quill/dist/quill.snow.css';
import {Link} from "react-router-dom";

class AddFiles extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            file: '',
            name: '',
            description: '',
            permission:'',
            specifEmpName:'',
            FileList: [],
            isUploading: false,
            error_message: '',
            created_on: new Date().toLocaleString(),
            validError: true,
            EmployeesList:[],
            filter:'',
        }

        this.validator = new SimpleReactValidator({
            className: "text-danger",
            validators: {
                passwordvalid: {
                    message: "The :attribute must be at least 6 and at most 30 with 1 numeric,1 special charac" +
                            "ter and 1 alphabet.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /^(?=.*\d)(?=.*[!@#$%^&*])(?=.*[a-z]).{6,30}$/i) && params.indexOf(val) === -1);
                    }
                },
                passwordMismatch: {
                    message: "confirm password must match with password field.",
                    rule: function (val, params, validator) {
                        return document
                            .getElementById("password_input")
                            .value === val
                            ? true
                            : false;
                    }
                },
                AlertquantityStock: {
                    message: "alert quantity should be less than quantity Instock.",
                    rule: function (val, params, validator) {
                        var qty = document
                            .getElementById('quantity_stock')
                            .value;

                        if (Number(qty) > Number(val)) {

                            return true;
                        } else {
                            return false;
                        }

                    }
                },
                priceCostValid: {
                    message: "Selling price should be less than original price.",
                    rule: function (val, params, validator) {
                        var qty = document
                            .getElementById('price_cost')
                            .value;

                        if (Number(qty) > Number(val)) {

                            return true;
                        } else {
                            return false;
                        }

                    }
                },
                whitespace: {
                    message: "The :attribute not allowed first whitespace   characters.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /[^\s\\]/) && params.indexOf(val) === -1);
                    }
                },
                specialChar: {
                    message: "The :attribute not allowed special   characters.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /^[ A-Za-z0-9_@./#&+-]*$/i) && params.indexOf(val) === -1);
                    }
                },
                specialCharText: {
                    message: "The :attribute may only contain letters, dot and spaces.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /^[ A-Za-z_@./#&+-]*$/i) && params.indexOf(val) === -1);
                    }
                },

                zip: {
                    message: "Invalid Pin Code",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /^(\d{5}(\d{4})?)?$/i) && params.indexOf(val) === -1);
                    }
                },
                website: {
                    message: "The Url should be example.com ",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /(http(s)?:\/\/.)?(www\.)?[-a-zA-Z0-9@:%._\+~#=]{2,256}\.[a-z]{2,6}\b([-a-zA-Z0-9@:%_\+.~#?&//=]*)/g) && params.indexOf(val) === -1);
                    }
                },
                Fax: {
                    message: "Invalid fax number ",
                    rule: function (val, params, validator) {
                        return (validator.helpers.testRegex(val, /^([\w-\.]+)@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.)|(([\w-]+\.)+))([a-zA-Z]{2,4}|[0-9]{1,3})(\]?)$/i) && params.indexOf(val) === -1);
                    }
                }
            }
        });
    }

    componentDidMount() {
        var ref = firebase
            .database()
            .ref('Employees/');
        ref.once("value", snapshot => {
            const data = [];
            console.log(snapshot.val());
            snapshot.forEach(element => {
                const usersData = {
                    employeeID: element
                        .key
                        .toString(),

                    first_name: element
                        .val()
                        .first_name,
                    last_name: element
                        .val()
                        .last_name,
                    email: element
                        .val()
                        .email,
                    
                    date: element
                        .val()
                        .date
                }
                data.unshift(usersData);
            });

            this.setState({
                EmployeesList: data
            }, () => {
                console.log(this.state.EmployeesList, 'Employees');
                
            });
         
        })
    } 
    handleSubmit = (event) => {
        event.preventDefault();
        if (this.validator.allValid() && this.state.validError == true) {
            console.log(this.state.avatar);
            // var sessionId = sessionStorage.getItem("RoleId");
            // var userName = sessionStorage.getItem("userName");

            let dbCon = firebase
                .database()
                .ref('/Files');
            dbCon.push({
               
                file: this.state.file,
                name: this.state.name,
                description: this.state.description,
                permission: this.state.permission,
                specifEmpName: this.state.specifEmpName,
                created_on: this.state.created_on,
               
            });
            this
                .props
                .history
                .push("/FileList/Added");
        } else {
            this
                .validator
                .showMessages();
            this.forceUpdate();
        }

    };
    handleChange = (event) => {
        this.setState({
            [event.target.name]: event.target.value
        });
    };

