import React from "react";
import {Link} from "react-router-dom";
import HeaderPage from "./header";
import FooterPage from "./footer";
import swal from 'sweetalert';
import SidebarPage from "./sidebar";
import firebase from '../config';
import Pagination from "react-js-pagination";

import $ from 'jquery';
$.DataTable = require('datatables.net');
class MerchantAccount extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            gstList: [],
            loading: false,
            count: 1,
            activePage: "1",
            countPage: ""
        };
        this.deleteItem = this
            .deleteItem
            .bind(this);

    }

    componentDidMount() {
        this.setState({loading: true});
        var ref = firebase
            .database()
            .ref("admin_users/")
            .orderByChild("Role")
            .equalTo("Merchant");
        var refOrder = firebase
            .database()
            .ref("orders/");
        ref.on('value', snapshot => {
            const data = [];
            snapshot.forEach(childSnapShot => {

                const GSTData = {
                    cmsId: childSnapShot
                        .key
                        .toString(),
                    fullName: childSnapShot
                        .val()
                        .fullName,
                    email: childSnapShot
                        .val()
                        .email,
                    username: childSnapShot
                        .val()
                        .username,
                    password: childSnapShot
                        .val()
                        .password,
                    status: childSnapShot
                        .val()
                        .status,
                    createdAt: childSnapShot
                        .val()
                        .created_on
                };
                refOrder
                    .orderByChild("merchantId")
                    .equalTo(childSnapShot.key.toString())
                    .on('value', snap => {
                        GSTData.orders = snap.numChildren();
                    });
                data.unshift(GSTData);
            });

            this.setState({gstList: data, countPage: data.length, loading: false});
            console.log(this.state.gstList);
            this.interval = setTimeout(() => $('.paginationTable').DataTable(), 1000);
        });
    }
    componentWillUnmount() {

        clearTimeout(this.interval);
    }
    deleteItem = uid => {

        swal({title: "Are you sure?", text: "Do your really want to remove?", icon: "warning", buttons: true, dangerMode: true}).then(willDelete => {
            if (willDelete) {
                console.log(uid);
                var playersRef = firebase
                    .database()
                    .ref(`/admin_users/${uid}`);
                playersRef.remove();
                this
                    .props
                    .history
                    .push("/MerchantAccount/Deleted");

            } else {}
            // window.location.reload("/Home");

        });
    }

    onChange = (event) => {

        var cmsId = event.target.id;
        console.log(cmsId);
        this.setState({
            [event.target.name]: event.target.value
        });
        console.log(event.target.value);

        let dbCon = firebase
            .database()
            .ref(`/admin_users/${cmsId}`);
        console.log(dbCon);
        dbCon.update({status: event.target.value});
    };

    render() {

        var pathname = this
            .props
            .location
            .pathname
            .split("/");
        var alertName = pathname[2];
        console.log(alertName);
        return (
            <div>
                <SidebarPage/>

                <div className="all-content-wrapper">
                    <HeaderPage/>

                    <div className="breadcome-area">
                        <div className="container-fluid">
                            <div className="row">
                                <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                    <div className="breadcome-list">
                                        <div className="row">
                                            <div className="col-lg-6 col-md-6 col-sm-6 col-xs-6">
                                                <div className="breadcomb-wp">

                                                    <div className="breadcomb-ctn">
                                                        <h2>Admin Merchant</h2>
                                                    </div>
                                                </div>
                                            </div>

                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

                    <div className="product-status mg-b-30">
                        <div className="container-fluid">
                            <div className="row">
                                <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                    <div className="product-status-wrap">
                                        <h4>Merchant Account</h4>
                                        <div className="add-product">
                                            <Link to='/AddMerchantAccount'>Add Merchant Account</Link>
                                        </div>
                                        {alertName
                                            ? <div
                                                    className="alert alert-success display-none"
                                                    style={{
                                                    display: "block"
                                                }}>
                                                    <a data-dismiss="alert" href="#" aria-hidden="true" className="close">Ã—</a>
                                                    Categories {alertName}
                                                    Successfully
                                                </div>
                                            : ''}
                                        <table className="table table-bordered paginationTable">
                                            <thead>
                                                <tr>
                                                    <th>S.No</th>
                                                    <th>Full Name</th>
                                                    <th>Email</th>
                                                    <th>Orders</th>
                                                    <th>Status</th>
                                                    <th>Created on</th>
                                                    <th>Action</th>
                                                </tr>
                                            </thead>
                                            <tbody>

                                                {this.state.gstList && this
                                                    .state
                                                    .gstList
                                                    .map((data, index) => {
                                                        console.log(data.orders);

                                                        return (
                                                            <tr key={index}>
                                                                <td>{index + 1}</td>
                                                                <td>{data.fullName}</td>
                                                                <td>{data.email}
                                                                </td>
                                                                <td>
                                                                    <Link to={`/MerchantOrders/${data.cmsId}`}>{data.orders}</Link>
                                                                </td>
                                                                <td>
                                                                    <select
                                                                        name="status"
                                                                        onChange={this.onChange}
                                                                        id={data.cmsId}
                                                                        className="form-control pro-edt-select form-control-primary active">
                                                                        <option
                                                                            value="Active"
                                                                            defaultValue={data.status == "Active"
                                                                            ? true
                                                                            : false}>Active</option>
                                                                        <option
                                                                            value="InActive"
                                                                            defaultValue={data.status == "InActive"
                                                                            ? true
                                                                            : false}>InActive</option>

                                                                    </select>
                                                                </td>
                                                                <td>{data.createdAt}
                                                                </td>
                                                                <td>

                                                                    <Link to={`/EditMerchantAccount/${data.cmsId}`}>
                                                                        <button data-toggle="tooltip" title="Edit" className="pd-setting-ed">
                                                                            <i className="fa fa-pencil-square-o" aria-hidden="true"></i>
                                                                        </button>
                                                                    </Link>
                                                                    <Link to={`/ViewMerchantDetails/${data.cmsId}`}>
                                                                        <button data-toggle="tooltip" title="Edit" className="pd-setting-ed">
                                                                            <i className="fa fa-eye" aria-hidden="true"></i>
                                                                        </button>
                                                                    </Link>

                                                                    <button
                                                                        data-toggle="tooltip"
                                                                        title="Trash"
                                                                        className="pd-setting-ed"
                                                                        data-id={data.cmsId}
                                                                        onClick={this
                                                                        .deleteItem
                                                                        .bind(this, data.cmsId)}>
                                                                        <i className="fa fa-trash-o" aria-hidden="true"></i>
                                                                    </button>
                                                                </td>

                                                            </tr>
                                                        )

                                                    })}

                                            </tbody>
                                        </table>

                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

                </div>

                <FooterPage/>

            </div>
        );
    }
}

export default MerchantAccount;
