import React from "react";
import {Link} from "react-router-dom";
import HeaderPage from "./header";
import FooterPage from "./footer";
// import HomePage from "./home";
import SidebarPage from "./sidebar";
import firebase from '../config';
import Moment from 'moment';

import $ from 'jquery';
$.DataTable = require('datatables.net');

class Notifications extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            gstList: [],
            loading: false,
            count: 1,
            activePage: "1",
            countPage: ""
        };
        this.deleteItem = this
            .deleteItem
            .bind(this);

    }

    componentDidMount() {
        this.setState({loading: true});
        var ref = firebase
            .database()
            .ref("Notifications/");

        ref.on('value', snapshot => {
            const data = [];
            // console.log(snapshot); console.log(snapshot.val);

            snapshot.forEach(childSnapShot => {

                const GSTData = {
                    cmsId: childSnapShot
                        .key
                        .toString(),
                    pageTitle: childSnapShot
                        .val()
                        .pageTitle,
                    pageName: childSnapShot
                        .val()
                        .pageName,
                    created_on: childSnapShot
                        .val()
                        .created_on
                };

                data.unshift(GSTData);
            });

            this.setState({gstList: data, countPage: data.length, loading: false});
            console.log(this.state.gstList);
            this.interval = setTimeout(() => $('.paginationTable').DataTable(), 1000);
        });
    }
    componentWillUnmount() {
        //this.dataTable.destroy(true);
        clearTimeout(this.interval);
    }
    deleteItem = id => {

        console.log(id);
        var playersRef = firebase
            .database()
            .ref(`/Notifications/${id}`);
        playersRef.remove();
        this
            .props
            .history
            .push("/Notifications/Deleted");
    };

    handlePageChange(pageNumber) {
        console.log(`active page is ${pageNumber}`);
        this.setState({activePage: pageNumber});
    }

    render() {

        var pathname = this
            .props
            .location
            .pathname
            .split("/");
        var alertName = pathname[2];
        console.log(alertName);
        return (
            <div>
                <SidebarPage/>

                <div className="all-content-wrapper">
                    <HeaderPage/>

                    <div className="breadcome-area">
                        <div className="container-fluid">
                            <div className="row">
                                <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                    <div className="breadcome-list">
                                        <div className="row">
                                            <div className="col-lg-6 col-md-6 col-sm-6 col-xs-6">
                                                <div className="breadcomb-wp">

                                                    <div className="breadcomb-ctn">
                                                        <h2>Notifications</h2>
                                                    </div>
                                                </div>
                                            </div>

                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

                    <div className="product-status mg-b-30">
                        <div className="container-fluid">
                            <div className="row">
                                <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                    <div className="product-status-wrap">
                                        <h4>Notifications</h4>
                                        <div className="add-product">
                                            <Link to='/AddNotifications'>Add Notifications</Link>
                                        </div>
                                        {alertName
                                            ? <div
                                                    className="alert alert-success display-none"
                                                    style={{
                                                    display: "block"
                                                }}>
                                                    <a data-dismiss="alert" href="#" aria-hidden="true" className="close">×</a>
                                                    Notifications {alertName}
                                                    Successfully
                                                </div>
                                            : ''}
                                        <table className="table table-bordered paginationTable">
                                            <thead>
                                                <tr>
                                                    <th>S.No</th>
                                                    <th>Sent On</th>
                                                    <th>
                                                        title</th>
                                                    <th>Sent To</th>
                                                    <th>Action</th>
                                                </tr>
                                            </thead>
                                            <tbody>

                                                {this.state.gstList && this
                                                    .state
                                                    .gstList
                                                    .map((data, index) => {
                                                        console.log(data);

                                                        return (
                                                            <tr key={index}>
                                                                <td>{index + 1}</td>
                                                                <td>{Moment(data.created_on).format('D MMM Y')}</td>
                                                                <td>{data.pageName}</td>
                                                                <td>All</td>

                                                                <td>

                                                                    <Link to={`/EditNotification/${data.cmsId}`}>
                                                                        <button data-toggle="tooltip" title="Edit" className="pd-setting-ed">
                                                                            <i className="fa fa-pencil-square-o" aria-hidden="true"></i>
                                                                        </button>
                                                                    </Link>

                                                                    <button
                                                                        data-toggle="tooltip"
                                                                        title="Trash"
                                                                        className="pd-setting-ed"
                                                                        data-id={data.cmsId}
                                                                        onClick={this
                                                                        .deleteItem
                                                                        .bind(this, data.cmsId)}>
                                                                        <i className="fa fa-trash-o" aria-hidden="true"></i>
                                                                    </button>
                                                                </td>

                                                            </tr>
                                                        )

                                                    })}

                                            </tbody>
                                        </table>

                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

                </div>

                <FooterPage/>

            </div>
        );
    }
}

export default Notifications;
