import React from "react";
import FadeIn from "react-fade-in";
import {Link, withRouter} from 'react-router-dom';
import {Collapse} from 'react-bootstrap';
import firebase from '../config';

class SidebarPage extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            userRole: '',
            loading: false,
            orderStatusList: [],
            usePermList: []
        };
    }
    componentDidMount() {
        this.setState({loading: true});
        var sessionId = sessionStorage.getItem("RoleId");
        if (sessionId) {

            firebase
                .database()
                .ref('admin_users/' + sessionId)
                .on('value', snapshot => {
                    var Users = snapshot.val();
                    console.log(Users);
                    sessionStorage.setItem("userName", Users.fullName);
                    sessionStorage.setItem("userRole", Users.Role);

                    this.setState({userRole: Users.Role, loading: false});
                    console.log(this.state.userRole);
                    this.ordersSliders();

                });
        }

    }
    ordersSliders() {

        this.setState({loading: true});
        var ref = firebase
            .database()
            .ref("orderStatus");

        ref.on('value', snapshot => {
            const data = [];

            snapshot.forEach(childSnapShot => {

                const orderStatus = {
                    categoryId: childSnapShot
                        .key
                        .toString(),
                    orderStatus: childSnapShot
                        .val()
                        .orderStatus
                };

                data.push(orderStatus);
            });

            this.setState({orderStatusList: data, loading: false});

        });
    }
    componentWillMount() {
        var sessionId = sessionStorage.getItem("userRole");
        this.setState({loading: true});
        var ref = firebase
            .database()
            .ref("Role")
            .orderByChild("Role")
            .equalTo(sessionId);

        ref.on('value', snapshot => {
            const data = [];

            snapshot.forEach(childSnapShot => {

                const orderStatus = {
                    RoleId: childSnapShot
                        .key
                        .toString(),
                    dashboard: childSnapShot
                        .val()
                        .dashboard,
                    orders: childSnapShot
                        .val()
                        .orders,
                        allOrders: childSnapShot.val().allOrders,
                        newOrders: childSnapShot.val().newOrders,
                        processingOrders:childSnapShot.val().processingOrders,
                        dispatchedOrders: childSnapShot.val().dispatchedOrders,
                        deliveredOrders: childSnapShot.val().deliveredOrders,
                        completedOrders: childSnapShot.val().completedOrders,
                        refundOrders: childSnapShot.val().refundOrders,
                        cancelledOrders: childSnapShot.val().cancelledOrders,
                    products: childSnapShot
                        .val()
                        .products,
                    category: childSnapShot
                        .val()
                        .category,
                    subCategory: childSnapShot
                        .val()
                        .subCategory,
                    invoices: childSnapShot
                        .val()
                        .invoices,
                    abandonedCart: childSnapShot
                        .val()
                        .abandonedCart,
                    coupons: childSnapShot
                        .val()
                        .coupons,
                    merchantPayments: childSnapShot
                        .val()
                        .merchantPayments,
                    AdminStaff: childSnapShot
                        .val()
                        .AdminStaff,
                    merchantMangement: childSnapShot
                        .val()
                        .merchantMangement,
                    notifications: childSnapShot
                        .val()
                        .notifications,
                    users: childSnapShot
                        .val()
                        .users,
                    settings: childSnapShot
                        .val()
                        .settings,
                    Analytics: childSnapShot
                        .val()
                        .Analytics,
                    sliders: childSnapShot
                        .val()
                        .sliders,
                    enquiry: childSnapShot
                        .val()
                        .enquiry
                };

                data.push(orderStatus);
            });

            this.setState({usePermList: data[0], loading: false});
            console.log(this.state.usePermList);
            console.log(this.state.usePermList.orders);
        });
    }

    render() {

        return (

            <div>

                <div className="left-sidebar-pro">
                    <nav id="sidebar" className="">
                        <div className="sidebar-header">
                            <Link to='/Dashboard'>
                                <img src="/img/logo/gootes.png"/></Link>
                            {/* <strong> Shopality </strong> */}
                        </div>

                        <div className="left-custom-menu-adp-wrap comment-scrollbar">
                            <nav className="sidebar-nav left-sidebar-menu-pro">

                                <ul
                                    className="nav metismenu"
                                    style={{
                                    height: "543px",
                                    overflowY: "auto"
                                }}
                                    id="style-2">
                                    {this.state.usePermList.dashboard == "read&write" || this.state.usePermList.dashboard == "read"
                                        ? <li
                                                className={this.isPathActive('/Dashboard') || this.state.componentMenuOpen
                                                ? 'active'
                                                : null}>
                                                <Link to="/Dashboard">
                                                    <i className="fa fa-tachometer" aria-hidden="true"></i>
                                                    <span className="mini-click-non">
                                                        Dashboard</span>
                                                </Link>
                                            </li>
                                        : ""}

                                    {this.state.usePermList.orders == "read&write" || this.state.usePermList.orders == "read"? 
                                    <li
                                                className={this.isPathActive('/OrderListPage') || this.state.mapMenuOpen
                                                ? 'active'
                                                : null}>
                                                <a
                                                    className="has-arrow"
                                                    onClick={() => this.setState({
                                                    mapMenuOpen: !this.state.mapMenuOpen
                                                })}
                                                    data-toggle="collapse">

                                                    <i className="fa fa-file-text" aria-hidden="true"></i>
                                                    <span className="mini-click-non">
                                                        Orders</span>
                                                </a>
                                                <Collapse in={this.state.mapMenuOpen}>
                                                    <div>
                                                        <ul className="nav submenu-angl">
                                                        {this.state.usePermList.allOrders == "read&write" || this.state.usePermList.allOrders == "read"? 
                                                            <li
                                                                className={this.isPathActive('/orders/All')
                                                                ? 'active'
                                                                : null}>
                                                                <Link to='/orders/All'>All Orders</Link>
                                                            </li>:''}

                                                            {/* {this
                                                                .state
                                                                .orderStatusList
                                                                .map((data, index) => {
console.log(data.orderStatus);
                                                                    return (
                                                                        <li
                                                                            key={index}
                                                                            className={this.isPathActive('/orders/' + data.orderStatus)
                                                                            ? 'active'
                                                                            : null}>
                                                                            <Link to={'/orders/' + data.orderStatus}>{data.orderStatus}&nbsp;
                                                                                 Orders</Link>
                                                                        </li>

                                                                    )

                                                                })} */}
           {this.state.usePermList.newOrders == "read&write" || this.state.usePermList.newOrders == "read"?
                  <li className={this.isPathActive('/orders/New') ? 'active' : null}><Link to='/orders/New'>New Orders</Link></li>:''}
                 {this.state.usePermList.processingOrders == "read&write" || this.state.usePermList.processingOrders == "read"? <li className={this.isPathActive('/orders/Processing') ? 'active' : null}><Link to='/orders/Processing'>Processing Orders</Link></li>:''}
               {this.state.usePermList.dispatchedOrders == "read&write" || this.state.usePermList.dispatchedOrders == "read"?   <li className={this.isPathActive('/orders/Dispatched') ? 'active' : null}><Link to='/orders/Dispatched'>Dispatched Orders</Link></li>:''}
               {this.state.usePermList.deliveredOrders == "read&write" || this.state.usePermList.deliveredOrders == "read"?  <li className={this.isPathActive('/orders/Delivered') ? 'active' : null}><Link to='/orders/Delivered'>Delivered Orders</Link></li>:''}
               {this.state.usePermList.completedOrders == "read&write" || this.state.usePermList.completedOrders == "read"? <li className={this.isPathActive('/orders/Completed') ? 'active' : null}><Link to='/orders/Completed'>Completed Orders</Link></li>:''}
               {this.state.usePermList.refundOrders == "read&write" || this.state.usePermList.refundOrders == "read"?  <li className={this.isPathActive('/orders/Refund') ? 'active' : null}><Link to='/orders/Refund'>Refuned Orders</Link></li>:''}
               {this.state.usePermList.cancelledOrders == "read&write" || this.state.usePermList.cancelledOrders == "read"? <li className={this.isPathActive('/orders/Cancelled') ? 'active' : null}><Link to='/orders/Cancelled'>Cancelled Orders</Link></li>:''}
                                                        </ul>
                                                    </div>
                                                </Collapse>
                                            </li>
                                        : ""}
                                    {this.state.usePermList.products == "read&write" || this.state.usePermList.products == "read"
                                        ? <li
                                                className={this.isPathActive('/MerchantListPage') || this.state.componentMenuOpen
                                                ? 'active'
                                                : null}>
                                                <a
                                                    className="has-arrow"
                                                    onClick={() => this.setState({
                                                    componentMenuOpen: !this.state.componentMenuOpen
                                                })}
                                                    data-toggle="collapse">
                                                    <i className="fa fa-th" aria-hidden="true"></i>
                                                    <span className="mini-click-non">
                                                        Products</span>
                                                </a>
                                                <Collapse in={this.state.componentMenuOpen}>
                                                    <div>
                                                        <ul className="nav">

                                                            <li
                                                                className={this.isPathActive('/ProductListPage')
                                                                ? 'active'
                                                                : null}>
                                                                <Link to="/ProductListPage">
                                                                    Product List</Link>
                                                            </li>
                                                            {this.state.usePermList.category == "read&write" || this.state.usePermList.category == "read"
                                        ?
                                                            <li
                                                                className={this.isPathActive('/CategoryListPage')
                                                                ? 'active'
                                                                : null}>
                                                                <Link to="/CategoryListPage">
                                                                    Categories</Link>
                                                            </li>: ''}
                                                            {this.state.usePermList.subCategory == "read&write" || this.state.usePermList.subCategory == "read"
                                        ?
                                                            <li
                                                                className={this.isPathActive('/SubCategoryListPage')
                                                                ? 'active'
                                                                : null}>
                                                                <Link to="/SubCategoryListPage">
                                                                    Sub Categories</Link>
                                                            </li>: ''}

                                                        </ul>
                                                    </div>
                                                </Collapse>
                                            </li>
                                        : ''}
                                    {this.state.usePermList.invoices == "read&write" || this.state.usePermList.invoices == "read"
                                        ? <li
                                                className={this.isPathActive('/Invoices') || this.state.componentMenuOpen
                                                ? 'active'
                                                : null}>
                                                <Link to="/Invoices">
                                                    <i className="fa fa-file-text" aria-hidden="true"></i>
                                                    <span className="mini-click-non">
                                                        Invoices</span>
                                                </Link>
                                            </li>
                                        : ''}
                                    {/* {this.state.usePermList.abandonedCart == "read&write" || this.state.usePermList.abandonedCart == "read"
                                        ? <li
                                                className={this.isPathActive('/OrderListPending') || this.state.componentMenuOpen
                                                ? 'active'
                                                : null}>
                                                <Link to="/OrderListPending">
                                                    <i className="fa fa-cart-plus" aria-hidden="true"></i>
                                                    <span className="mini-click-non">
                                                        Abandoned Cart</span>
                                                </Link>
                                            </li>
                                        : ''} */}
                                    {/* {this.state.usePermList.Analytics == "read&write" || this.state.usePermList.Analytics == "read"
                                        ? <li
                                                className={this.isPathActive('/GoogleAnalytics') || this.state.analyticsOpen
                                                ? 'active'
                                                : null}>
                                                <a
                                                    className="has-arrow"
                                                    onClick={() => this.setState({
                                                    analyticsOpen: !this.state.analyticsOpen
                                                })}
                                                    data-toggle="collapse">

                                                    <i className="fa fa-bar-chart" aria-hidden="true"></i>
                                                    <span className="mini-click-non">
                                                        Analytics</span>
                                                </a>
                                                <Collapse in={this.state.analyticsOpen}>
                                                    <div>
                                                        <ul className="nav submenu-angl">
                                                            <li
                                                                className={this.isPathActive('/GoogleAnalytics')
                                                                ? 'active'
                                                                : null}>
                                                                <Link to='/GoogleAnalytics'>
                                                                    Google Analytics</Link>
                                                            </li>
                                                        </ul>
                                                    </div>
                                                </Collapse>
                                            </li>
                                        : ''} */}

                                    {this.state.usePermList.coupons == "read&write" || this.state.usePermList.coupons == "read"
                                        ? <li
                                                className={this.isPathActive('/Coupons') || this.state.componentMenuOpen
                                                ? 'active'
                                                : null}>
                                                <Link to="/Coupons">
                                                    <i className="fa fa-tag" aria-hidden="true"></i>
                                                    <span className="mini-click-non">
                                                        Coupons</span>
                                                </Link>
                                            </li>
                                        : ''}
                                    {this.state.usePermList.merchantPayments == "read&write" || this.state.usePermList.merchantPayments == "read"
                                        ? <li
                                                className={this.isPathActive('/MerchantPaymentsList') || this.state.componentMenuOpen
                                                ? 'active'
                                                : null}>
                                                <Link to="/MerchantPaymentsList">
                                                    <i className="fa fa-credit-card-alt" aria-hidden="true"></i>
                                                    <span className="mini-click-non">
                                                         Payments</span>
                                                </Link>
                                            </li>
                                        : ''}
                                    {this.state.usePermList.AdminStaff == "read&write" || this.state.usePermList.AdminStaff == "read"
                                        ? <li
                                                className={this.isPathActive('/AdminStaff') || this.state.componentMenuOpen
                                                ? 'active'
                                                : null}>
                                                <Link to="/AdminStaff">
                                                    <i className="fa fa-users" aria-hidden="true"></i>
                                                    <span className="mini-click-non">
                                                        Admin Staff</span>
                                                </Link>
                                            </li>
                                        : ''}
                                    {this.state.usePermList.merchantMangement == "read&write" || this.state.usePermList.merchantMangement == "read"
                                        ? <li
                                                className={this.isPathActive('/MerchantAccount') || this.state.componentMenuOpen
                                                ? 'active'
                                                : null}>
                                                <Link to="/MerchantAccount">
                                                    <i className="fa fa-clock-o" aria-hidden="true"></i>
                                                    <span className="mini-click-non">
                                                        Merchant Management</span>
                                                </Link>
                                            </li>
                                        : ''}
                                    {this.state.usePermList.notifications == "read&write" || this.state.usePermList.notifications == "read"
                                        ? <li
                                                className={this.isPathActive('/Notifications') || this.state.componentMenuOpen
                                                ? 'active'
                                                : null}>
                                                <Link to="/Notifications">
                                                    <i className="fa fa-bell" aria-hidden="true"></i>
                                                    <span className="mini-click-non">
                                                        Notifications</span>
                                                </Link>
                                            </li>
                                        : ''}
                                    {this.state.usePermList.sliders == "read&write" || this.state.usePermList.sliders == "read"
                                        ? <li
                                                className={this.isPathActive('/Slider') || this.state.slidersMenuOpen
                                                ? 'active'
                                                : null}>
                                                <a
                                                    className="has-arrow"
                                                    onClick={() => this.setState({
                                                    slidersMenuOpen: !this.state.slidersMenuOpen
                                                })}
                                                    data-toggle="collapse">

                                                    <i className="fa fa-sliders" aria-hidden="true"></i>
                                                    <span className="mini-click-non">
                                                        Sliders</span>
                                                </a>
                                                <Collapse in={this.state.slidersMenuOpen}>
                                                    <div>
                                                        <ul className="nav submenu-angl">
                                                            <li
                                                                className={this.isPathActive('/Slider')
                                                                ? 'active'
                                                                : null}>
                                                                <Link to='/Slider'>
                                                                    Sliders</Link>
                                                            </li>
                                                            <li
                                                                className={this.isPathActive('/MultipleSliderList')
                                                                ? 'active'
                                                                : null}>
                                                                <Link to='/MultipleSliderList'>
                                                                    Multiple Sliders</Link>
                                                            </li>
                                                            <li
                                                                className={this.isPathActive('/SubSliderList')
                                                                ? 'active'
                                                                : null}>
                                                                <Link to='/SubSliderList'>
                                                                    Sub Sliders</Link>
                                                            </li>

                                                        </ul>
                                                    </div>
                                                </Collapse>
                                            </li>
                                        : ''}
                                    {this.state.usePermList.users == "read&write" || this.state.usePermList.users == "read"
                                        ? <li
                                                className={this.isPathActive('/Users') || this.state.componentMenuOpen
                                                ? 'active'
                                                : null}>
                                                <Link to="/Users">
                                                    <i className="fa fa-user" aria-hidden="true"></i>
                                                    <span className="mini-click-non">
                                                        Users</span>
                                                </Link>
                                            </li>
                                        : ''}
                                    {this.state.usePermList.settings == "read&write" || this.state.usePermList.settings == "read"
                                        ? <li
                                                className={this.isPathActive('/GSTListPage') || this.state.tableMenuOpen
                                                ? 'active'
                                                : null}>
                                                <a
                                                    className="has-arrow"
                                                    onClick={() => this.setState({
                                                    tableMenuOpen: !this.state.tableMenuOpen
                                                })}
                                                    data-toggle="collapse">
                                                    <i className="fa fa-cog" aria-hidden="true"></i>
                                                    <span className="mini-click-non">
                                                        Settings</span>
                                                </a>
                                                <Collapse in={this.state.tableMenuOpen}>
                                                    <div>
                                                        <ul className="nav submenu-angl">
                                                            <li>
                                                                <Link to='/GSTListPage'>
                                                                    GST</Link>
                                                            </li>
                                                            <li>
                                                                <Link to='/RoleListPage'>
                                                                    Role</Link>
                                                            </li>
                                                            <li>
                                                                <Link to='/PincodeList'>
                                                                    Pin Code</Link>
                                                            </li>
                                                            <li>
                                                                <Link to='/ShippingCharges'>
                                                                    Shipping Charges</Link>
                                                            </li>
                                                            <li >
                                                                <Link to="/CMSPages">
                                                                    CMS Pages</Link>
                                                            </li>
                                                            <li>
                                                                <Link to='/ServiceProviderListPage'>
                                                                    Courier Service Providers</Link>
                                                            </li>
                                                            <li>
                                                                <Link to='/SiteSettingListPage'>
                                                                    Site settings</Link>
                                                            </li>
                                                            <li>
                                                                <Link to='/EmailCOntentListPage'>
                                                                    Email Content</Link>
                                                            </li>
                                                            <li>
                                                                <Link to='/SocialAccounts'>
                                                                    Social Media Accounts</Link>
                                                            </li>
                                                            <li>
                                                                <Link to='/FooterLinks'>
                                                                    Footer Links</Link>
                                                            </li>

                                                        </ul>
                                                    </div>
                                                </Collapse>
                                            </li>
                                        : ''}
                                    {this.state.usePermList.enquiry == "read&write" || this.state.usePermList.enquiry == "read"
                                        ? <li
                                                className={this.isPathActive('/websiteEnquiries') || this.state.componentMenuOpen
                                                ? 'active'
                                                : null}>
                                                <Link to="/websiteEnquiries">
                                                    <i className="fa fa-globe" aria-hidden="true"></i>
                                                    <span className="mini-click-non">
                                                        Website Enquiries</span>
                                                </Link>
                                            </li>
                                        : ''}
                                    {this.state.userRole == "Merchant"
                                        ? <li
                                                className={this.isPathActive('/MerchantAccountDetails') || this.state.componentMenuOpen
                                                ? 'active'
                                                : null}>
                                                <Link to="/MerchantAccountDetails">
                                                    <i className="fa fa-envelope" aria-hidden="true"></i>
                                                    <span className="mini-click-non">
                                                         Account Details</span>
                                                </Link>
                                            </li>
                                        : ''}

                                </ul>

                            </nav>
                        </div>

                    </nav>
                </div>
            </div>
        );
    }

    isPathActive(path) {
        return this
            .props
            .location
            .pathname
            .startsWith(path);
    }
}

export default withRouter(SidebarPage);
