import React from "react";
import HeaderPage from "./header";
import FooterPage from "./footer";
import SidebarPage from "./sidebar";
import firebase from '../config';
import {Form} from 'reactstrap';
import FileUploader from 'react-firebase-file-uploader';
import SimpleReactValidator from "simple-react-validator";
import ReactQuill from "react-quill";
import 'react-quill/dist/quill.snow.css';
import {Link} from "react-router-dom";

class EditProductPage extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            productName: '',
            category: '',
            subCategory: '',
            gstValue: '',
            pincode: '',
            price: '',
            offerPrice: '',
            sellingPrice: '',
            quantityStock: '',
            packagingCost: '',
            alertQuantity: '',
            uploadImage: '',
            content: '',
            cancellation: '',
            theme: 'snow',
            categoryList: [],
            subCategoryList: [],
            avatar: "",
            isUploading: false,
            progress: 0,
            avatarURL: "",
            filenames: [],
            downloadURLs: [],
            uploadProgress: 0,
            error_message: '',
            date: new Date().toLocaleString(),
            validError: true
        }

        this.validator = new SimpleReactValidator({
            className: "text-danger",
            validators: {
                passwordvalid: {
                    message: "The :attribute must be at least 6 and at most 30 with 1 numeric,1 special charac" +
                            "ter and 1 alphabet.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /^(?=.*\d)(?=.*[!@#$%^&*])(?=.*[a-z]).{6,30}$/i) && params.indexOf(val) === -1);
                    }
                },
                passwordMismatch: {
                    message: "confirm password must match with password field.",
                    rule: function (val, params, validator) {
                        return document
                            .getElementById("password_input")
                            .value === val
                            ? true
                            : false;
                    }
                },
                AlertquantityStock: {
                    message: "alert quantity should be less than quantity Instock.",
                    rule: function (val, params, validator) {
                        var qty = document
                            .getElementById('quantity_stock')
                            .value;

                        if (Number(qty) > Number(val)) {

                            return true;
                        } else {
                            return false;
                        }

                    }
                },
                productPriceMismatch: {
                    message: "Selling price should be less than original price.",
                    rule: function (val, params, validator) {
                        var qty = document
                            .getElementById('product_price')
                            .value;

                        if (Number(qty) > Number(val)) {

                            return true;
                        } else {
                            return false;
                        }

                    }
                },
                whitespace: {
                    message: "The :attribute not allowed first whitespace   characters.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /[^\s\\]/) && params.indexOf(val) === -1);
                    }
                },
                specialChar: {
                    message: "The :attribute not allowed special   characters.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /^[ A-Za-z0-9_@./#&+-]*$/i) && params.indexOf(val) === -1);
                    }
                },
                specialCharText: {
                    message: "The :attribute may only contain letters, dot and spaces.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /^[ A-Za-z_@./#&+-]*$/i) && params.indexOf(val) === -1);
                    }
                },

                zip: {
                    message: "Invalid Pin Code",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /^(\d{5}(\d{4})?)?$/i) && params.indexOf(val) === -1);
                    }
                },
                website: {
                    message: "The Url should be example.com ",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /(http(s)?:\/\/.)?(www\.)?[-a-zA-Z0-9@:%._\+~#=]{2,256}\.[a-z]{2,6}\b([-a-zA-Z0-9@:%_\+.~#?&//=]*)/g) && params.indexOf(val) === -1);
                    }
                },
                Fax: {
                    message: "Invalid fax number ",
                    rule: function (val, params, validator) {
                        return (validator.helpers.testRegex(val, /^([\w-\.]+)@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.)|(([\w-]+\.)+))([a-zA-Z]{2,4}|[0-9]{1,3})(\]?)$/i) && params.indexOf(val) === -1);
                    }
                }
            }
        });
    }

    componentDidMount() {

        const {productID} = this.props.match.params;
        console.log(productID);
        this.setState({loading: true});
        var ref = firebase
            .database()
            .ref(`products/${productID}`);

        ref.on('value', snapshot => {
            var products = snapshot.val();
            console.log(products);

            this.setState({
                productName: products.productName,
                category: products.category,
                subCategory: products.subCategory,
                gstValue: products.gstValue,
                pincode: products.pincode,
                price: products.price,
                cancellation: products.cancellation,
                offerPrice: products.offerPrice,
                sellingPrice: products.sellingPrice,
                quantityStock: products.quantityStock,
                alertQuantity: products.alertQuantity,
                packagingCost: products.packagingCost,
                downloadURLs: products.uploadImage,
                avatarURL: products.frontImage,
                content: products.description,
                loading: false
            }, () => {
                this.setSubCategoryList(this.state.category)
            });

        });
        {
            this.categories()
        }
        console.log(this.state.category);

    }
    categories = (event) => {
        this.setState({loading: true});
        var ref = firebase
            .database()
            .ref("category/");

        ref.on('value', snapshot => {
            const data = [];

            snapshot.forEach(childSnapShot => {

                const Categories = {
                    categoryId: childSnapShot
                        .key
                        .toString(),
                    category: childSnapShot
                        .val()
                        .category,

                    created_on: childSnapShot
                        .val()
                        .created_on
                };

                data.push(Categories);
            });

            this.setState({categoryList: data, loading: false});
            console.log(this.state.categoryList);
        });

        var GstData = firebase
            .database()
            .ref("GST/");

        GstData.on('value', snapshot => {
            const data = [];

            snapshot.forEach(childSnapShot => {

                const GSTList = {
                    gstId: childSnapShot
                        .key
                        .toString(),
                    gst: childSnapShot
                        .val()
                        .gst,
                    created_on: childSnapShot
                        .val()
                        .created_on
                };

                data.push(GSTList);
            });

            this.setState({GSTList: data, loading: false});
            console.log(this.state.GSTList);
        });

    };
    onChange = (event) => {

        this.setState({
            [event.target.name]: event.target.value
        });
    };
    productNameChange = (event) => {
        this.setState({
            [event.target.name]: event.target.value
        });

        var ref = firebase
            .database()
            .ref('products/')
            .orderByChild("productName")
            .equalTo(event.target.value);
        ref.on('value', snapshot => {
            var user_exist = snapshot.numChildren();
            console.log(user_exist);

            if (user_exist > 0) {

                this.setState({error_message: "Product Name is already exist", validError: false});
            } else {
                this.setState({error_message: "", validError: true});

            }

        })

    };
    setCategoryName = (e) => {
        console.log(e.target.value);

        let selectedCategoryName = e.target.value;

        {
            this.setSubCategoryList(selectedCategoryName)
        }

    };
    setSubCategoryList = (selectedCategoryName) => {

        console.log(selectedCategoryName);
        var ref = firebase
            .database()
            .ref("sub_category")
            .orderByChild("category")
            .equalTo(selectedCategoryName);

        ref.on('value', snapshot => {

            const data = [];

            snapshot.forEach(childSnapShot => {

                const GSTList = {
                    subCategoryId: childSnapShot
                        .key
                        .toString(),
                    subCategory: childSnapShot
                        .val()
                        .subCategory
                };

                data.push(GSTList);
            });

            this.setState({subcategoryList: data, category: selectedCategoryName, loading: false});
            console.log(this.state.subcategoryList);
        });
    }

    handleChange = (value) => {
        this.setState({content: value})
    };
    handleUploadStart = () => this.setState({isUploading: true, uploadProgress: 0, downloadURLs: ''});
    handleFrontImageUploadStart = () => this.setState({isUploading: true, uploadProgress: 0, avatarURL: ''});

    handleProgress = progress => this.setState({uploadProgress: progress});

    handleUploadError = error => {
        this.setState({
            isUploading: false
            // Todo: handle error
        });
        console.error(error);
    };
    handleFrontImageUploadSuccess = filename => {
        this.setState({avatar: filename, progress: 100, isUploading: false});
        firebase
            .storage()
            .ref("images")
            .child(filename)
            .getDownloadURL()
            .then(url => this.setState({avatarURL: url}));
    };
    handleUploadSuccess = async filename => {
        const downloadURL = await firebase
            .storage()
            .ref("images")
            .child(filename)
            .getDownloadURL();

        this.setState(oldState => ({
            filenames: [
                ...oldState.filenames,
                filename
            ],
            downloadURLs: [
                ...oldState.downloadURLs,
                downloadURL
            ],
            uploadProgress: 100,
            isUploading: false
        }));
        console.log(this.state.downloadURLs);
    };

    handleSubmit = (event) => {

        event.preventDefault();
        if (this.validator.allValid() && this.state.validError == true) {

            const {productID} = this.props.match.params;
            var sessionId = sessionStorage.getItem("RoleId");
            var userName = sessionStorage.getItem("userName");

            let dbCon = firebase
                .database()
                .ref(`products/${productID}`);
            dbCon.update({
                productName: this.state.productName,
                userName: userName,
                category: this.state.category,
                subCategory: this.state.subCategory,
                gstValue: this.state.gstValue,
                pincode: this.state.pincode,
                cancellation: this.state.cancellation,
                packagingCost: this.state.packagingCost,
                price: this.state.price,
                offerPrice: this.state.offerPrice,
                sellingPrice: this.state.sellingPrice,
                quantityStock: this.state.quantityStock,

                alertQuantity: this.state.alertQuantity,
                frontImage: this.state.avatarURL,
                uploadImage: this.state.downloadURLs,
                description: this.state.content,
                sessionId: sessionId,
                created_on: this.state.date
            });

            this
                .props
                .history
                .push("/ProductListPage/Updated");
        } else {
            this
                .validator
                .showMessages();
            this.forceUpdate();
        }
    };

    render() {

        return (
            <div>
                <SidebarPage/>

                <div className="all-content-wrapper">
                    <HeaderPage/>

                    <div className="breadcome-area">
                        <div className="container-fluid">
                            <div className="row">
                                <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                    <div className="breadcome-list">
                                        <div className="row">
                                            <div className="col-lg-6 col-md-6 col-sm-6 col-xs-6">
                                                <div className="breadcomb-wp">

                                                    <div className="breadcomb-ctn">
                                                        <h2>Products</h2>
                                                    </div>
                                                </div>
                                            </div>

                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

                    <div className="single-product-tab-area mg-b-30">

                        <div className="single-pro-review-area">
                            <div className="container-fluid">
                                <div className="row">
                                    <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                        <div className="review-tab-pro-inner">

                                            <h4>Edit Products</h4>

                                            <div className="product-tab-list tab-pane fade active in">
                                                <Form onSubmit={this.handleSubmit} id="Add-products">

                                                    <div className="row m-b-20">
                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                            <label>Product Name<span style={{
                color: 'red'
            }}>*</span>
                                                            </label>
                                                            <input
                                                                type="text"
                                                                className="form-control"
                                                                placeholder=""
                                                                name="productName"
                                                                onChange={this.productNameChange}
                                                                value={this.state.productName}/> {this
                                                                .validator
                                                                .message("Product Name", this.state.productName, "required|whitespace|min:2|max:70")}
                                                            <div className="text-danger">
                                                                {" "}
                                                                {this.state.error_message}
                                                            </div>
                                                        </div>

                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                            <label>Select Category<span style={{
                color: 'red'
            }}>*</span>
                                                            </label>
                                                            <select
                                                                className="form-control pro-edt-select form-control-primary"
                                                                name="category"
                                                                value={this.state.category}
                                                                onChange={this.setCategoryName}>
                                                               
                                                                {this.state.categoryList && this
                                                                    .state
                                                                    .categoryList
                                                                    .map((data, index) => {

                                                                        return (
                                                                            <option value={data.category} key={index} selected={data.category == this.state.category}>{data.category}</option>
                                                                        )
                                                                       

                                                                    })}

                                                            </select>
                                                            {
                                                                            this
                                                                                .validator
                                                                                .message("Select category", this.state.category, "required")
                                                                        }
                                                        </div>
                                                    </div>

                                                    <div className="row m-b-20">
                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                            <label>Select Sub Category<span style={{
                color: 'red'
            }}>*</span>
                                                            </label>
                                                            <select
                                                                className="form-control pro-edt-select form-control-primary"
                                                                name="subCategory"
                                                                value={this.state.subCategory}
                                                                onChange={this.onChange}>
                                                                {this.state.subcategoryList && this
                                                                    .state
                                                                    .subcategoryList
                                                                    .map((data, index) => {

                                                                        return (
                                                                            <option value={data.subCategory} key={index} selected={data.subCategory == this.state.subCategory}>{data.subCategory}</option>
                                                                        )

                                                                    })}
                                                               
                                                            </select>
                                                            {this
                                                                    .validator
                                                                    .message("Select Sub category", this.state.subCategory, "required")
}

                                                        </div>
                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                            <label>GST</label>

                                                            <select
                                                                name="select"
                                                                className="form-control pro-edt-select form-control-primary"
                                                                name="gstValue"
                                                                value={this.state.gstValue}
                                                                onChange={this.onChange}>
                                                                
                                                                {this.state.GSTList && this
                                                                    .state
                                                                    .GSTList
                                                                    .map((data, index) => {

                                                                        return (
                                                                            <option value={data.gst} key={index} selected={data.gst == this.state.gstValue}>{data.gst}</option>
                                                                        )
                                                                        
                                                                    })}

                                                            </select>
{
    this
        .validator
        .message("Select GST", this.state.gstValue, "required")
}
                                                        </div>

                                                    </div>
                                                    <div className="row m-b-20"></div>

                                                    <div className="row m-b-20">
                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                            <label>Price<span style={{
                color: 'red'
            }}>*</span>
                                                            </label>
                                                            <input
                                                                type="text"
                                                                className="form-control"
                                                                placeholder=""
                                                                name="price"
                                                                id="product_price"
                                                                onChange={this.onChange}
                                                                value={this.state.price}/> {this
                                                                .validator
                                                                .message("Price Value", this.state.price, "required|numeric|min:1|max:5")}

                                                        </div>

                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                            <label>Selling Price</label>
                                                            <input
                                                                type="text"
                                                                className="form-control"
                                                                placeholder=""
                                                                name="sellingPrice"
                                                                onChange={this.onChange}
                                                                value={this.state.sellingPrice}/> {this
                                                                .validator
                                                                .message(" Selling Price", this.state.sellingPrice, "required|productPriceMismatch|numeric|min:1|max:5")}

                                                        </div>
                                                    </div>

                                                    <div className="row m-b-20">

                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                            <label>Pin Code<span style={{
                color: 'red'
            }}>*</span>
                                                            </label>
                                                            <input
                                                                type="text"
                                                                className="form-control"
                                                                placeholder=""
                                                                name="pincode"
                                                                onChange={this.onChange}
                                                                value={this.state.pincode}/> {this
                                                                .validator
                                                                .message("Pin Code", this.state.pincode, "required|numeric|min:5|max:6")}

                                                        </div>
                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                            <label>Quantity Instock<span style={{
                color: 'red'
            }}>*</span>
                                                            </label>
                                                            <input
                                                                type="text"
                                                                className="form-control"
                                                                placeholder=""
                                                                id="quantity_stock"
                                                                name="quantityStock"
                                                                onChange={this.onChange}
                                                                value={this.state.quantityStock}/> {this
                                                                .validator
                                                                .message(" Quantity Price", this.state.quantityStock, "required|numeric|min:1|max:5")}

                                                        </div>
                                                    </div>

                                                    {/* <div className="row m-b-20">
                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                            <label>Packaging Cost<span style={{
                color: 'red'
            }}>*</span>
                                                            </label>
                                                            <input
                                                                type="text"
                                                                className="form-control"
                                                                placeholder=""
                                                                name="packagingCost"
                                                                onChange={this.onChange}
                                                                value={this.state.packagingCost}/> {this
                                                                .validator
                                                                .message("Packaging Cost", this.state.packagingCost, "required|numeric|min:1|max:5")}

                                                        </div>
                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                            <label>Alert Quantity<span style={{
                color: 'red'
            }}>*</span>
                                                            </label>
                                                            <input
                                                                type="text"
                                                                className="form-control"
                                                                placeholder=""
                                                                name="alertQuantity"
                                                                onChange={this.onChange}
                                                                value={this.state.alertQuantity}/> {this
                                                                .validator
                                                                .message("Alert Quantity", this.state.alertQuantity, "required|AlertquantityStock|numeric|min:1|max:5")}

                                                        </div>

                                                    </div> */}

                                                    <div className="row m-b-20">
                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">

                                                            <div className="dz-message needsclick download-custom">
                                                                <i className="fa fa-cloud-download" aria-hidden="true"></i>
                                                                <h2>Front Image Upload<span style={{
                color: 'red'
            }}>*</span>
                                                                </h2>

                                                            </div>
                                                            {this.state.isUploading && <p>Progress: {this.state.progress}</p>}
                                                            {this.state.avatarURL && <img
                                                                src={this.state.avatarURL}
                                                                style={{
                                                                height: "200px",
                                                                width: "250px"
                                                            }}/>}
                                                            <FileUploader
                                                                accept="image/*"
                                                                name="avatar"
                                                                randomizeFilename
                                                                storageRef={firebase
                                                                .storage()
                                                                .ref("images")}
                                                                onUploadStart={this.handleFrontImageUploadStart}
                                                                onUploadError={this.handleUploadError}
                                                                onUploadSuccess={this.handleFrontImageUploadSuccess}
                                                                onProgress={this.handleProgress}/> {this
                                                                .validator
                                                                .message("Front Image Upload", this.state.avatarURL, "required")}

                                                        </div>
                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                            <label>Select Cancellation Product<span style={{
                color: 'red'
            }}>*</span>
                                                            </label>
                                                            <select
                                                                name="cancellation"
                                                                onChange={this.onChange}
                                                                value={this.state.cancellation}
                                                                className="form-control pro-edt-select form-control-primary">
                                                                <option>Select Cancellation</option>
                                                                <option value="Yes">Yes</option>
                                                                <option value="No">No</option>
                                                                {this
                                                                    .validator
                                                                    .message("Select Cancellation", this.state.cancellation, "required")
}
                                                            </select>

                                                        </div>

                                                    </div>

                                                    <div className="row m-b-20">
                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                            <div className="dropzone-pro">
                                                                <div id="dropzone">

                                                                    <div className="dz-message needsclick download-custom">
                                                                        <i className="fa fa-cloud-download" aria-hidden="true"></i>
                                                                        <h2>Upload multiple Product Images<span style={{
                color: 'red'
            }}>*</span>
                                                                        </h2>

                                                                    </div>

                                                                    {this.state.downloadURLs && <p>Filenames: {this
                                                                            .state
                                                                            .filenames
                                                                            .join(", ")}</p>}

                                                                    <div>
                                                                        {this.state.downloadURLs && this
                                                                            .state
                                                                            .downloadURLs
                                                                            .map((downloadURL, i) => {
                                                                                return <img
                                                                                    key={i}
                                                                                    src={downloadURL}
                                                                                    style={{
                                                                                    height: "200px",
                                                                                    width: "250px"
                                                                                }}/>;
                                                                            })}
                                                                    </div>
                                                                    <FileUploader
                                                                        accept="image/*"
                                                                        name="image-uploader-multiple"
                                                                        randomizeFilename
                                                                        storageRef={firebase
                                                                        .storage()
                                                                        .ref("images")}
                                                                        onUploadStart={this.handleUploadStart}
                                                                        onUploadError={this.handleUploadError}
                                                                        onUploadSuccess={this.handleUploadSuccess}
                                                                        onProgress={this.handleProgress}
                                                                        multiple/> {this
                                                                        .validator
                                                                        .message("Upload Product Image", this.state.downloadURLs, "required")}

                                                                </div>
                                                            </div>

                                                        </div>

                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                            <label>Description<span style={{
                color: 'red'
            }}>*</span>
                                                            </label>

                                                            {/* <textarea
                                                            className="form-control"
                                                            style={{
                                                            height: "90px"
                                                        }}
                                                            name="description"
                                                            onChange={this.onChange}
                                                            value={this.state.description}></textarea> */}
                                                            <ReactQuill
                                                                theme={this.state.theme}
                                                                value={this.state.content}
                                                                placeholder='Enter Text'
                                                                onChange={this.handleChange}
                                                                className="add-new-post__editor mb-1"/> {this
                                                                .validator
                                                                .message("Description", this.state.content, "required")}

                                                        </div>
                                                    </div>

                                                    <div className="row">
                                                        <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                                            <div className="text-center custom-pro-edt-ds">
                                                                <button
                                                                    type="submit"
                                                                    className="btn btn-ctl-bt waves-effect waves-light m-r-10">Save
                                                                </button>
                                                                <Link to="/ProductListPage">
                                                                    <button type="button" className="btn btn-ctl-bt waves-effect waves-light">Cancel
                                                                    </button>
                                                                </Link>
                                                            </div>
                                                        </div>
                                                    </div>
                                                </Form>
                                            </div>

                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
                <FooterPage/>

            </div>
        );
    }
}

export default EditProductPage;
