import React from "react";
import {Link} from "react-router-dom";
import FadeIn from "react-fade-in";
import axios from "axios";
import HeaderPage from "./header";
import FooterPage from "./footer";
// import HomePage from "./home";
import SidebarPage from "./sidebar";
import firebase from '../config';
import moment from 'moment';
import {CSVLink, CSVDownload} from "react-csv";
import SimpleReactValidator from "simple-react-validator";

import swal from 'sweetalert';
import $ from 'jquery';
import readXlsxFile from 'read-excel-file'
import {OutTable, ExcelRenderer} from 'react-excel-renderer';
$.DataTable = require('datatables.net');

class MerchantPaymentsList extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            orderList: [],
            loading: false,
            count: 1,
            activePage: "1",
            countPage: "",
            orderStatusList: [],
            validFrom: "",
            validTo: "",
            orderDateList: [],
            cols: '',
            rows: '',
            paymentList:[],
        };
        this.validator = new SimpleReactValidator({
            className: "text-danger",
            validators: {

                whitespace: {
                    message: "The :attribute not allowed first whitespace   characters.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /[^\s\\]/) && params.indexOf(val) === -1);
                    }
                },
                specialChar: {
                    message: "The :attribute not allowed special   characters.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /^[ A-Za-z0-9_@./#&+-]*$/i) && params.indexOf(val) === -1);
                    }
                },
                specialCharText: {
                    message: "The :attribute may only contain letters, dot and spaces.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /^[ A-Za-z_@./#&+-]*$/i) && params.indexOf(val) === -1);
                    }
                }
            }
        });

    }

    onChange = (event) => {
        readXlsxFile(event.type.value).then((rows) => {

            console.log(rows);
            // `rows` is an array of rows each row being an array of cells.
        })
    }
    fileHandler = (event) => {
        let fileObj = event.target.files[0];

        //just pass the fileObj as parameter
        ExcelRenderer(fileObj, (err, resp) => {
            console.log(resp);

            if (err) {
                console.log(err);
            } else {

                this.setState({cols: resp.cols, rows: resp.rows});

                const data = [];
                var ServiceData={};
                var array ;
                console.log(resp.rows);
                resp
                    .rows
                    .forEach((res) => {
                        console.log(res);
                       
                        
                        for (var i = 1; i < res.length; i++) {
                            array[i] = { name: res[i], key: i };
                        
                    }
                    const ServiceData = {
                        array
                    }
                    data.push(ServiceData);

                        
                    });
                console.log(data);

                this.setState({ paymentList:data,loading:false });
                 console.log(this.state.paymentList);
            }
        });

    }
    componentWillUnmount() {

        clearTimeout(this.interval);
    }

    dateChange = (event) => {

        this.setState({
            [event.target.name]: event.target.value
        });
    }

    handleSubmit = (event) => {
        event.preventDefault();
        if (this.validator.allValid()) {
            var validFrom = new Date(this.state.validFrom).toLocaleString();
            var validTo = new Date(this.state.validTo).toLocaleString();

            var ref = firebase
                .database()
                .ref("orders/")
                .orderByChild("created_on")
                .startAt(validFrom)
                .endAt(validTo);

            ref.once('value', snapshot => {
                const data = [];
                console.log(snapshot.val());
                var i = 1;
                snapshot.forEach(childSnapShot => {

                    const Merchants = {
                        Sno: i++,
                        orderId: childSnapShot
                            .val()
                            .orderId,
                        customerName: childSnapShot
                            .val()
                            .first_name + " " + childSnapShot
                            .val()
                            .last_name,
                        items: childSnapShot
                            .val()
                            .cartDataItems
                            .length,
                        totalAmount: childSnapShot
                            .val()
                            .totalAmount,
                        status: childSnapShot
                            .val()
                            .status,
                        order_on: childSnapShot
                            .val()
                            .created_on,
                        emailId: childSnapShot
                            .val()
                            .email_id
                    };

                    data.push(Merchants);
                });
                this.setState({orderDateList: data, loading: false});
                console.log(this.state.orderDateList);

                this
                    .csvLink
                    .link
                    .click();

            });

        } else {
            this
                .validator
                .showMessages();
            this.forceUpdate();
        }
    };
    render() {
        const pathName = this.props.location.pathname;

        const ordersPath = pathName.substr(8);

        return (
            <div>
                <SidebarPage/>

                <div className="all-content-wrapper">
                    <HeaderPage/>

                    <div className="breadcome-area">
                        <div className="container-fluid">
                            <div className="row">
                                <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                    <div className="breadcome-list">
                                        <div className="row">
                                            <div className="col-lg-6 col-md-6 col-sm-6 col-xs-6">
                                                <div className="breadcomb-wp">

                                                    <div className="breadcomb-ctn">
                                                        <h2>Orders</h2>
                                                    </div>
                                                </div>
                                            </div>

                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

                    <div className="product-status mg-b-30">
                        <div className="container-fluid">
                            <div className="row">
                                <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                    <div className="product-status-wrap">
                                        <h4>
                                            {ordersPath}
                                            Orders List</h4>
                                        <input
                                            type="file"
                                            onChange={this
                                            .fileHandler
                                            .bind(this)}
                                            style={{
                                            "padding": "10px"
                                        }}/>

                                        <div className="row">
                                            <div className='col-sm-6'>
                                                <div className="row">
                                                    <div className='col-sm-6'>
                                                        <div className="form-group">
                                                            <label>From</label>
                                                            <div className='input-group date' id='datetimepicker2'>
                                                                <input
                                                                    name="validFrom"
                                                                    id="validFrom"
                                                                    min={moment().format("2019-06-01")}
                                                                    max={moment(new Date()).format("YYYY-MM-DD")}
                                                                    type="date"
                                                                    onChange={this.dateChange}
                                                                    value={this.state.validFrom}
                                                                    className="form-control"/>

                                                                <span className="input-group-addon">
                                                                    <span className="glyphicon glyphicon-calendar"></span>
                                                                </span>

                                                            </div>
                                                            {this
                                                                .validator
                                                                .message("Valid From", this.state.validFrom, "required")}
                                                        </div>
                                                    </div>

                                                    <div className='col-sm-6'>
                                                        <div className="form-group">
                                                            <label>To</label>
                                                            <div className='input-group date' id='datetimepicker2'>
                                                                <input
                                                                    name="validTo"
                                                                    id="validTo"
                                                                    className="form-control"
                                                                    max={moment(new Date()).format("YYYY-MM-DD")}
                                                                    type="date"
                                                                    min={moment(this.state.validFrom).format("YYYY-MM-DD")}
                                                                    onChange={this.dateChange}
                                                                    value={this.state.validTo}/>
                                                                <span className="input-group-addon">
                                                                    <span className="glyphicon glyphicon-calendar"></span>
                                                                </span>

                                                            </div>
                                                            {this
                                                                .validator
                                                                .message("Valid To", this.state.validTo, "required")}
                                                        </div>
                                                    </div>
                                                </div>
                                            </div>
                                            <div className='col-sm-6 pull-right'>
                                                <button
                                                    type="button"
                                                    onClick={this.handleSubmit}
                                                    title="download"
                                                    className="pd-setting-ed mrg_top"
                                                    style={{
                                                    marginTop: "28px"
                                                }}>
                                                    Export to excel
                                                </button>
                                            </div>
                                        </div>

                                        <CSVLink data={this.state.orderDateList} ref={(r) => this.csvLink = r}></CSVLink>

                                        <table className="table table-bordered paginationTable">
                                            <thead>
                                                <tr>
                                                    <th>S.no</th>
                                                    <th>CustomerName</th>
                                                    <th>Order ID</th>
                                                    <th>Items(Count)</th>
                                                    <th className="li-product-price">Grand Total
                                                    </th>
                                                    <th>Status</th>
                                                    <th>Order Date</th>
                                                    <th>Action</th>
                                                </tr>
                                            </thead>
                                            <tbody>
                                                {this.state.orderList && this
                                                    .state
                                                    .orderList
                                                    .map((data, index) => {

                                                        return (

                                                            <tr key={index}>
                                                                <td>{index + 1}</td>
                                                                {/* <td>{data.customerName ===NaN ?"":data.customerName}</td> */}
                                                                <td>{data.customerName && data.customerName}</td>
                                                                <td>{data.orderId}</td>
                                                                <td>{data.items}</td>
                                                                <td>{data.totalcart}</td>
                                                                <td>
                                                                    <select
                                                                        name="select"
                                                                        className="form-control pro-edt-select form-control-primary"
                                                                        name="orderStatus"
                                                                        value={data.orderStatus}
                                                                        id={data.fireorderId}
                                                                        data-rid={data.orderId}
                                                                        data-email={data.emailId && data.emailId}
                                                                        data-name={data.customerName && data.customerName}
                                                                        onChange={this.onChange}>
                                                                        <option value={data.status}>{data.status}</option>
                                                                        {this
                                                                            .state
                                                                            .orderStatusList
                                                                            .map((data, index) => {

                                                                                return (
                                                                                    <option value={data.orderStatus} key={index}>{data.orderStatus}</option>
                                                                                )

                                                                            })}

                                                                    </select>

                                                                </td>

                                                                <td>{data.created_on}</td>

                                                                <td>
                                                                    {/* <button data-toggle="tooltip" title="View" className="pd-setting-ed"><i className="fa fa-eye" aria-hidden="true"></i></button> */}

                                                                    <Link to={`/ViewInvoice/${data.fireorderId}`}>
                                                                        <button data-toggle="tooltip" title="Edit" className="pd-setting-ed">
                                                                            <i className="fa fa-eye" aria-hidden="true"></i>
                                                                        </button>
                                                                    </Link>

                                                                </td>
                                                            </tr>
                                                        )
                                                    })}

                                            </tbody>

                                        </table>
                                        {this.state.rows
                                            ? <OutTable
                                                    data={this.state.rows}
                                                    columns={this.state.cols}
                                                    tableClassName="ExcelTable2007"
                                                    tableHeaderRowClass="heading"/>
                                            : ''}

                                    </div>

                                </div>
                            </div>
                        </div>
                    </div>
                </div>
                <FooterPage/>

            </div>
        );
    }
}

export default MerchantPaymentsList;
