import React from "react";
import {Link} from "react-router-dom";
import {Form} from 'reactstrap';
import HeaderPage from "./header";
import FooterPage from "./footer";
import SidebarPage from "./sidebar";
import firebase from '../config';
import SimpleReactValidator from "simple-react-validator";
import FileUploader from "react-firebase-file-uploader";

class EditLocation extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            location: '',
            error_message: '',
            date: new Date().toLocaleString(),
            validError: false,
            uploadImage: '',
            avatar: "",
            isUploading: false,
            progress: 0,
            avatarURL: ""
        }

        this.validator = new SimpleReactValidator({
            className: "text-danger",
            validators: {

                whitespace: {
                    message: "The :attribute not allowed first whitespace   characters.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /[^\s\\]/) && params.indexOf(val) === -1);
                    }
                },
                specialChar: {
                    message: "The :attribute not allowed special   characters.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /^[ A-Za-z0-9_@./#&+-]*$/i) && params.indexOf(val) === -1);
                    }
                },
                specialCharText: {
                    message: "The :attribute may only contain letters, dot and spaces.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /^[ A-Za-z_@./#&+-]*$/i) && params.indexOf(val) === -1);
                    }
                }
            }
        });

    }
    componentDidMount() {
        const {locationId} = this.props.match.params;
        console.log(locationId);
        var sessionId = sessionStorage.getItem("RoleId"); 
        var ref = firebase
            .database()
            .ref(`Locations/${locationId}`);

        ref.on('value', snapshot => {
            var categories = snapshot.val();

            // console.log(categories)

            this.setState({
                location_Name: categories.location_Name,
                addressl: categories.addressl,
                address2: categories.address2,
                land_Mark: categories.land_Mark,
                city: categories.city,
                pincode: categories.pincode,
                contact_Person_Name: categories.contact_Person_Name,
                contact_Number: categories.contact_Number,
                email: categories.email,
                rental_Deposit_Amount: categories.rental_Deposit_Amount,
                rent_Per_Month: categories.rent_Per_Month,
                electricity_Per_Month: categories.electricity_Per_Month,
                wifi_Per_Month: categories.wifi_Per_Month,
                additional_Expenses: categories.additional_Expenses,
                notes: categories.notes,

                avatarURL: categories.avatarURL,
                created_on: categories.created_on, 
                sessionId: sessionId,


               
                 loading: false
                });
            console.log(this.state.location);

            this.employeeSListView();
        });

    }

    employeeSListView() {


        var ref = firebase
            .database()
            .ref('Employees/');
        ref.once("value", snapshot => {
            const data = [];
            console.log(snapshot.val());
            snapshot.forEach(element => {
                const usersData = {

                    value: element
                        .val()
                        .email,
                    label: element
                        .val()
                        .first_name + " " + element
                        .val()
                        .last_name + "(" + element
                        .val()
                        .email + ")"
                }
                data.unshift(usersData);
            });

            this.setState({
                EmployeesList: data
            }, () => {
                console.log(this.state.EmployeesList, 'Employees');

            });

        })
    }
    handleChange = (event) => {
        console.log(this.state.validError);
        this.setState({
            [event.target.name]: event.target.value
        });

       

    };

    handleUploadStart = () => {

        this.setState({isUploading: true, uploadProgress: 0});

    }
    handleFrontImageUploadStart = () => this.setState({isUploading: true, uploadProgress: 0, avatarURL: ''});
    handleProgress = progress => this.setState({uploadProgress: progress});

    handleUploadError = error => {
        this.setState({
            isUploading: false
            // Todo: handle error
        });
        console.error(error);
    };

    handleUploadSuccess = async filename => {
        const downloadURL = await firebase
            .storage()
            .ref("images")
            .child(filename)
            .getDownloadURL();

        this.setState(oldState => ({
            filenames: [
                ...oldState.filenames,
                filename
            ],
            downloadURLs: [
                ...oldState.downloadURLs,
                downloadURL
            ],
            uploadProgress: 100,
            isUploading: false
        }));
	};

  handleSelect = selectedOption => {
    this.setState({selectedOption});
    console.log(`Option selected:`, selectedOption);
};
    handleSubmit = (event) => {
        event.preventDefault();
        if (this.validator.allValid() && this.state.validError == true) {
            const {locationId} = this.props.match.params;
            var sessionId = sessionStorage.getItem("RoleId"); 
            let dbCon = firebase
                .database()
                .ref(`/Locations/${locationId}`);
            console.log(dbCon);
            dbCon.update({
                location_Name:this.state.location_Name,
                addressl:this.state.addressl,
                address2:this.state.address2,
                land_Mark:this.state.land_Mark,
                city: this.state.city,
                pincode: this.state.pincode,
                contact_Person_Name:this.state.contact_Person_Name,
                contact_Number: this.state.contact_Number,
                email:this.state.email,
                rental_Deposit_Amount:this.state.rental_Deposit_Amount,
                rent_Per_Month:this.state.rent_Per_Month,
                electricity_Per_Month:this.state.electricity_Per_Month,
                wifi_Per_Month:this.state.wifi_Per_Month,
                additional_Expenses:this.state.additional_Expenses,
                notes:this.state.notes,
                photo: this.state.avatarURL,
                created_on: this.state.created_on, 
                sessionId: sessionId
            });

            this
                .props
                .history
                .push("/Locations/Updated");

        } else {
            this
                .validator
                .showMessages();
            this.forceUpdate();
        }
    };

    render() {

        return (
            <div>
                <SidebarPage/>

                <div className="all-content-wrapper">
                    <HeaderPage/>

                    <div className="breadcome-area">
                        <div className="container-fluid">
                            <div className="row">
                                <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                    <div className="breadcome-list">
                                        <div className="row">
                                            <div className="col-lg-6 col-md-6 col-sm-6 col-xs-6">
                                                <div className="breadcomb-wp">

                                                    <div className="breadcomb-ctn">
                                                        <h2>Locations</h2>
                                                    </div>
                                                </div>
                                            </div>

                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

                    <div className="single-product-tab-area mg-b-30">

                        <div className="single-pro-review-area">
                            <div className="container-fluid">
                                <div className="row">
                                    <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                        <div className="review-tab-pro-inner">

                                            <h4>Edit Location</h4>

                                            <div className="product-tab-list tab-pane fade active in">
                                            <Form onSubmit={this.handleSubmit} id="Add-products">

<div className="row m-b-20">
    <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12 space">
        <label>Location Name<span style={{color: 'red'}}>*</span>
        </label>
        <input
            type="text"
            className="form-control"
            placeholder=""
            name="location_Name"
            onChange={this.handleChange}
            value={this.state.location_Name}/> {this
            .validator
            .message("Location Name", this.state.location_Name, "required|specialCharText|whitespace|min:2|max:70")}
        <div className="text-danger">
            {" "}
            {this.state.error_message}
        </div>
    </div>
    <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
        <label>City<span style={{color: 'red'}}>*</span>
        </label>
        <input
            type="text"
            className="form-control"
            placeholder=""
            name="city"
            onChange={this.handleChange}
            value={this.state.city}/> {this
                .validator
                .message("City ", this.state.city, "required|min:2|max:70")}
            <div className="text-danger">
                {" "}
                {this.state.error_message}
        </div>
    </div>
   
   
    <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
        <label>Address 1<span style={{color: 'red'}}>*</span>
        </label>
        <textarea
            type="text"
            className="form-control"
            placeholder=""
            name="addressl"
            onChange={this.handleChange}
           
            value={this.state.addressl}/> {this
            .validator
            .message("Address 1", this.state.addressl, "required|min:5|max:150")}

    </div>
    <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
        <label>Address 2<span style={{color: 'red'}}>*</span>
        </label>
        <textarea
            type="text"
            className="form-control"
            placeholder=""
            name="address2"
            onChange={this.handleChange}
           
            value={this.state.address2}/> {this
            .validator
            .message("Address 2", this.state.address2, "required|min:5|max:150")}

    </div>
    <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
        <label>Land Mark<span style={{color: 'red'}}>*</span>
        </label>
        <input
            type="text"
            className="form-control"
            placeholder=""
            name="land_Mark"
            onChange={this.handleChange}
            value={this.state.land_Mark}/> {this
                .validator
                .message("Land Mark", this.state.land_Mark, "required|min:2|max:70")}
            <div className="text-danger">
                {" "}
                {this.state.error_message}
        </div>
    </div>
  
    <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
        <label>Pincode<span style={{color: 'red'}}>*</span>
        </label>
        <input
            type="text"
            className="form-control"
            placeholder=""
            name="pincode"
            onChange={this.handleChange}
            value={this.state.pincode}/> {this
                .validator
                .message("Pincode ", this.state.pincode, "required||numeric|max:10")}
            <div className="text-danger">
                {" "}
                {this.state.error_message}
        </div>
    </div>
    <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
        <label>Contact Person Name<span style={{color: 'red'}}>*</span>
        </label>
        <input
            type="text"
            className="form-control"
            placeholder=""
            name="contact_Person_Name"
            onChange={this.handleChange}
            value={this.state.contact_Person_Name}/> {this
            .validator
            .message("Contact Person Name", this.state.contact_Person_Name, "required|specialCharText|whitespace|min:2|max:70")}
        <div className="text-danger">
            {" "}
            {this.state.error_message}
        </div>
    </div>
    <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
        <label>Contact Number<span style={{color: 'red'}}>*</span>
        </label>
        <input
            type="text"
            className="form-control"
            placeholder=""
            name="contact_Number"
            onChange={this.handleChange}
            value={this.state.contact_Number}/> {this
                .validator
                .message(" Contact Number", this.state.contact_Number, "required||numeric|max:10")}  <div className="text-danger">
            {" "}
            {this.state.error_message}
        </div>
    </div>
    <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
        <label>E-mail Address<span style={{color: 'red'}}>*</span>
        </label>
        <input
            type="text"
            className="form-control"
            placeholder=""
            name="email"
            id="price_cost"
            onChange={this.handleChange}
            value={this.state.email}/> {this
            .validator
            .message("E-mail Address", this.state.email, "required|email")}

    </div>
   
    <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
        <label>Rental Deposit Amount<span style={{color: 'red'}}>*</span>
        </label>
        <input
            type="text"
            className="form-control"
            placeholder=""
            name="rental_Deposit_Amount"
            id="price_cost"
            onChange={this.handleChange}
            value={this.state.rental_Deposit_Amount}/> {this
                .validator
                .message("Rental Deposit Amount", this.state.rental_Deposit_Amount, "required|numeric")}

    </div>

    <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
        <label>Rent Per Month<span style={{color: 'red'}}>*</span>
        </label>
        <input
            type="text"
            className="form-control"
            placeholder=""
            name="rent_Per_Month"
            id="price_cost"
            onChange={this.handleChange}
            value={this.state.rent_Per_Month}/> {this
                .validator
                .message("Rent Per Month", this.state.rent_Per_Month, "required|numeric")}

    </div>

    <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
        <label>Electricity Per Month<span style={{color: 'red'}}>*</span>
        </label>
        <input
            type="text"
            className="form-control"
            placeholder=""
            name="electricity_Per_Month"
            id="price_cost"
            onChange={this.handleChange}
            value={this.state.electricity_Per_Month}/> {this
                .validator
                .message("Electricity Per Month", this.state.electricity_Per_Month, "required|numeric")}

    </div>
    <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
        <label>Wifi Per Month<span style={{color: 'red'}}>*</span>
        </label>
        <input
            type="text"
            className="form-control"
            placeholder=""
            name="wifi_Per_Month"
            id="price_cost"
            onChange={this.handleChange}
            value={this.state.wifi_Per_Month}/> {this
                .validator
                .message("Wifi Per Month", this.state.wifi_Per_Month, "required|numeric")}

    </div>

    <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
        <label>Any Additional Expenses<span style={{color: 'red'}}>*</span>
        </label>
        <input
            type="text"
            className="form-control"
            placeholder=""
            name="additional_Expenses"
            id="price_cost"
            onChange={this.handleChange}
