import React from "react";
import {Link} from "react-router-dom";
import FadeIn from "react-fade-in";
import axios from "axios";
import HeaderPage from "./header";
import FooterPage from "./footer";
// import HomePage from "./home";
import SidebarPage from "./sidebar";
import firebase from '../config';
import moment from 'moment';
import {CSVLink, CSVDownload} from "react-csv";
import SimpleReactValidator from "simple-react-validator";

import swal from 'sweetalert';
import $ from 'jquery';

$.DataTable = require('datatables.net');
 const webUrl = window.location.origin;
class OrderListPage extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            orderList: [],
            loading: false,
            count: 1,
            activePage: "1",
            countPage: "",
            orderStatusList: [],
            validFrom: "",
            validTo: "",
            orderDateList: [],
            deliveryList:[],
            employer_sevice_message:''
        };
        this.deleteItem = this
            .deleteItem
            .bind(this);
        this.validator = new SimpleReactValidator({
            className: "text-danger",
            validators: {

                whitespace: {
                    message: "The :attribute not allowed first whitespace   characters.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /[^\s\\]/) && params.indexOf(val) === -1);
                    }
                },
                specialChar: {
                    message: "The :attribute not allowed special   characters.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /^[ A-Za-z0-9_@./#&+-]*$/i) && params.indexOf(val) === -1);
                    }
                },
                specialCharText: {
                    message: "The :attribute may only contain letters, dot and spaces.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /^[ A-Za-z_@./#&+-]*$/i) && params.indexOf(val) === -1);
                    }
                }
            }
        });

    }

    componentDidMount() {
        var pathname = this.props.location.pathname;

        var pageName = pathname.substr(8);

        this.setState({loading: true});
        var sessionId = sessionStorage.getItem("RoleId");
        var userRole = sessionStorage.getItem("userRole");
        if (userRole == "Merchant") {
            var ref = firebase
                .database()
                .ref("orders/")
                .orderByChild("merchantId")
                .equalTo(sessionId);
        } else if(userRole == "Delivery Boy") {
            var ref = firebase
                .database()
                .ref("orders/").orderByChild("assignTo")
                .equalTo(sessionId);

        }else{
            var ref = firebase
                .database()
                .ref("orders/");

        }

        ref.once('value', snapshot => {
            const data = [];

            snapshot.forEach(childSnapShot => {

                const Merchants = {
                    fireorderId: childSnapShot
                        .key
                        .toString(),
                    orderId: childSnapShot
                        .val()
                        .orderId,
                    customerName: childSnapShot
                        .val()
                        .first_name + "" + childSnapShot
                        .val()
                        .last_name,
                    items: childSnapShot
                        .val()
                        .cartDataItems
                        .length,
                    totalcart: childSnapShot
                        .val()
                        .totalAmount,
                    status: childSnapShot
                        .val()
                        .status,
                        assignTo:childSnapShot
                        .val()
                        .assignTo,
                    created_on: childSnapShot
                        .val()
                        .created_on,
                    emailId: childSnapShot
                        .val()
                        .email_id
                };

                data.unshift(Merchants);
            });
            if (pageName != "All") {
                var sortedKeys = data.filter(res => {

                    return res.status == pageName;
                });
            } else {
                var sortedKeys = data;
            }

            this.setState({orderList: sortedKeys, countPage: data.length, loading: false});

            this.interval = setTimeout(() => $('.paginationTable').DataTable(), 1000);
        });

        var ref = firebase
            .database()
            .ref("orderStatus");

        ref.on('value', snapshot => {
            const data = [];

            snapshot.forEach(childSnapShot => {

                const orderStatus = {
                    categoryId: childSnapShot
                        .key
                        .toString(),
                    orderStatus: childSnapShot
                        .val()
                        .orderStatus
                };

                data.push(orderStatus);
            });

            this.setState({orderStatusList: data, loading: false});

        });

    }
    componentWillMount(){
        var ref = firebase
            .database()
            .ref("admin_users").orderByChild("Role").equalTo("Delivery Boy");

        ref.on('value', snapshot => {
            const data = [];

            snapshot.forEach(childSnapShot => {

                const orderStatus = {
                    userId: childSnapShot
                        .key
                        .toString(),
                        fullName: childSnapShot
                        .val()
                        .fullName,
                        email: childSnapShot
                        .val()
                        .email
                };

                data.push(orderStatus);
            });

            this.setState({deliveryList: data, loading: false});

        });
    }
    componentWillUnmount() {

        clearTimeout(this.interval);
    }
    deleteItem = id => {

        var playersRef = firebase
            .database()
            .ref(`/orders/${id}`);
        playersRef.remove();

    };
    dateChange = (event) => {

        this.setState({
            [event.target.name]: event.target.value
        });
    }
    onChange = (event) => {

        var orderId = event.target.id;
        var orderStatusValue = event.target.value;
        var orderRid = event
            .target
            .getAttribute('data-rid');
        var emailId = event
            .target
            .getAttribute('data-email');
        var name = event
            .target
            .getAttribute('data-name');
        var username = name
            ? name
            : emailId;

        swal({title: "Are you sure?", text: "Do your really want to  change the status?", icon: "warning", buttons: true, dangerMode: true}).then(willChange => {
            if (willChange) {
                let dbCon = firebase
                    .database()
                    .ref(`/orders/${orderId}`);
                let trackingNumber = '';
                let deliveredBy = '';
                var headers = {
                    'Content-Type': 'application/json'
                }

                if (orderStatusValue == 'Dispatched') {
                    trackingNumber = '6245454784';
                    deliveredBy = 'Fedex';
                }

                // let user = {
                //     "orderStatus": orderStatusValue,
                //     "username": username,
                //     "to": emailId,
                //     "orderId": orderRid,
                //     "trackingNumber": trackingNumber,
                //     "deliveredBy": deliveredBy
                // };
        // var message = "Dear "+ username+", Thank you for your business.Order Number : "+ orderRid+" Your Gootes Order is "+ orderStatusValue+" for shipping. CheersTeam Gootes"
        var message = '<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd"> <html xmlns="http://www.w3.org/1999/xhtml">'+
        '<head> <meta http-equiv="Content-Type" content="text/html; charset=utf-8" /> <title>Gootes</title> </head> '+
       '<body style="background:#ebe8dd; width: 600px; margin: 0px auto;"> '+
        '<table style="width:100%"> <tr> <td width="75%" style="color:#57706d; font-size:28px; font-family:arial; text-align:center; padding-bottom:30px; padding-top:20px; letter-spacing: 2px;"> '+
       ' Thanks for shopping <br> width gootes </td> <td width="25%" style="padding-bottom:40px;">&nbsp;</td> </tr> <tr> <td width="75%" style="    color: #ff914d;    font-size: 23px;    font-family: arial;    font-weight: 800;    text-align: center;    line-height: 1.5;    letter-spacing: 1.5px;    padding-bottom:30px;">'+
       'YOUR ORDER GETTING READY<br> TO  '+ orderStatusValue+' WE WILL KEEP<br> YOU UPDATED ABOUT YOUR<br>ORDER </td> <td width="25%" style="padding-bottom:30px;"><img src="'+webUrl+'/img/cart_icon.png" width="150px" style="position: relative;    top: -70px;"></td>'+
        '</tr> <tr> <td width="75%" style="    color: #57706d;    font-size: 18px;    font-family: arial;    font-weight: 600;    text-align: center;    line-height: 1.5;    letter-spacing: 2px; padding-bottom:30px;">'+
      'Your order number: '+ orderRid+'<br> For more informaion about your order<br> please do track from gootes app </td> <td width="25%" style="padding-bottom:30px; text-align:right;"><img src="'+webUrl+'/img/delivey_icon.png" style="position: relative;    top: -60px;"></td> </tr> </table> '+
      '<p style="padding-bottom:30px;"><img src="'+webUrl+'/img/logo.png"></p> <p style="color:#aba174; font-size:14px; font-family:arial; font-weight: 600; letter-spacing: 1.5px; text-align:right; padding-bottom:30px;">TERMS & CONDITIONS APPLY.</p> </body> </html>'

  let data = {

                    "subject": "Gootes  Order "+ orderRid+" in  "+ orderStatusValue,
                    "message":message,
                    "to": emailId,
                    "username": username,
                   
                };
                const url = 'https://www.gootes.com/api/services/sendEmail';
                axios(url, {
                    method: 'POST',
                    data: data,
                    headers: {
                        "Content-type": "application/json; charset=UTF-8"
                    }
                }).then((res) =>{
                    console.log(res);
                dbCon.update({status: orderStatusValue});
                })
            } else {} 
        });
    }
    deliverytAssignTo = (event) => {

        var orderId = event.target.id;
        var orderStatusValue = event.target.value;
        var orderRid = event
            .target
            .getAttribute('data-rid');
        var emailId = event
            .target
            .getAttribute('data-email');
        var name = event
            .target
            .getAttribute('data-name');
        var username = name
            ? name
            : emailId;

        swal({title: "Are you sure?", text: "Do your really want to  change the Assign To Delivery Boy?", icon: "warning", buttons: true, dangerMode: true}).then(willChange => {
            if (willChange) {
                let dbCon = firebase
                    .database()
                    .ref(`/orders/${orderId}`);
               
            

               
       // var message = "Dear "+ username+", Thank you for your business.Order Number : "+ orderRid+" Your Gootes Order is "+ orderStatusValue+" for shipping. CheersTeam Gootes"
        var message = '<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd"> <html xmlns="http://www.w3.org/1999/xhtml">'+
        '<head> <meta http-equiv="Content-Type" content="text/html; charset=utf-8" /> <title>Gootes</title> </head> '+
       '<body style="background:#ebe8dd; width: 600px; margin: 0px auto;"> '+
        '<table style="width:100%"> <tr> <td width="75%" style="color:#57706d; font-size:28px; font-family:arial; text-align:center; padding-bottom:30px; padding-top:20px; letter-spacing: 2px;"> '+
       ' Thanks for shopping <br> width gootes </td> <td width="25%" style="padding-bottom:40px;">&nbsp;</td> </tr> <tr> <td width="75%" style="    color: #ff914d;    font-size: 23px;    font-family: arial;    font-weight: 800;    text-align: center;    line-height: 1.5;    letter-spacing: 1.5px;    padding-bottom:30px;">'+
       'YOUR ORDER GETTING READY<br> TO  '+ orderStatusValue+' WE WILL KEEP<br> YOU UPDATED ABOUT YOUR<br>ORDER </td> <td width="25%" style="padding-bottom:30px;"><img src="'+webUrl+'/img/cart_icon.png" width="150px" style="position: relative;    top: -70px;"></td>'+
        '</tr> <tr> <td width="75%" style="    color: #57706d;    font-size: 18px;    font-family: arial;    font-weight: 600;    text-align: center;    line-height: 1.5;    letter-spacing: 2px; padding-bottom:30px;">'+
      'Your order number: '+ orderRid+'<br> For more informaion about your order<br> please do track from gootes app </td> <td width="25%" style="padding-bottom:30px; text-align:right;"><img src="'+webUrl+'/img/delivey_icon.png" style="position: relative;    top: -60px;"></td> </tr> </table> '+
      '<p style="padding-bottom:30px;"><img src="'+webUrl+'/img/logo.png"></p> <p style="color:#aba174; font-size:14px; font-family:arial; font-weight: 600; letter-spacing: 1.5px; text-align:right; padding-bottom:30px;">TERMs & CONDITIONS APPLY.</p> </body> </html>'


        
        let data = {

                    "subject": "Gootes  Order "+ orderRid+" in  "+ orderStatusValue,
                    "message":message,
                    "to": emailId,
                    "username": username,
                   
                };
                const url = 'https://www.gootes.com/api/services/sendEmail';
                axios(url, {
                    method: 'POST',
                    data: data,
                    headers: {
                        "Content-type": "application/json; charset=UTF-8"
                    }
                }).then((res) =>{
                    console.log(res);
                dbCon.update({assignTo: orderStatusValue});
                })
            } else {} 
        });
    }
    handleSubmit = (event) => {
        event.preventDefault();
        if (this.validator.allValid()) {
            var validFrom = new Date(this.state.validFrom).toLocaleString();
            var validTo = new Date(this.state.validTo).toLocaleString();

            var ref = firebase
                .database()
                .ref("orders/")
                .orderByChild("created_on")
                .startAt(validFrom)
                .endAt(validTo);

            ref.once('value', snapshot => {


                console.log(snapshot.val());

                if(snapshot.val()==null||snapshot.val()==''){

                    this.setState({employer_sevice_message: "No records are found"});

                }else{

                const data = [];

                var i = 1;
                snapshot.forEach(childSnapShot => {

                    const Merchants = {
                        Sno: i++,
                        orderId: childSnapShot
                            .val()
                            .orderId,
                        customerName: childSnapShot
                            .val()
                            .first_name + " " + childSnapShot
                            .val()
                            .last_name,
                        items: childSnapShot
                            .val()
                            .cartDataItems
                            .length,
                        totalAmount: childSnapShot
                            .val()
                            .totalAmount,
                        status: childSnapShot
                            .val()
                            .status,
                        order_on: childSnapShot
                            .val()
                            .created_on,
                        emailId: childSnapShot
                            .val()
                            .email_id
                    };

                    data.push(Merchants);
                });
                this.setState({orderDateList: data, loading: false,validFrom:'',validTo:''});
                this
                .csvLink
                .link
                .click();
            
            }
                

            });

        } else {
            this
                .validator
                .showMessages();
            this.forceUpdate();
        }
    };
    render() {
        const pathName = this.props.location.pathname;

        const ordersPath = pathName.substr(8);
// console.log(window.location.origin);
        return (
            <div>
                <SidebarPage/>

                <div className="all-content-wrapper">
                    <HeaderPage/>

                    <div className="breadcome-area">
                        <div className="container-fluid">
                            <div className="row">
                                <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                    <div className="breadcome-list">
                                        <div className="row">
                                            <div className="col-lg-6 col-md-6 col-sm-6 col-xs-6">
                                                <div className="breadcomb-wp">

                                                    <div className="breadcomb-ctn">
                                                        <h2>Orders</h2>
                                                    </div>
                                                </div>
                                            </div>

                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

                    <div className="product-status mg-b-30">
                        <div className="container-fluid">
                            <div className="row">
                                <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                    <div className="product-status-wrap">
                                        <h4>
                                            {ordersPath}
                                            Orders List</h4>

                                        <div className="row">
                                            <div className='col-sm-6'>
                                                <div className="row">
                                                    <div className='col-sm-6'>
                                                        <div className="form-group">
                                                            <label>From</label>
                                                            <div className='input-group date' id='datetimepicker2'>
                                                                <input
                                                                    name="validFrom"
                                                                    id="validFrom"
                                                                    min={moment().format("2019-06-01")}
                                                                    max={moment(new Date()).format("YYYY-MM-DD")}
                                                                    type="date"
                                                                    onChange={this.dateChange}
                                                                    value={this.state.validFrom}
                                                                    className="form-control"/>

                                                                <span className="input-group-addon">
                                                                    <span className="glyphicon glyphicon-calendar"></span>
                                                                </span>

                                                            </div>
                                                            {this
                                                                .validator
                                                                .message("Valid From", this.state.validFrom, "required")}
                                                        </div>
                                                    </div>

                                                    <div className='col-sm-6'>
                                                        <div className="form-group">
                                                            <label>To</label>
                                                            <div className='input-group date' id='datetimepicker2'>
                                                                <input
                                                                    name="validTo"
                                                                    id="validTo"
                                                                    className="form-control"
                                                                    max={moment(new Date()).format("YYYY-MM-DD")}
                                                                    type="date"
                                                                    min={moment(this.state.validFrom).format("YYYY-MM-DD")}
                                                                    onChange={this.dateChange}
                                                                    value={this.state.validTo}/>
                                                                <span className="input-group-addon">
                                                                    <span className="glyphicon glyphicon-calendar"></span>
                                                                </span>

                                                            </div>
                                                            {this
                                                                .validator
                                                                .message("Valid To", this.state.validTo, "required")}
                                                        </div>
                                                    </div>
                                                    
                                                </div>
                                                {this.state.employer_sevice_message
                                            ? (
                                                <div className="alert alert-warning" role="alert">
                                                    {this.state.employer_sevice_message}
                                                </div>
                                            )
                                            : ("")}{" "}
                                            </div>
                                            <div className='col-sm-6 pull-right'>
                                                <button
                                                    type="button"
                                                    onClick={this.handleSubmit}
                                                    title="download"
                                                    className="pd-setting-ed mrg_top"
                                                    style={{
                                                    marginTop: "28px"
                                                }}>
                                                    Export to excel
                                                </button>
                                            </div>
                                        </div>

                                        <CSVLink data={this.state.orderDateList} ref={(r) => this.csvLink = r}></CSVLink>

                                        <table className="table table-bordered paginationTable">
                                            <thead>
                                                <tr>
                                                    <th>S.no</th>
                                                    <th>Customer Name</th>
                                                    <th>Order ID</th>
                                                    <th>Items</th>
                                                    <th >Grand Total
                                                    </th>
                                                    <th>Status</th>
                                                    {sessionStorage.getItem("userRole") == "Admin"||sessionStorage.getItem("userRole") == "Super Admin"
                                                                    ? <th>Assign to</th>:''}
                                                    <th>Order Date</th>
                                                    {sessionStorage.getItem("userRole") == "Admin"||sessionStorage.getItem("userRole") == "Super Admin"
                                                                    ? <th>Action</th>:''}
                                                </tr>
                                            </thead>
                                            <tbody>
                                                {this.state.orderList && this
                                                    .state
                                                    .orderList
                                                    .map((data, index) => {

                                                        return (

                                                            <tr key={index}>
                                                                <td>{index + 1}</td>
                                                                {/* <td>{data.customerName ===NaN ?"":data.customerName}</td> */}
                                                                <td>{data.customerName && data.customerName}</td>
                                                                <td>{data.orderId}</td>
                                                                <td>{data.items}</td>
                                                                <td>{data.totalcart}</td>
                                                                {sessionStorage.getItem("userRole") == "Admin"||sessionStorage.getItem("userRole") == "Super Admin"||sessionStorage.getItem("userRole") == "Delivery Boy"
                                                                    ?   <td>
                                                                    <select
                                                                        name="select"
                                                                        className="form-control pro-edt-select form-control-primary"
                                                                        name="orderStatus"
                                                                        defaultValue={data.status}
                                                                        id={data.fireorderId}
                                                                        data-rid={data.orderId}
                                                                        data-email={data.emailId && data.emailId}
                                                                        data-name={data.customerName && data.customerName}
                                                                        onChange={this.onChange}>
                                                                             <option >selected</option> 
                                                                        {sessionStorage.getItem("userRole") == "Delivery Boy"
                                                                                ?
                                                                                <option value="Delivered" selected={data.status == "Delivered"}>Delivered</option>
                                                                              
                                                                            : this
                                                                            .state
                                                                            .orderStatusList
                                                                            .map((data1, index) => {
                                                                               
                                                                                return (
                                                                                    <option value={data1.orderStatus} key={index} selected={data.status == data1.orderStatus}>{data1.orderStatus}</option>
                                                                                )

                                                                            })}

                                                                    </select>

                                                                </td>:<td>{data.status}</td>}
                                                                {sessionStorage.getItem("userRole") == "Admin"||sessionStorage.getItem("userRole") == "Super Admin"
                                                                    ?   <td>
                                                                    <select
                                                                        className="form-control pro-edt-select form-control-primary"
                                                                        name="assignTo"
                                                                        value={data.assignTo}
                                                                        id={data.fireorderId}
                                                                        data-rid={data.orderId}
                                                                        data-email={data.emailId && data.emailId}
                                                                        data-name={data.customerName && data.customerName}
                                                                        onChange={this.deliverytAssignTo}>
                                                                              <option >selected</option> 
                                                                        
                                                                        {this
                                                                            .state
                                                                            .deliveryList
                                                                            .map((data1, index) => {

                                                                                return (
                                                                                    <option value={data1.userId} key={index} selected={data.assignTo == data1.userId}>{data1.fullName}</option>
                                                                                )

                                                                            })}

                                                                    </select>

                                                                </td>:''}

                                                                <td>{data.created_on}</td>

                                                                {sessionStorage.getItem("userRole") == "Admin"||sessionStorage.getItem("userRole") == "Super Admin"
                                                                    ?<td>
                                                                    

                                                                    <Link to={`/ViewInvoice/${data.fireorderId}`}>
                                                                        <button data-toggle="tooltip" title="Edit" className="pd-setting-ed">
                                                                            <i className="fa fa-eye" aria-hidden="true"></i>
                                                                        </button>
                                                                    </Link>

                                                                </td>:''}
                                                            </tr>
                                                        )
                                                    })}

                                            </tbody>

                                        </table>

                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
                <FooterPage/>

            </div>
        );
    }
}

export default OrderListPage;
