import React from "react";
import {Link} from "react-router-dom";
import HeaderPage from "./header";
import FooterPage from "./footer";
import SidebarPage from "./sidebar";
import firebase from '../config';
import swal from 'sweetalert';
import $ from 'jquery';
$.DataTable = require('datatables.net');

class CategoryListPage extends React.Component {
    constructor(props) {
        super(props);
        this.datatable = null;
        this.state = {
            categoryList: [],
            loading: false,
            count: 1,
            activePage: "1",
            countPage: "",
            usePermList: ""
        };

    }
    componentWillMount() {
        var sessionId = sessionStorage.getItem("userRole");
        this.setState({loading: true});
        var ref = firebase
            .database()
            .ref("Role")
            .orderByChild("Role")
            .equalTo(sessionId);

        ref.on('value', snapshot => {
            const data = [];

            snapshot.forEach(childSnapShot => {

                const orderStatus = {
                    RoleId: childSnapShot
                        .key
                        .toString(),

                    category: childSnapShot
                        .val()
                        .category
                };

                data.push(orderStatus);
            });

            this.setState({usePermList: data[0].category, loading: false});
            console.log(this.state.usePermList);

        });
    }
    componentDidMount() {
        this.setState({loading: true});
        var ref = firebase
            .database()
            .ref("category/");

        ref.on('value', snapshot => {
            const data = [];
            // console.log(snapshot); console.log(snapshot.val);

            snapshot.forEach(childSnapShot => {

                const Categories = {
                    categoryId: childSnapShot
                        .key
                        .toString(),
                    userName: childSnapShot
                        .val()
                        .userName,
                    category: childSnapShot
                        .val()
                        .category,
                    created_on: childSnapShot
                        .val()
                        .created_on
                };

                data.push(Categories);
            });

            this.setState({categoryList: data, countPage: data.length, loading: false});
            console.log(this.state.categoryList);
            // this.$el = $(this.el);

            this.interval = setTimeout(() => $('.paginationTable').DataTable(), 1000);
        });

    }
    componentWillUnmount() {
        //this.dataTable.destroy(true);
        clearTimeout(this.interval);
    }
    deleteItem = id => {

        swal({title: "Are you sure?", text: "Do your really want to remove?", icon: "warning", buttons: true, dangerMode: true}).then(willDelete => {
            if (willDelete) {
                console.log(id);
                var playersRef = firebase
                    .database()
                    .ref(`/category/${id}`);
                playersRef.remove();

                this
                    .props
                    .history
                    .push("/CategoryListPage/deleted");
            } else {}
            // window.location.reload("/Home");

        });

    };

    render() {

        var pathname = this
            .props
            .location
            .pathname
            .split("/");
        var alertName = pathname[2];
        console.log(alertName);
        return (
            <div>
                <SidebarPage/>

                <div className="all-content-wrapper">
                    <HeaderPage/>

                    <div className="breadcome-area">
                        <div className="container-fluid">
                            <div className="row">
                                <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                    <div className="breadcome-list">
                                        <div className="row">
                                            <div className="col-lg-6 col-md-6 col-sm-6 col-xs-6">
                                                <div className="breadcomb-wp">

                                                    <div className="breadcomb-ctn">
                                                        <h2>Categories</h2>

                                                    </div>

                                                </div>
                                            </div>

                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

                    <div className="product-status mg-b-30">
                        <div className="container-fluid">
                            <div className="row">
                                <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                    <div className="product-status-wrap">
                                        <h4>Categories List</h4>

                                        {this.state.usePermList == "read&write"
                                            ? <div className="add-product">
                                                    <Link to='/AddCategoryPage'>Add Category</Link>
                                                </div>
                                            : ""}
                                        {alertName
                                            ? <div
                                                    className="alert alert-success display-none"
                                                    style={{
                                                    display: "block"
                                                }}>
                                                    <a data-dismiss="alert" href="#" aria-hidden="true" className="close">×</a>
                                                    Categories {alertName}
                                                    Successfully
                                                </div>
                                            : ''}
                                        <table className="table table-bordered paginationTable">
                                            <thead>
                                                <tr>
                                                    <th>S.No</th>
                                                    <th>Category Name</th>
                                                    <th>Created By</th>
                                                    <th>Created On</th>
                                                    {this.state.usePermList == "read&write"
                                                        ? <th>Action</th>
                                                        : ""}

                                                </tr>
                                            </thead>
                                            <tbody>

                                                {this
                                                    .state
                                                    .categoryList
                                                    .map((data, index) => {
                                                        console.log(data);

                                                        return (
                                                            <tr key={index}>
                                                                <td>{index + 1}</td>
                                                                <td>{data.category}</td>
                                                                <td>{data.userName}
                                                                </td>
                                                                <td>{data.created_on}
                                                                </td>
                                                                {this.state.usePermList == "read&write"
                                                                    ? <td>

                                                                            <Link to={`/EditCategoryPage/${data.categoryId}`}>
                                                                                <button data-toggle="tooltip" title="Edit" className="pd-setting-ed">
                                                                                    <i className="fa fa-pencil-square-o" aria-hidden="true"></i>
                                                                                </button>
                                                                            </Link>

                                                                            <button
                                                                                data-toggle="tooltip"
                                                                                title="Trash"
                                                                                className="pd-setting-ed"
                                                                                data-id={data.categoryId}
                                                                                onClick={this
                                                                                .deleteItem
                                                                                .bind(this, data.categoryId)}>
                                                                                <i className="fa fa-trash-o" aria-hidden="true"></i>
                                                                            </button>
                                                                        </td>
                                                                    : ""}

                                                            </tr>
                                                        )

                                                    })}

                                            </tbody>
                                        </table>

                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

                </div>

                <FooterPage/>

            </div>
        );
    }
}

export default CategoryListPage;
