import React from "react";
import {Link} from "react-router-dom";
import HeaderPage from "./header";
import FooterPage from "./footer";
import SidebarPage from "./sidebar";
import firebase from '../config';
import swal from 'sweetalert';
import $ from 'jquery';
$.DataTable = require('datatables.net');

class Files extends React.Component {
    constructor(props) {
        super(props);
        this.datatable = null;
        this.state = {
            file: '',
            name: '',
            description: '',
            permission: '',
            specifEmpName: '',
            FileList: [],
            isUploading: false,
            error_message: '',
            created_on: new Date().toLocaleString(),
            validError: true,
            loading: false,
            count: 1,
            activePage: "1",
            countPage: "",
            usePermList: ""
        };

    }

    componentDidMount() {
        var ref = firebase
            .database()
            .ref('Files/');
        ref.once("value", snapshot => {
            const data = [];
            console.log(snapshot.val());
            snapshot.forEach(element => {
                const usersData = {
                    fileID: element
                        .key
                        .toString(),

                    file: element
                        .val()
                        .file,
                    name: element
                        .val()
                        .name,

                    created_on: element
                        .val()
                        .created_on
                }
                data.unshift(usersData);
            });

            this.setState({
                Files: data
            }, () => {
                console.log(this.state.Files, 'Files');
            });
        })
    }
    componentWillUnmount() {

        clearTimeout(this.interval);
    }
    deleteItem = id => {

        swal({title: "Are you sure?", text: "Do your really want to remove?", icon: "warning", buttons: true, dangerMode: true}).then(willDelete => {
            if (willDelete) {
                console.log(id);
                var playersRef = firebase
                    .database()
                    .ref(`/Files/${id}`);
                playersRef.remove();

                this
                    .props
                    .history
                    .push("/Files/Deleted");
            } else {}

        });

    };

    render() {

        // var pathname = this     .props     .location     .pathname     .split("/");
        // var alertName = pathname[2];
        return (
            <div>
                <SidebarPage/>

                <div className="all-content-wrapper">
                    <HeaderPage/>

                    <div className="breadcome-area">
                        <div className="container-fluid">
                            <div className="row">
                                <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                    <div className="breadcome-list">
                                        <div className="row">
                                            <div className="col-lg-6 col-md-6 col-sm-6 col-xs-6">
                                                <div className="breadcomb-wp">

                                                    <div className="breadcomb-ctn">
                                                        <h2>Files</h2>

                                                    </div>

                                                </div>
                                            </div>

                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

                    <div className="product-status mg-b-30">
                        <div className="container-fluid">
                            <div className="row">
                                <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                    <div className="product-status-wrap">
                                        <h4>Files List</h4>


                                      

                                        
                                        <div className="row">
                                        <div className="col-lg-3 col-md-3 col-sm-4">
                                            <div className="folderblock">
                                                <div className="folderhead">Folders
                        <span className="pull-right" data-toggle="modal" data-target="#exampleModal">
                            <i className="fa fa-plus-circle" aria-hidden="true"></i>
                            </span>
                                                </div>
                        <div className="folderrow"><i className="fa fa-folder-open" aria-hidden="true"></i> Idli Machine V.1.0</div>
                        <div className="folderrow"><i className="fa fa-folder-open" aria-hidden="true"></i> Idli Machine V.2.0</div>
                        <div className="folderrow"><i className="fa fa-folder-open" aria-hidden="true"></i> Idli Machine V.3.0</div>
                        <div className="folderrow"><i className="fa fa-folder-open" aria-hidden="true"></i> Idli Machine V.4.0</div>
                        <div className="folderrow"><i className="fa fa-folder-open" aria-hidden="true"></i> Idli Machine V.5.0</div>
                                            </div>
                                        </div>

                                        <div className="col-lg-9 col-md-9 col-sm-8">
                                            <div className="folderheading">Idli Machine V.1.0 <span className="pull-right">
                                                
                                            <div className="uploadfile">
                                            <Link to='/AddFiles'>Add File</Link>
                                        </div> 
                                        </span></div>

                                        
{/*                                         
                                        {alertName
                                            ? <div
                                                    className="alert alert-success display-none"
                                                    style={{
                                                    display: "block"
                                                }}>
                                                    <a data-dismiss="alert" href="#" aria-hidden="true" className="close">Ã—</a>
                                                    Files {alertName}

                                                    Successfully
                                                </div>
                                            : ''} */}
                                        <table className="table table-bordered paginationTable">
                                            <thead>
                                                <tr>
                                                                                                       <th>File Name</th>
                                                    <th>uploaded By</th>
                                                    <th>uploaded On</th>
                                                    <th>Action</th>

                                                </tr>
                                            </thead>
                                            <tbody>

                                                {this.state.Files && this
                                                    .state
                                                    .Files
                                                    .map((data, index) => {
                                                        console.log(data);

                                                        return (
                                                            <tr key={index}>
                                                              
                                                                <td>Lorem Ipsum is simply dummy text of the printing.</td>
                                                                <td>{data.name}
                                                                </td>
                                                                <td>{data.created_on}
                                                                </td>
                                                                <td>

                                                                    <Link to={`/EditFile/${data.fileID}`}>
                                                                        <button data-toggle="tooltip" title="Edit" className="btn-folder btn btn-primary">
                                                                            <i className="fa fa-pencil-square-o" aria-hidden="true"></i>
                                                                        </button>
                                                                    </Link>

                                                                        <button
                                                                        data-toggle="tooltip"
                                                                        title="Trash"
                                                                        className="btn-folder btn btn-danger"
                                                                        data-id={data.fileID}
                                                                        onClick={this
                                                                        .deleteItem
                                                                        .bind(this, data.fileID)}>
                                                                        <i className="fa fa-trash-o" aria-hidden="true"></i>
                                                                    </button>
                                                                </td>

                                                            </tr>
                                                        )

                                                    })}

                                            </tbody>
                                        </table>
                                        </div>

                                    </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

                </div>

                <FooterPage/>


                <div className="modal fade" id="exampleModal" tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
  <div className="modal-dialog" role="document">
    <div className="modal-content">
        <form onSubmit={this.handleSubmit}>
      <div className="modal-header">
        <h5 className="modal-title" id="exampleModalLabel">Create Folder</h5>
        <button type="button" className="close" data-dismiss="modal" aria-label="Close">
          <span aria-hidden="true">&times;</span>
        </button>
      </div>
      <div className="modal-body">
       <input type="text" className="form-control" />
      </div>
      <div className="modal-footer">
        <button type="button" className="btn btn-secondary" data-dismiss="modal">Cancel</button>
        <button type="submit" className="btn btn-primary">Save</button>
      </div>
      </form>
    </div>
  </div>
</div>

            </div>
