export default {
    getDesignations: () => {
        var ref = firebase
        .database()
        .ref("designation/");

    ref.on('value', snapshot => {
        const data = [];
        // console.log(snapshot); console.log(snapshot.val);

        snapshot.forEach(childSnapShot => {

            const Categories = {
                locationId: childSnapShot
                    .key
                    .toString(),
                    designation: childSnapShot
                    .val()
                    .designation,
               
                created_on: childSnapShot
                    .val()
                    .created_on
            };

            data.unshift(Categories);
        });

       return data;
    });

    },
}
