import React from "react";
import { Link } from "react-router-dom";
import HeaderPage from "./header";
import FooterPage from "./footer";
// import HomePage from "./home";
import SidebarPage from "./sidebar";
import firebase from '../config';
import Pagination from "react-js-pagination";
import swal from 'sweetalert';
import ReactNotification from "react-notifications-component";
import "react-notifications-component/dist/theme.css";
import SimpleReactValidator from "simple-react-validator";

import $ from 'jquery';
require('datatables.net');



class CategoryListPage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
        categoryList: [],
        loading: false,
        count: 1,
        activePage: "1",
        countPage: ""
      };
      this.deleteItem = this.deleteItem.bind(this);
      this.addNotification = this.addNotification.bind(this);
      this.notificationDOMRef = React.createRef();
  }

  componentDidMount() {
    this.setState({ loading: true });
    var ref = firebase.database().ref("category/");


    ref.on('value', snapshot => {
        const data =[];
        // console.log(snapshot);
        // console.log(snapshot.val);

        snapshot.forEach(childSnapShot => {
           
          const Categories = {
            categoryId: childSnapShot.key.toString(),
            userName:childSnapShot
            .val()
            .userName,
            category: childSnapShot.val().category,
            created_on: childSnapShot.val().created_on,
          };
  
          data.push(Categories);
        });
       
        this.setState({ categoryList:data,countPage: data.length,loading: false });
        console.log(this.state.categoryList);
      });
      this.interval = setTimeout(() =>  this.dataTable = this.$el.DataTable(), 1000);
}
componentWillUnmount() {
  this.dataTable.destroy(true);
  clearTimeout(this.interval);
}
deleteItem = id => {
    
  swal({title: "Are you sure?", text: "Do your really want to remove?", icon: "warning", buttons: true, dangerMode: true}).then(willDelete => {
    if (willDelete) {
      console.log(id);
      var playersRef = firebase.database().ref(`/category/${id}`);
      playersRef.remove();
      

    } else {}
    // window.location.reload("/Home");
   
});

  };
  handlePageChange(pageNumber) {
    console.log(`active page is ${pageNumber}`);
    this.setState({ activePage: pageNumber });
  }
  addNotification(message) {
    this.notificationDOMRef.current.addNotification({
      title: "Success",
      message: "Succesfully added!",
      type: "success",
      insert: "bottom",
      container: "top-center",
      animationIn: ["animated", "fadeIn"],
      animationOut: ["animated", "fadeOut"],
      dismiss: { duration: 2000 },
      dismissable: { click: true }
    });
  }
  render() {
    

    return (
      <div>
     <SidebarPage/>
   
    <div className="all-content-wrapper">
    <HeaderPage/>
   
          
            <div className="breadcome-area">
                <div className="container-fluid">
                    <div className="row">
                        <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                            <div className="breadcome-list">
                                <div className="row">
                                    <div className="col-lg-6 col-md-6 col-sm-6 col-xs-6">
                                        <div className="breadcomb-wp">
                                        
                                            <div className="breadcomb-ctn">
                                            <h2>Categories</h2>

                                                </div>
                                               

                                        </div>
                                    </div>
                          
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
      
     
            <div className="product-status mg-b-30">
            <div className="container-fluid">
                <div className="row">
                    <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                        <div className="product-status-wrap">
                            <h4>Categories List</h4>
                            <ReactNotification ref={this.notificationDOMRef} />
                            {sessionStorage.getItem("userRole")!="Merchant"?
                            <div className="add-product">
                                <Link to='/AddCategoryPage'>Add Category</Link>
                            </div>:""}
                            <table className="table table-bordered paginationTable">
                            <thead>
                                <tr>
                                    <th>S.No</th>
                                    <th>Category Name</th>
									 <th>Created By</th>
                   <th>Created On</th>
                   {sessionStorage.getItem("userRole")!="Merchant"?
                           <th>Action</th>:""}
                                
                                </tr>
                                </thead>
                                <tbody>
                                 
                                    
                                    {this.state.categoryList.map((data,index) =>{
                                        console.log(data);
                                       
                                        return(
                                         <tr key={index}>
                                         <td>{index+1}</td>
                                         <td>{data.category}</td>
                                         <td>{data.userName}	</td>
                                         <td>{data.created_on}	</td>
                                         {sessionStorage.getItem("userRole")!="Merchant"?
                           
                                         <td>									
                                            
                                         <Link to={`/EditCategoryPage/${data.categoryId}`}>
                                         <button data-toggle="tooltip" title="Edit" className="pd-setting-ed"><i className="fa fa-pencil-square-o" aria-hidden="true"></i></button>
                                         </Link>								
                     
                                         <button data-toggle="tooltip" title="Trash" className="pd-setting-ed" data-id={data.categoryId}  onClick={this.deleteItem.bind(this, data.categoryId)}><i className="fa fa-trash-o" aria-hidden="true"></i></button>
                                         </td>
                                         :""}
                                         
                                         </tr>)

                                    } )}
								

                   
                    </tbody>
                    </table>
                    {/* <div className="custom-pagination">
                            <Pagination
                          hideNavigation
                          activePage={this.state.activePage}
                          itemsCountPerPage={3}
                          totalItemsCount={this.state.countPage}
                          pageRangeDisplayed={3}
                          onChange={this.handlePageChange.bind(this)}
                        />
                            </div> */}
                        </div>
                    </div>
                </div>
            </div>
        </div> 
            
        </div>
        
         <FooterPage/>
    
      </div>
    );
  }
}

export default CategoryListPage;
