import React from "react";
import { Form } from 'reactstrap';
import HeaderPage from "./header";
import FooterPage from "./footer";
import SidebarPage from "./sidebar";
import firebase from '../config';
import { Editor } from '@tinymce/tinymce-react';
import SimpleReactValidator from "simple-react-validator";





class AddEmailContentPage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
        emailContentName:'',
        emailDesc:'',
        date:new Date().toLocaleString(),

    }
    this.validator = new SimpleReactValidator({
        className: "text-danger",
        validators: {
  
            whitespace: {
                message: "The :attribute not allowed first whitespace   characters.",
                rule: function (val, params, validator) {
                    // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                    // params.indexOf(val) === -1
                    return (validator.helpers.testRegex(val, /[^\s\\]/) && params.indexOf(val) === -1);
                }
            },
            specialChar: {
                message: "The :attribute not allowed special   characters.",
                rule: function (val, params, validator) {
                    // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                    // params.indexOf(val) === -1
                    return (validator.helpers.testRegex(val, /^[ A-Za-z0-9_@./#&+-]*$/i) && params.indexOf(val) === -1);
                }
            },
            specialCharText: {
                message: "The :attribute may only contain letters, dot and spaces.",
                rule: function (val, params, validator) {
                    // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                    // params.indexOf(val) === -1
                    return (validator.helpers.testRegex(val, /^[ A-Za-z_@./#&+-]*$/i) && params.indexOf(val) === -1);
                }
            }
        }
    });
    

  }
  onChange = (event) => {
      
    this.setState({ [event.target.name]: event.target.value });
  };

  handleSubmit = (event) => {
        event.preventDefault();
        if (this.validator.allValid()) {
        let dbCon = firebase.database().ref('/emailContent');
        dbCon.push({
            emailContentName: this.state.emailContentName,
            emailDesc: this.state.emailDesc,
            
            created_on: this.state.date
        });      
      this.setState({
        emailContentName:'',
        emailDesc:'',
      });
      this.props.history.push("/EmailContentListPage");
    } else {
        this
            .validator
            .showMessages();
        this.forceUpdate();
      }
    
  };
  handleEditorChange = (emailDesc) => {
    this.setState({
        emailDesc,
    });
  };
  
  render() {
    

    return (
      <div>
     <SidebarPage/>
   
    <div className="all-content-wrapper">
    <HeaderPage/>
   
          
            <div className="breadcome-area">
                <div className="container-fluid">
                    <div className="row">
                        <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                            <div className="breadcome-list">
                                <div className="row">
                                    <div className="col-lg-6 col-md-6 col-sm-6 col-xs-6">
                                        <div className="breadcomb-wp">
                                        
                                            <div className="breadcomb-ctn">
                                            <h2>Email Content</h2>
                                                </div>
                                        </div>
                                    </div>
                          
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
      
     
            <div className="single-product-tab-area mg-b-30">
          
            <div className="single-pro-review-area">
                <div className="container-fluid">
                    <div className="row">
                        <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                            <div className="review-tab-pro-inner">
                   
				   <h4>Add  Email Content</h4>				   
				   
                            
                            <div className="product-tab-list tab-pane fade active in">
                            <Form
      onSubmit={this.handleSubmit}
        id="Add-email"
      >
                            
                            <div className="row m-b-20">
                            <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                            <label>Email Content Name<span style={{color: 'red' }}>*</span></label>
                            <input type="text" className="form-control" placeholder="Email Content Name*"  id="add_emailContent"
                            name="emailContentName" onChange={ this.onChange } value={ this.state.emailContentName } required=""/>
                           
                           {this.validator.message("Email Content Name", this.state.emailContentName, "required|specialChar|min:3|max:70")}

                            </div>
                            </div>
                            <div className="row m-b-20">
                            <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                         
                            <label>Email Description<span style={{color: 'red' }}>*</span></label>

                        <Editor value={this.state.emailDesc} onEditorChange={this.handleEditorChange} />
                        {this.validator.message("Email Description", this.state.emailDesc, "required|min:3|max:250")}

                         </div>

                            </div>
                            

                                        <div className="row">
                                            <div className="col-lg-6 col-md-6 col-sm-12 col-xs-12">
                                                <div className="text-left custom-pro-edt-ds">
                                                    <button type="submit" className="btn btn-ctl-bt waves-effect waves-light">Add
														</button>
                                                 
                                                </div>
                                            </div>
                                        </div>
                                        </Form>
                                    </div>
                                 

			
								
						
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
        </div>
         <FooterPage/>
    
      </div>
    );
  }
}

export default AddEmailContentPage;
