import React from "react";
import {Link} from "react-router-dom";
import FadeIn from "react-fade-in";
import axios from "axios";
import HeaderPage from "./header";
import FooterPage from "./footer";
// import HomePage from "./home";
import SidebarPage from "./sidebar";
import firebase from '../config';
import swal from 'sweetalert';
import $ from 'jquery';
$.DataTable = require('datatables.net');

class EmailContentListPage extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            emailContentList: [],
            loading: false,
            count: 1,
            activePage: "1",
            countPage: ""
        };
        
    }

    componentDidMount() {
        this.setState({loading: true});
        var ref = firebase
            .database()
            .ref("emailContent/");

        ref.on('value', snapshot => {
            const data = [];

            snapshot.forEach(childSnapShot => {

                const EmailData = {
                    emailContentId: childSnapShot
                        .key
                        .toString(),
                    emailContentName: childSnapShot
                        .val()
                        .emailContentName,
                    emailDesc: childSnapShot
                        .val()
                        .emailDesc,
                    created_on: childSnapShot
                        .val()
                        .created_on
                };

                data.push(EmailData);
            });

            this.setState({emailContentList: data, countPage: data.length, loading: false});
            console.log(this.state.emailContentList);
            this.interval = setTimeout(() => $('.paginationTable').DataTable(), 1000);
        });
    }
    componentWillUnmount() {
        clearTimeout(this.interval);
    }
    deleteItem = id => {

        swal({title: "Are you sure?", text: "Do your really want to remove?", icon: "warning", buttons: true, dangerMode: true}).then(willDelete => {
            if (willDelete) {
                console.log(id);
                var playersRef = firebase
                    .database()
                    .ref(`/emailContent/${id}`);
                playersRef.remove();
                this
                    .props
                    .history
                    .push("/PincodeList/Deleted");

            } else {}

        });

    };

    render() {
        var pathname = this
            .props
            .location
            .pathname
            .split("/");
        var alertName = pathname[2];

        return (
            <div>
                <SidebarPage/>

                <div className="all-content-wrapper">
                    <HeaderPage/>

                    <div className="breadcome-area">
                        <div className="container-fluid">
                            <div className="row">
                                <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                    <div className="breadcome-list">
                                        <div className="row">
                                            <div className="col-lg-6 col-md-6 col-sm-6 col-xs-6">
                                                <div className="breadcomb-wp">

                                                    <div className="breadcomb-ctn">
                                                        <h2>Merchants</h2>
                                                    </div>
                                                </div>
                                            </div>

                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

                    <div className="product-status mg-b-30">
                        <div className="container-fluid">
                            <div className="row">
                                <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                    <div className="product-status-wrap">
                                        <h4>Email Content List</h4>
                                        <div className="add-product">
                                            <Link to='/AddEmailContentPage'>Add Email Content</Link>
                                        </div>
                                        {alertName
                                            ? <div
                                                    className="alert alert-success display-none"
                                                    style={{
                                                    display: "block"
                                                }}>
                                                    <a data-dismiss="alert" href="#" aria-hidden="true" className="close">Ã—</a>
                                                    Email Content {alertName}
                                                    Successfully
                                                </div>
                                            : ''}
                                        <table className="table table-bordered paginationTable">
                                            <thead>
                                                <tr>
                                                    <th>S.No</th>
                                                    <th>Name</th>
                                                    <th>Description</th>
                                                    <th>Created on</th>
                                                    <th>Action</th>
                                                </tr>
                                            </thead>
                                            <tbody>

                                                {this.state.emailContentList && this
                                                    .state
                                                    .emailContentList
                                                    .map((data, index) => {
                                                        console.log(data);

                                                        return (
                                                            <tr key={index}>
                                                                <td>{index + 1}</td>
                                                                <td>{data.emailContentName}</td>
                                                                <td>{data.emailDesc}</td>
                                                                <td>{data.created_on}
                                                                </td>
                                                                <td>

                                                                    <Link to={`/EditEmailContentPage/${data.emailContentId}`}>
                                                                        <button data-toggle="tooltip" title="Edit" className="pd-setting-ed">
                                                                            <i className="fa fa-pencil-square-o" aria-hidden="true"></i>
                                                                        </button>
                                                                    </Link>

                                                                    <button
                                                                        data-toggle="tooltip"
                                                                        title="Trash"
                                                                        className="pd-setting-ed"
                                                                        onClick={this
                                                                        .deleteItem
                                                                        .bind(this, data.emailContentId)}>
                                                                        <i className="fa fa-trash-o" aria-hidden="true"></i>
                                                                    </button>
                                                                </td>

                                                            </tr>
                                                        )

                                                    })}

                                            </tbody>
                                        </table>
                                      
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

                </div>

                <FooterPage/>

            </div>
        );
    }
}

export default EmailContentListPage;
