import React from "react";
import {Link} from "react-router-dom";
import HeaderPage from "./header";
import FooterPage from "./footer";
import SidebarPage from "./sidebar";
import firebase from '../config';
import swal from 'sweetalert';
import $ from 'jquery';
$.DataTable = require('datatables.net');

class RoleListPage extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            gstList: [],
            loading: false,
            count: 1,
            activePage: "1",
            countPage: ""
        };
        this.deleteItem = this
            .deleteItem
            .bind(this);

    }

    componentDidMount() {
        this.setState({loading: true});
        var ref = firebase
            .database()
            .ref("Role/");

        ref.on('value', snapshot => {
            const data = [];

            snapshot.forEach(childSnapShot => {

                const GSTData = {
                    gstId: childSnapShot
                        .key
                        .toString(),
                    Role: childSnapShot
                        .val()
                        .Role,
                    created_on: childSnapShot
                        .val()
                        .created_on
                };

                data.unshift(GSTData);
            });

            this.setState({gstList: data, countPage: data.length, loading: false});
            this.interval = setTimeout(() => $('.paginationTable').DataTable(), 1000);
        });
    }
    componentWillUnmount() {
        clearTimeout(this.interval);
    }
    deleteItem = id => {

        swal({title: "Are you sure?", text: "Do your really want to remove?", icon: "warning", buttons: true, dangerMode: true}).then(willDelete => {
            if (willDelete) {
                console.log(id);
                var playersRef = firebase
                    .database()
                    .ref(`/Role/${id}`);
                playersRef.remove();

                this
                    .props
                    .history
                    .push("/RoleListPage/Deleted");
            } else {}
            // window.location.reload("/Home");

        });

    };

    render() {

        var pathname = this
            .props
            .location
            .pathname
            .split("/");
        var alertName = pathname[2];
        return (
            <div>
                <SidebarPage/>

                <div className="all-content-wrapper">
                    <HeaderPage/>

                    <div className="breadcome-area">
                        <div className="container-fluid">
                            <div className="row">
                                <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                    <div className="breadcome-list">
                                        <div className="row">
                                            <div className="col-lg-6 col-md-6 col-sm-6 col-xs-6">
                                                <div className="breadcomb-wp">

                                                    <div className="breadcomb-ctn">
                                                        <h2>setting</h2>
                                                    </div>
                                                </div>
                                            </div>

                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

                    <div className="product-status mg-b-30">
                        <div className="container-fluid">
                            <div className="row">
                                <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                    <div className="product-status-wrap">
                                        <h4>Role List</h4>
                                        <div className="add-product">
                                            <Link to='/AddRolePage'>Add Role</Link>
                                        </div>
                                        {alertName
                                            ? <div
                                                    className="alert alert-success display-none"
                                                    style={{
                                                    display: "block"
                                                }}>
                                                    <a data-dismiss="alert" href="#" aria-hidden="true" className="close">Ã—</a>
                                                    Role {alertName} &nbsp;
                                                    Successfully
                                                </div>
                                            : ''}
                                        <table className="table table-bordered paginationTable">
                                            <thead>
                                                <tr>
                                                    <th>S.No</th>
                                                    <th>Role</th>
                                                    <th>Created on</th>
                                                    <th>Action</th>
                                                </tr>
                                            </thead>
                                            <tbody>

                                                {this.state.gstList && this
                                                    .state
                                                    .gstList
                                                    .map((data, index) => {
                                                        console.log(data);

                                                        return (
                                                            <tr key={index}>
                                                                <td>{index + 1}</td>
                                                                <td>{data.Role}</td>
                                                                <td>{data.created_on}
                                                                </td>
                                                                <td>

                                                                    <Link to={`/EditRolePage/${data.gstId}`}>
                                                                        <button data-toggle="tooltip" title="Edit" className="pd-setting-ed">
                                                                            <i className="fa fa-pencil-square-o" aria-hidden="true"></i>
                                                                        </button>
                                                                    </Link>

                                                                    <button
                                                                        data-toggle="tooltip"
                                                                        title="Trash"
                                                                        className="pd-setting-ed"
                                                                        data-id={data.gstId}
                                                                        onClick={this
                                                                        .deleteItem
                                                                        .bind(this, data.gstId)}>
                                                                        <i className="fa fa-trash-o" aria-hidden="true"></i>
                                                                    </button>
                                                                </td>

                                                            </tr>
                                                        )

                                                    })}

                                            </tbody>
                                        </table>

                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

                </div>

                <FooterPage/>

            </div>
        );
    }
}

export default RoleListPage;
