import React from "react";
import {Link} from "react-router-dom";

import {Form} from 'reactstrap';
import HeaderPage from "./header";
import FooterPage from "./footer";
import SidebarPage from "./sidebar";
import firebase from '../config';

import ReactNotification from "react-notifications-component";
import "react-notifications-component/dist/theme.css";
import SimpleReactValidator from "simple-react-validator";

class EditSubCategoryPage extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            category: '',
            subCategory: '',
            categoryList: [],
            date: new Date().toLocaleString()
        }
        this.addNotification = this
            .addNotification
            .bind(this);
        this.notificationDOMRef = React.createRef();
        this.validator = new SimpleReactValidator({
            className: "text-danger",
            validators: {

                whitespace: {
                    message: "The :attribute not allowed first whitespace   characters.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /[^\s\\]/) && params.indexOf(val) === -1);
                    }
                },
                specialChar: {
                    message: "The :attribute not allowed special   characters.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /^[ A-Za-z0-9_@./#&+-]*$/i) && params.indexOf(val) === -1);
                    }
                },
                specialCharText: {
                    message: "The :attribute may only contain letters, dot and spaces.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /^[ A-Za-z_@./#&+-]*$/i) && params.indexOf(val) === -1);
                    }
                }
            }
        });

    }
    componentDidMount() {
        const {categoryID} = this.props.match.params;
        console.log(categoryID);

        var ref = firebase
            .database()
            .ref(`sub_category/${categoryID}`);

        ref.on('value', snapshot => {
            var categories = snapshot.val();
            // console.log(categories)

            this.setState({category: categories.category, subCategory: categories.subCategory, loading: false});
            console.log(this.state.category);
        });

    }
    componentWillMount() {
        this.setState({loading: true});
        var ref = firebase
            .database()
            .ref("category/");

        ref.on('value', snapshot => {
            const data = [];

            snapshot.forEach(childSnapShot => {

                const Categories = {
                    categoryId: childSnapShot
                        .key
                        .toString(),
                    category: childSnapShot
                        .val()
                        .category,

                    created_on: childSnapShot
                        .val()
                        .created_on
                };

                data.push(Categories);
            });

            this.setState({categoryList: data, loading: false});
            console.log(this.state.categoryList);
        });

    }
    onChange = (event) => {

      this.setState({
          [event.target.name]: event.target.value
      });
  };

    handleSubmit = (event) => {
        event.preventDefault();
        if (this.validator.allValid()) {
            const {categoryID} = this.props.match.params;
            var userName = sessionStorage.getItem("userName");
            if (this.state.category && this.state.category.trim().length !== 0) {
                this.addNotification();
                event.preventDefault();
                let dbCon = firebase
                    .database()
                    .ref(`/sub_category/${categoryID}`);
                console.log(dbCon);
                dbCon.update({category: this.state.category, subCategory: this.state.subCategory, userName: userName, created_on: this.state.date});

                this
                    .props
                    .history
                    .push("/SubCategoryListPage");
            }
        } else {
            this
                .validator
                .showMessages();
            this.forceUpdate();
        }
    };
    addNotification() {
        this
            .notificationDOMRef
            .current
            .addNotification({
                title: "Success",
                message: "Succesfully added!",
                type: "success",
                insert: "bottom",
                container: "top-center",
                animationIn: [
                    "animated", "fadeIn"
                ],
                animationOut: [
                    "animated", "fadeOut"
                ],
                dismiss: {
                    duration: 2000
                },
                dismissable: {
                    click: true
                }
            });
    }
    render() {

        return (
            <div>
                <SidebarPage/>

                <div className="all-content-wrapper">
                    <HeaderPage/>

                    <div className="breadcome-area">
                        <div className="container-fluid">
                            <div className="row">
                                <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                    <div className="breadcome-list">
                                        <div className="row">
                                            <div className="col-lg-6 col-md-6 col-sm-6 col-xs-6">
                                                <div className="breadcomb-wp">

                                                    <div className="breadcomb-ctn">
                                                        <h2>Categories</h2>
                                                    </div>
                                                </div>
                                            </div>

                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

                    <div className="single-product-tab-area mg-b-30">

                        <div className="single-pro-review-area">
                            <div className="container-fluid">
                                <div className="row">
                                    <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                        <div className="review-tab-pro-inner">

                                            <h4>Edit Sub Category</h4>

                                            <ReactNotification ref={this.notificationDOMRef}/>

                                            <div className="product-tab-list tab-pane fade active in">
                                                <Form onSubmit={this.handleSubmit} id="Edit-category">

                                                    <div className="row m-b-20">
                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                            <label>Select Category</label>
                                                            <select
                                                                name="select"
                                                                className="form-control pro-edt-select form-control-primary"
                                                                name="category"
                                                                onChange={this.onChange}>
                                                                <option value={this.state.category}>{this.state.category}</option>
                                                                {this.state.categoryList && this
                                                                    .state
                                                                    .categoryList
                                                                    .map((data, index) => {

                                                                        return (
                                                                            <option value={data.category} key={index}>{data.category}</option>
                                                                        )

                                                                    })}
                                                                {this
                                                                    .validator
                                                                    .message("Select category", this.state.categoryList, "required")}

                                                            </select>

                                                        </div>
                                                    </div>
                                                    <div className="row m-b-20">
                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">

                                                            <input
                                                                type="text"
                                                                className="form-control"
                                                                placeholder="Sub Category Name*"
                                                                name="subCategory"
                                                                onChange={this.onChange}
                                                                value={this.state.subCategory}/> {this
                                                                .validator
                                                                .message("Sub Category Name", this.state.subCategory, "required|whitespace|specialChar|min:3|max:70")}

                                                        </div>

                                                    </div>

                                                    <div className="row">
                                                        <div className="col-lg-6 col-md-6 col-sm-12 col-xs-12">
                                                            <div className="text-left custom-pro-edt-ds">
                                                                <button type="submit" className="btn btn-ctl-bt waves-effect waves-light">Save
                                                                </button>
                                                                <Link to="/SubCategoryListPage">
                                                                    <button type="button" className="btn btn-ctl-bt waves-effect waves-light">Cancel
                                                                    </button>
                                                                </Link>

                                                            </div>
                                                        </div>
                                                    </div>
                                                </Form>
                                            </div>

                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
                <FooterPage/>

            </div>
        );
    }
}
export default EditSubCategoryPage;
