import React from "react";
import {Link} from "react-router-dom";
import HeaderPage from "./header";
import FooterPage from "./footer";
// import HomePage from "./home";
import SidebarPage from "./sidebar";
import firebase from '../config';
import Moment from 'moment';

import swal from 'sweetalert';
import $ from 'jquery';
$.DataTable = require('datatables.net');

class ProductListPage extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            productList: [],
            loading: false,
            count: 1,
            activePage: "1",
            countPage: "",
            status: '',
            usePermList: ""
        };
        this.deleteItem = this
            .deleteItem
            .bind(this);

    }
    componentWillMount() {
        var sessionId = sessionStorage.getItem("userRole");
        this.setState({loading: true});
        var ref = firebase
            .database()
            .ref("Role")
            .orderByChild("Role")
            .equalTo(sessionId);

        ref.on('value', snapshot => {
            const data = [];

            snapshot.forEach(childSnapShot => {

                const orderStatus = {
                    RoleId: childSnapShot
                        .key
                        .toString(),

                    products: childSnapShot
                        .val()
                        .products
                };

                data.push(orderStatus);
            });

            this.setState({usePermList: data[0].products, loading: false});
            console.log(this.state.usePermList);

        });
    }
    componentDidMount() {
        this.setState({loading: true});
        var sessionId = sessionStorage.getItem("RoleId");
        var userRole = sessionStorage.getItem("userRole");
        if (userRole == "Merchant") {
            var ref = firebase
                .database()
                .ref("products/")
                .orderByChild("sessionId")
                .equalTo(sessionId);

        } else {
            var ref = firebase
                .database()
                .ref("products/");
        }

        ref.on('value', snapshot => {

            const data = [];
            // console.log(snapshot); console.log(snapshot.val);

            snapshot.forEach(childSnapShot => {

                const Products = {
                    productId: childSnapShot
                        .key
                        .toString(),
                    productName: childSnapShot
                        .val()
                        .productName,
                    category: childSnapShot
                        .val()
                        .category,
                    userName: childSnapShot
                        .val()
                        .userName,
                    price: childSnapShot
                        .val()
                        .price,
                    sellingPrice: childSnapShot
                        .val()
                        .sellingPrice,
                    status: childSnapShot
                        .val()
                        .status,
                    stock: childSnapShot
                        .val()
                        .stock,
                    created_on: childSnapShot
                        .val()
                        .created_on
                };

                data.push(Products);
            });

            this.setState({productList: data, countPage: data.length, loading: false});
            console.log(this.state.productList);
            this.interval = setTimeout(() => $('.paginationTable').DataTable(), 1000);
        });

    }
    componentWillUnmount() {
        //this.dataTable.destroy(true);
        clearTimeout(this.interval);
    }
    onChange = (event) => {

        var productId = event.target.id;
        console.log(productId);
        this.setState({
            [event.target.name]: event.target.value
        });
        console.log(event.target.value);

        let dbCon = firebase
            .database()
            .ref(`/products/${productId}`);
        console.log(dbCon);
        dbCon.update({
            [event.target.name]: event.target.value
        });
    };
    onStockChange = (event) => {

        var productId = event.target.id;

        let dbCon = firebase
            .database()
            .ref(`/products/${productId}`);
        console.log(dbCon);
        dbCon.update({stock: event.target.value});
    };

    deleteItem = id => {

        swal({title: "Are you sure?", text: "Do your really want to remove?", icon: "warning", buttons: true, dangerMode: true}).then(willDelete => {
            if (willDelete) {
                console.log(id);
                var playersRef = firebase
                    .database()
                    .ref(`/products/${id}`);
                playersRef.remove();
                this
                    .props
                    .history
                    .push("/ProductListPage/Deleted");
            } else {}
            // window.location.reload("/Home");

        });
    }
    handlePageChange(pageNumber) {
        console.log(`active page is ${pageNumber}`);
        this.setState({activePage: pageNumber});
    }

    render() {

        var pathname = this
            .props
            .location
            .pathname
            .split("/");
        var alertName = pathname[2];
        console.log(alertName);
        return (
            <div>
                <SidebarPage/>

                <div className="all-content-wrapper">
                    <HeaderPage/>

                    <div className="breadcome-area">
                        <div className="container-fluid">
                            <div className="row">
                                <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                    <div className="breadcome-list">
                                        <div className="row">
                                            <div className="col-lg-6 col-md-6 col-sm-6 col-xs-6">
                                                <div className="breadcomb-wp">

                                                    <div className="breadcomb-ctn">
                                                        <h2>Products</h2>
                                                    </div>
                                                </div>
                                            </div>

                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

                    <div className="product-status mg-b-30">
                        <div className="container-fluid">
                            <div className="row">
                                <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                    <div className="product-status-wrap">
                                        <h4>Products List</h4>
                                        <div className="add-product">
                                            {this.state.usePermList == "read&write"
                                                ? <Link to='/AddProductPage'>Add Product</Link>
                                                : ''}
                                        </div>
                                        {alertName
                                            ? <div
                                                    className="alert alert-success display-none"
                                                    style={{
                                                    display: "block"
                                                }}>
                                                    <a data-dismiss="alert" href="#" aria-hidden="true" className="close">×</a>
                                                    Products {alertName}
                                                    Successfully
                                                </div>
                                            : ''}
                                        <table className="table table-bordered paginationTable">
                                            <thead>

                                                <tr>
                                                    <th>S.No</th>
                                                    <th>Product Name</th>
                                                    <th>Category Name</th>
                                                    <th>Price</th>
                                                    <th>Created BY</th>
                                                    <th>Created On</th>
                                                    {sessionStorage.getItem("userRole") == "Admin"||sessionStorage.getItem("userRole") == "Super Admin"
                                                        ? <th>Product Stock</th>
                                                        : ''}
                                                    {sessionStorage.getItem("userRole") == "Admin"||sessionStorage.getItem("userRole") == "Super Admin"
                                                        ? <th>Status</th>
                                                        : ""}

                                                    <th>Action</th>

                                                </tr>
                                            </thead>
                                            <tbody>
                                                {this
                                                    .state
                                                    .productList
                                                    .map((data, index) => {
                                                        console.log(data);

                                                        return (

                                                            <tr key={index}>
                                                                <td>{index + 1}</td>
                                                                <td>{data.productName}</td>
                                                                <td>{data.category}</td>
                                                                <td>{data.sellingPrice}
                                                                </td>
                                                                <td>{data.userName}
                                                                </td>
                                                                <td>{Moment(data.created_on).format('D MMM Y')}
                                                                </td>

                                                                {sessionStorage.getItem("userRole") == "Admin"||sessionStorage.getItem("userRole") == "Super Admin"
                                                                    ? <td>
                                                                            <select
                                                                                name="stock"
                                                                                value={data.stock}
                                                                                onChange={this.onStockChange}
                                                                                id={data.productId}
                                                                                className="form-control pro-edt-select form-control-primary active">

                                                                                <option value="Available">Available
                                                                                </option>
                                                                                <option value="Sold out">Sold out
                                                                                </option>
                                                                                <option value="Featured">Featured</option>

                                                                                <option value="BestSeller">BestSeller
                                                                                </option>
                                                                                <option value="OfferZone">OfferZone
                                                                                </option>
                                                                                <option value="Trending">Trending
                                                                                </option>

                                                                            </select>
                                                                        </td>
                                                                    : ''}
                                                                {sessionStorage.getItem("userRole") == "Admin"||sessionStorage.getItem("userRole") == "Super Admin"
                                                                    ? <td>
                                                                            <select
                                                                                name="status"
                                                                                value={data.status}
                                                                                onChange={this.onChange}
                                                                                id={data.productId}
                                                                                className="form-control pro-edt-select form-control-primary active">
                                                                                <option value="Active">Active</option>
                                                                                <option value="InActive">InActive</option>

                                                                            </select>
                                                                        </td>
                                                                    : ""}

                                                                <td>
                                                                    <Link to={`/ProductListView/${data.productId}`}>
                                                                        <button data-toggle="tooltip" title="View" className="pd-setting-ed">
                                                                            <i className="fa fa fa-eye" aria-hidden="true"></i>
                                                                        </button>
                                                                    </Link>

                                                                    {this.state.usePermList == "read&write"
                                                                        ? <span>
                                                                                <Link to={`/EditProductPage/${data.productId}`}>
                                                                                    <button data-toggle="tooltip" title="Edit" className="pd-setting-ed">
                                                                                        <i className="fa fa-pencil-square-o" aria-hidden="true"></i>
                                                                                    </button>
                                                                                </Link>

                                                                                <button
                                                                                    data-toggle="tooltip"
                                                                                    title="Trash"
                                                                                    className="pd-setting-ed"
                                                                                    onClick={this
                                                                                    .deleteItem
                                                                                    .bind(this, data.productId)}>
                                                                                    <i className="fa fa-trash-o" aria-hidden="true"></i>
                                                                                </button>
                                                                            </span>
                                                                        : ""}
                                                                </td>
                                                            </tr>
                                                        )
                                                    })}

                                            </tbody>

                                        </table>

                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
                <FooterPage/>

            </div>
        );
    }
}

export default ProductListPage;
