import React from "react";
import {Link} from "react-router-dom";
import HeaderPage from "./header";
import FooterPage from "./footer";
// import HomePage from "./home";
import SidebarPage from "./sidebar";
import firebase from '../config';
import Pagination from "react-js-pagination";
import swal from 'sweetalert';
import { UncontrolledAlert } from 'reactstrap';
import 'bootstrap/dist/css/bootstrap.css';

import $ from 'jquery';
$.DataTable = require('datatables.net');
class MultipleSliderList extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            categoryList: [],
            loading: false,
            count: 1,
            activePage: "1",
            countPage: "",
            status: '',
            visible:false, 
        };
        this.deleteItem = this
            .deleteItem
            .bind(this);

    }

    componentDidMount() {
        var pathname = this.props.location.pathname;
        if(pathname=="/MultipleSliderList/success"){
           
         
            this.setState({ visible: true}, () => {
               alert(this.state.visible, 'visible');
                });
               


        }

     console.log(pathname);
        this.setState({loading: true});
        var ref = firebase
            .database()
            .ref("MultipleSlider/");

        ref.on('value', snapshot => {
            const data = [];

            snapshot.forEach(childSnapShot => {

                const GSTData = {
                    cmsId: childSnapShot
                        .key
                        .toString(),
                    pageTitle: childSnapShot
                        .val()
                        .pageTitle,
                    userName: childSnapShot
                        .val()
                        .userName,
                    status: childSnapShot
                        .val()
                        .status,
                        Category: childSnapShot
                        .val()
                        .category,
                    sliderUrl: childSnapShot
                        .val()
                        .sliderUrl,
                    uploadImage: childSnapShot
                        .val()
                        .uploadImage,
                    created_on: childSnapShot
                        .val()
                        .created_on
                };

                data.push(GSTData);
            });

            this.setState({gstList: data, countPage: data.length, loading: false});
            console.log(this.state.gstList);
            this.interval = setTimeout(() => $('.paginationTable').DataTable(), 1000);
        });
    }
    componentWillUnmount() {
        //this.dataTable.destroy(true);
        clearTimeout(this.interval);
      }
    deleteItem = id => {

        swal({title: "Are you sure?", text: "Do your really want to remove?", icon: "warning", buttons: true, dangerMode: true}).then(willDelete => {
            if (willDelete) {
                console.log(id);
                var playersRef = firebase
                    .database()
                    .ref(`/MultipleSlider/${id}`);
                playersRef.remove();

            } else {}
            // window.location.reload("/Home");

        });

    };
    handlePageChange(pageNumber) {
        console.log(`active page is ${pageNumber}`);
        this.setState({activePage: pageNumber});
    }

    onChange = (event) => {

        var productId = event.target.id;
        console.log(productId);
        this.setState({
            [event.target.name]: event.target.value
        });
        console.log(event.target.value);

        let dbCon = firebase
            .database()
            .ref(`/MultipleSlider/${productId}`);
        console.log(dbCon);
        dbCon.update({status: event.target.value});
    };
    onDismiss=() =>{
        this.setState({ visible: false });
      }
    
    render() {

        return (
            <div>
                <SidebarPage/>

                <div className="all-content-wrapper">
                    <HeaderPage/>

                    <div className="breadcome-area">
                        <div className="container-fluid">
                            <div className="row">
                                <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                    <div className="breadcome-list">
                                        <div className="row">
                                            <div className="col-lg-6 col-md-6 col-sm-6 col-xs-6">
                                                <div className="breadcomb-wp">

                                                    <div className="breadcomb-ctn">
                                                        <h2>Slider</h2>
                                                    </div>
                                                </div>
                                            </div>

                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                   
                    <div className="product-status mg-b-30">
                        <div className="container-fluid">
                            <div className="row">
                                <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                    <div className="product-status-wrap">
                                        <h4>Multiple Slider List</h4>
                                        <div className="add-product">
                                            <Link to='/AddMultipleSlider'>Add Multiple Slider</Link>
                                        </div>
                                        <UncontrolledAlert color="info">
      I am an alert and I can be dismissed!
    </UncontrolledAlert>
                                        <table className="table table-bordered paginationTable">
                                            <thead>
                                                <tr>
                                                    <th>S.No</th>
                                                    <th>Slider Name</th>
                                                    <th>Slider Image</th>
                                                    <th>Slider Url</th>
                                                    <th>Category</th>
                                                    <th>Status</th>
                                                    <th>Created On</th>
                                                    <th>Action</th>
                                                </tr>
                                            </thead>
                                            <tbody>

                                                {this.state.gstList && this
                                                    .state
                                                    .gstList
                                                    .map((data, index) => {
                                                        console.log(data);

                                                        return (
                                                            <tr key={index}>
                                                                <td>{index + 1}</td>
                                                                <td>{data.pageTitle}</td>
                                                                <td className="img-product"><img src={data.uploadImage} alt=""/></td>
                                                                <td>{data.sliderUrl}</td>
                                                                <td>{data.Category}
                                                                </td>
                                                                <td>
                                                                    <select
                                                                        name="status"
                                                                        onChange={this.onChange}
                                                                        id={data.cmsId}
                                                                        className="form-control pro-edt-select form-control-primary active">
                                                                        <option
                                                                            value="Active"
                                                                            selected={data.status == "Active"
                                                                            ? true
                                                                            : false}>Active</option>
                                                                        <option
                                                                            value="InActive"
                                                                            selected={data.status == "InActive"
                                                                            ? true
                                                                            : false}>InActive</option>

                                                                    </select>
                                                                </td>
                                                                <td>{data.created_on}
                                                                </td>
                                                                <td>

                                                                    <Link to={`/EditMultipleSlider/${data.cmsId}`}>
                                                                        <button data-toggle="tooltip" title="Edit" className="pd-setting-ed">
                                                                            <i className="fa fa-pencil-square-o" aria-hidden="true"></i>
                                                                        </button>
                                                                    </Link>

                                                                    <button
                                                                        data-toggle="tooltip"
                                                                        title="Trash"
                                                                        className="pd-setting-ed"
                                                                        data-id={data.cmsId}
                                                                        onClick={this
                                                                        .deleteItem
                                                                        .bind(this, data.cmsId)}>
                                                                        <i className="fa fa-trash-o" aria-hidden="true"></i>
                                                                    </button>
                                                                </td>

                                                            </tr>
                                                        )

                                                    })}

                                            </tbody>
                                        </table>
                                      
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

                </div>

                <FooterPage/>

            </div>
        );
    }
}



export default MultipleSliderList;
