import React from "react";
import {Link} from "react-router-dom";
import HeaderPage from "./header";
import FooterPage from "./footer";
import SidebarPage from "./sidebar";
import firebase from '../config';
import SimpleReactValidator from "simple-react-validator";

class AddPinCode extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            pincode: '',
            area:'',
            taluka:'',
            district:'',
            date: new Date().toLocaleString()
        }

        this.validator = new SimpleReactValidator({
            className: "text-danger",
            validators: {

                whitespace: {
                    message: "The :attribute not allowed first whitespace   characters.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /[^\s\\]/) && params.indexOf(val) === -1);
                    }
                },
                specialChar: {
                    message: "The :attribute not allowed special   characters.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /^[ A-Za-z0-9_@./#&+-]*$/i) && params.indexOf(val) === -1);
                    }
                },
                specialCharText: {
                    message: "The :attribute may only contain letters, dot and spaces.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /^[ A-Za-z_@./#&+-]*$/i) && params.indexOf(val) === -1);
                    }
                }
            }
        });

    }
    onChange = (event) => {

        this.setState({
            [event.target.name]: event.target.value
        });
    };

    handleSubmit = (event) => {
        event.preventDefault();
        if (this.validator.allValid()) {
            if (this.state.pincode && this.state.pincode.trim().length !== 0) {

                event.preventDefault();
                let dbCon = firebase
                    .database()
                    .ref('/PinCode');
                dbCon
                    .push({pincode: this.state.pincode, created_on: this.state.date})
                    .then(response => {
                        try {
                            console.log(response);
                            this.setState({pincode: ''});
                            this
                                .props
                                .history
                                .push("/PincodeList/Created");

                        } catch (error) {}
                    });
            }
        } else {
            this
                .validator
                .showMessages();
            this.forceUpdate();
        }

    };

    render() {

        return (
            <div>
                <SidebarPage/>

                <div className="all-content-wrapper">
                    <HeaderPage/>

                    <div className="breadcome-area">
                        <div className="container-fluid">
                            <div className="row">
                                <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                    <div className="breadcome-list">
                                        <div className="row">
                                            <div className="col-lg-6 col-md-6 col-sm-6 col-xs-6">
                                                <div className="breadcomb-wp">

                                                    <div className="breadcomb-ctn">
                                                        <h2>Pin Code</h2>
                                                    </div>
                                                </div>
                                            </div>

                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

                    <div className="single-product-tab-area mg-b-30">

                        <div className="single-pro-review-area">
                            <div className="container-fluid">
                                <div className="row">
                                    <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                        <div className="review-tab-pro-inner">

                                            <h4>Add Pin Code</h4>
                                            <div className="product-tab-list tab-pane fade active in">
                                                <form onSubmit={this.handleSubmit} id="Add-category">

                                                    <div className="row m-b-20">
                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                            <label>Pincode<span style={{
                color: 'red'
            }}>*</span>
                                                            </label>
                                                            <input
                                                                type="number"
                                                                className="form-control"
                                                                placeholder="Pincode Value*"
                                                                id="add_GST"
                                                                name="pincode"
                                                                onChange={this.onChange}
                                                                value={this.state.pincode}/> {this
                                                                .validator
                                                                .message("Pincode Value", this.state.pincode, "required|numeric|min:5|max:10")}

                                                        </div>

                                                    </div>
                                                    <div className="row m-b-20">
                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                            <label>Area<span style={{
                color: 'red'
            }}>*</span>
                                                            </label>
                                                            <input
                                                                type="text"
                                                                className="form-control"
                                                                placeholder="Area*"
                                                                id="add_GST"
                                                                name="area"
                                                                onChange={this.onChange}
                                                                value={this.state.area}/> {this
                                                                .validator
                                                                .message("Area", this.state.area, "required|min:3|max:50")}
                                                            
                                                        </div>

                                                    </div>
                                                    <div className="row m-b-20">
                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                            <label>Taluk<span style={{
                color: 'red'
            }}>*</span>
                                                            </label>
                                                            <input
                                                                type="text"
                                                                className="form-control"
                                                                placeholder="Taluk*"
                                                                id="add_GST"
                                                                name="taluka"
                                                                onChange={this.onChange}
                                                                value={this.state.taluka}/> {this
                                                                .validator
                                                                .message("Taluk", this.state.taluka, "required|min:3|max:50")}
                                                            
                                                        </div>

                                                    </div>
                                                    <div className="row m-b-20">
                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                            <label>City<span style={{
                color: 'red'
            }}>*</span>
                                                            </label>
                                                            <input
                                                                type="text"
                                                                className="form-control"
                                                                placeholder="Taluk*"
                                                                id="add_GST"
                                                                name="city"
                                                                onChange={this.onChange}
                                                                value={this.state.city}/> {this
                                                                .validator
                                                                .message("City", this.state.city, "required|min:3|max:50")}
                                                            
                                                        </div>

                                                    </div>
                                                    <div className="row m-b-20">
                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                            <label>State<span style={{
                color: 'red'
            }}>*</span>
                                                            </label>
                                                            <input
                                                                type="text"
                                                                className="form-control"
                                                                placeholder="Taluk*"
                                                                id="add_GST"
                                                                name="state"
                                                                onChange={this.onChange}
                                                                value={this.state.state}/> {this
                                                                .validator
                                                                .message("state", this.state.state, "required|min:3|max:50")}
                                                            
                                                        </div>

                                                    </div>


                                                    <div className="row">
                                                        <div className="col-lg-6 col-md-6 col-sm-12 col-xs-12">
                                                            <div className="text-left custom-pro-edt-ds">

                                                                <button type="submit" className="btn btn-ctl-bt waves-effect waves-light">Add</button>
                                                                <Link to="/PincodeList">
                                                                    <button type="button" className="btn btn-ctl-bt waves-effect waves-light">Cancel
                                                                    </button>
                                                                </Link>

                                                            </div>
                                                        </div>
                                                    </div>
                                                </form>
                                            </div>

                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
                <FooterPage/>

            </div>
        );
    }
}

export default AddPinCode;
