import firebase from '../config';



export const getDesignations = () => {
    let returnObj = [];
    var ref = firebase
    .database()
    .ref("designation/");

ref.on('value', snapshot => {
 

    snapshot.forEach(childSnapShot => {

        const Categories = {
            locationId: childSnapShot
                .key
                .toString(),
                designation: childSnapShot
                .val()
                .designation,
           
            created_on: childSnapShot
                .val()
                .created_on
        };

        returnObj.unshift(Categories);
        return returnObj;
    });

 
});
    
  };
