import React from "react";
import {Link} from "react-router-dom";
import HeaderPage from "./header";
import FooterPage from "./footer";
import SidebarPage from "./sidebar";
import firebase from '../config';
import swal from 'sweetalert';
import $ from 'jquery';
$.DataTable = require('datatables.net');

class Coupons extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            gstList: [],
            loading: false,
            count: 1,
            activePage: "1",
            countPage: "",
            usePermList: ""
        };

    }
    componentWillMount() {
        var sessionId = sessionStorage.getItem("userRole");
        this.setState({loading: true});
        var ref = firebase
            .database()
            .ref("Role")
            .orderByChild("Role")
            .equalTo(sessionId);

        ref.on('value', snapshot => {
            const data = [];

            snapshot.forEach(childSnapShot => {

                const orderStatus = {
                    RoleId: childSnapShot
                        .key
                        .toString(),

                    coupons: childSnapShot
                        .val()
                        .coupons
                };

                data.push(orderStatus);
            });

            this.setState({usePermList: data[0].coupons, loading: false});
            console.log(this.state.usePermList);

        });
    }
    componentDidMount() {
        this.setState({loading: true});
        var sessionId = sessionStorage.getItem("RoleId");
        var ref = firebase
            .database()
            .ref("Coupons/")
            .orderByChild("sessionId")
            .equalTo(sessionId);

        ref.on('value', snapshot => {
            const data = [];

            snapshot.forEach(childSnapShot => {

                const GSTData = {
                    cmsId: childSnapShot
                        .key
                        .toString(),
                    couponCode: childSnapShot
                        .val()
                        .couponCode,
                    couponDescription: childSnapShot
                        .val()
                        .couponDescription,
                    type: childSnapShot
                        .val()
                        .type,
                    validFrom: childSnapShot
                        .val()
                        .validFrom,
                    validTo: childSnapShot
                        .val()
                        .validTo,
                    numberOfCoupons: childSnapShot
                        .val()
                        .numberOfCoupons,
                    usageType: childSnapShot
                        .val()
                        .usageType,
                    numberOfUsages: childSnapShot
                        .val()
                        .numberOfUsages,
                    offerValue: childSnapShot
                        .val()
                        .offerValue,
                    offerType: childSnapShot
                        .val()
                        .offerType,
                    created_on: childSnapShot
                        .val()
                        .created_on
                };

                data.push(GSTData);
            });

            this.setState({gstList: data, countPage: data.length, loading: false});
            console.log(this.state.gstList);

            this.interval = setTimeout(() => $('.paginationTable').DataTable(), 1000);
        });
    }
    componentWillUnmount() {
        //this.dataTable.destroy(true);
        clearTimeout(this.interval);
    }
    deleteItem = id => {

        swal({title: "Are you sure?", text: "Do your really want to remove?", icon: "warning", buttons: true, dangerMode: true}).then(willDelete => {
            if (willDelete) {
                console.log(id);
                var playersRef = firebase
                    .database()
                    .ref(`/Coupons/${id}`);
                playersRef.remove();

                this
                    .props
                    .history
                    .push("/Coupons/Deleted");

            } else {}
            // window.location.reload("/Home");

        });
    }
    handlePageChange(pageNumber) {
        console.log(`active page is ${pageNumber}`);
        this.setState({activePage: pageNumber});
    }

    render() {

        var pathname = this
            .props
            .location
            .pathname
            .split("/");
        var alertName = pathname[2];
        console.log(alertName);
        return (
            <div>
                <SidebarPage/>

                <div className="all-content-wrapper">
                    <HeaderPage/>

                    <div className="breadcome-area">
                        <div className="container-fluid">
                            <div className="row">
                                <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                    <div className="breadcome-list">
                                        <div className="row">
                                            <div className="col-lg-6 col-md-6 col-sm-6 col-xs-6">
                                                <div className="breadcomb-wp">

                                                    <div className="breadcomb-ctn">
                                                        <h2>Settings</h2>
                                                    </div>
                                                </div>
                                            </div>

                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

                    <div className="product-status mg-b-30">
                        <div className="container-fluid">
                            <div className="row">
                                <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                    <div className="product-status-wrap">
                                        <h4>Coupons</h4>
                                        {this.state.usePermList == "read&write"
                                            ? <div className="add-product">
                                                    <Link to='/AddCoupon'>Add Coupon</Link>
                                                </div>
                                            : ''}
                                        {alertName
                                            ? <div
                                                    className="alert alert-success display-none"
                                                    style={{
                                                    display: "block"
                                                }}>
                                                    <a data-dismiss="alert" href="#" aria-hidden="true" className="close">Ã—</a>
                                                    Coupons {alertName}
                                                    Successfully
                                                </div>
                                            : ''}
                                        <table className="table table-bordered paginationTable">
                                            <thead>
                                                <tr>
                                                    <th>S.No</th>
                                                    <th>Coupon Code</th>
                                                    <th>Coupon Type</th>
                                                    <th>Usage</th>
                                                    <th>Created on</th>
                                                    {this.state.usePermList == "read&write"
                                                        ? <th>Action</th>
                                                        : ""}
                                                </tr>
                                            </thead>
                                            <tbody>

                                                {this.state.gstList && this
                                                    .state
                                                    .gstList
                                                    .map((data, index) => {
                                                        console.log(data);

                                                        return (
                                                            <tr key={index}>
                                                                <td>{index + 1}</td>
                                                                <td>{data.couponCode}</td>
                                                                <td>{data.type}</td>
                                                                <td>{data.usageType}</td>
                                                                <td>{data.created_on}
                                                                </td>
                                                                {this.state.usePermList == "read&write"
                                                                    ? <td>

                                                                            <Link to={`/EditCoupon/${data.cmsId}`}>
                                                                                <button data-toggle="tooltip" title="Edit" className="pd-setting-ed">
                                                                                    <i className="fa fa-pencil-square-o" aria-hidden="true"></i>
                                                                                </button>
                                                                            </Link>

                                                                            <button
                                                                                data-toggle="tooltip"
                                                                                title="Trash"
                                                                                className="pd-setting-ed"
                                                                                data-id={data.cmsId}
                                                                                onClick={this
                                                                                .deleteItem
                                                                                .bind(this, data.cmsId)}>
                                                                                <i className="fa fa-trash-o" aria-hidden="true"></i>
                                                                            </button>
                                                                        </td>
                                                                    : ''}

                                                            </tr>
                                                        )

                                                    })}

                                            </tbody>
                                        </table>
                                        {/* <div className="custom-pagination">
                            <Pagination
                          hideNavigation
                          activePage={this.state.activePage}
                          itemsCountPerPage={3}
                          totalItemsCount={this.state.countPage}
                          pageRangeDisplayed={3}
                          onChange={this.handlePageChange.bind(this)}
                        />
                            </div> */}
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

                </div>

                <FooterPage/>

            </div>
        );
    }
}

export default Coupons;
