import React from "react";
import {Link} from "react-router-dom";
import {Form} from 'reactstrap';
import HeaderPage from "./header";
import FooterPage from "./footer";
import SidebarPage from "./sidebar";
import firebase from '../config';
import SimpleReactValidator from "simple-react-validator";
import FileUploader from "react-firebase-file-uploader";
import Select from 'react-select';
import Map from './map';
import {connect} from 'react-redux';
class EditLocation extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            location_Name: '',
            addressl:'',
            address2:'',
            land_Mark: '',
            city: '',
            pincode: '',
            contact_Person_Name: '',
            contact_Number: '',
            email:'',
            permission_type: '',
            specifEmpName: '',
            rental_Deposit_Amount:'',
            rent_Per_Month:'',
            electricity_Per_Month:'',
            wifi_Per_Month:'',
            additional_Expenses:'',
            notes:'',
            EmployeesList:[],
            uploadImage:'',
            google_Map:'',
            isUploading: false,


            error_message: '',
            created_on: new Date().toLocaleString(),
            validError: true,
            avatar: "",
         
            progress: 0,
            avatarURL: "",
            filenames: [],
            downloadURLs: "",
            uploadProgress: 0,
            validError: true,
            
            employer_sevice_message:'',
        }

        this.validator = new SimpleReactValidator({
            className: "text-danger",
            validators: {

                whitespace: {
                    message: "The :attribute not allowed first whitespace   characters.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /[^\s\\]/) && params.indexOf(val) === -1);
                    }
                },
                specialChar: {
                    message: "The :attribute not allowed special   characters.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /^[ A-Za-z0-9_@./#&+-]*$/i) && params.indexOf(val) === -1);
                    }
                },
                specialCharText: {
                    message: "The :attribute may only contain letters, dot and spaces.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /^[ A-Za-z_@./#&+-]*$/i) && params.indexOf(val) === -1);
                    }
                }
            }
        });

    }
    componentDidMount() {
        const {locationId} = this.props.match.params;
        console.log(locationId);
        var sessionId = sessionStorage.getItem("RoleId"); 
        var ref = firebase
            .database()
            .ref(`Locations/${locationId}`);

        ref.on('value', snapshot => {
            var categories = snapshot.val();

            // console.log(categories)

            this.setState({
                location_Name: categories.location_Name,
                addressl: categories.addressl,
                address2: categories.address2,
                land_Mark: categories.land_Mark,
                city: categories.city,
                pincode: categories.pincode,
                contact_Person_Name: categories.contact_Person_Name,
                contact_Number: categories.contact_Number,
                email: categories.email,
                rental_Deposit_Amount: categories.rental_Deposit_Amount,
                rent_Per_Month: categories.rent_Per_Month,
                electricity_Per_Month: categories.electricity_Per_Month,
                wifi_Per_Month: categories.wifi_Per_Month,
                additional_Expenses: categories.additional_Expenses,
                notes: categories.notes,

                avatarURL: categories.avatarURL,
                created_on: categories.created_on, 
                sessionId: sessionId,

                permission_type: categories.permission_type,
                selectedOption: categories.specifEmpName,
                locality: categories.locality,
            landMark: categories.land_mark,
            fullAddress: categories.full_address,
            latitude: categories.latitude,
            longitude: categories.longitude,


               
                 loading: false
                });
            console.log(this.state.location);

            this.employeeSListView();
        });

    }

    employeeSListView() {


        var ref = firebase
            .database()
            .ref('Employees/');
        ref.once("value", snapshot => {
            const data = [];
            console.log(snapshot.val());
            snapshot.forEach(element => {
                const usersData = {

                    value: element
                        .val()
                        .email,
                    label: element
                        .val()
                        .first_name + " " + element
                        .val()
                        .last_name + "(" + element
                        .val()
                        .email + ")"
                }
                data.unshift(usersData);
            });

            this.setState({
                EmployeesList: data
            }, () => {
                console.log(this.state.EmployeesList, 'Employees');

            });

        })
    }
    handleChange = (event) => {
        console.log(this.state.validError);
        this.setState({
            [event.target.name]: event.target.value
        });

       

    };

    handleUploadStart = () => {

        this.setState({isUploading: true, uploadProgress: 0});

    }
    handleFrontImageUploadStart = () => this.setState({isUploading: true, uploadProgress: 0, avatarURL: ''});
    handleProgress = progress => this.setState({uploadProgress: progress});

    handleUploadError = error => {
        this.setState({
            isUploading: false
            // Todo: handle error
        });
        console.error(error);
    };

    handleUploadSuccess = async filename => {
        const downloadURL = await firebase
            .storage()
            .ref("images")
            .child(filename)
            .getDownloadURL();

        this.setState(oldState => ({
            filenames: [
                ...oldState.filenames,
                filename
            ],
            downloadURLs: [
                ...oldState.downloadURLs,
                downloadURL
            ],
            uploadProgress: 100,
            isUploading: false
        }));
	};

  handleSelect = selectedOption => {
    this.setState({selectedOption});
    console.log(`Option selected:`, selectedOption);
};
    handleSubmit = (event) => {
        event.preventDefault();
        if (this.validator.allValid() && this.state.validError == true) {
            const {locationId} = this.props.match.params;
            var sessionId = sessionStorage.getItem("RoleId"); 
            let dbCon = firebase
                .database()
                .ref(`/Locations/${locationId}`);
            console.log(dbCon);
            dbCon.update({
                location_Name:this.state.location_Name,
                addressl:this.state.addressl,
                address2:this.state.address2,
                land_Mark:this.state.land_Mark,
                city: this.state.city,
                pincode: this.state.pincode,
                contact_Person_Name:this.state.contact_Person_Name,
                contact_Number: this.state.contact_Number,
                email:this.state.email,
                rental_Deposit_Amount:this.state.rental_Deposit_Amount,
                rent_Per_Month:this.state.rent_Per_Month,
                electricity_Per_Month:this.state.electricity_Per_Month,
                wifi_Per_Month:this.state.wifi_Per_Month,
                additional_Expenses:this.state.additional_Expenses,
                notes:this.state.notes,
                photo: this.state.avatarURL,
                created_on: this.state.created_on, 
                sessionId: sessionId,
                permission_type: this.state.permission_type,
                specifEmpName: this.state.selectedOption,
                locality: this.props.posts.locality,
            landMark: this.props.posts.land_mark,
            fullAddress: this.props.posts.full_address,
            latitude: this.props.posts.latitude,
            longitude: this.props.posts.longitude,
            });

            this
                .props
                .history
                .push("/Locations/Updated");

        } else {
            this
                .validator
                .showMessages();
            this.forceUpdate();
        }
    };

    render() {
        const {EmployeesList, selectedOption} = this.state;

        const divStyle = {
            'height': '100px',
            'resize':'none',
        };
        return (
            <div>
                <SidebarPage/>

                <div className="all-content-wrapper">
                    <HeaderPage/>

                    <div className="breadcome-area">
                        <div className="container-fluid">
                            <div className="row">
                                <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                    <div className="breadcome-list">
                                        <div className="row">
