import React from "react";
import {Link} from "react-router-dom";
import FadeIn from "react-fade-in";
import axios from "axios";
import HeaderPage from "./header";
import FooterPage from "./footer";
// import HomePage from "./home";
import SidebarPage from "./sidebar";
import firebase from '../config';
import moment from 'moment';
import { CSVLink, CSVDownload } from "react-csv";

import swal from 'sweetalert';
import $ from 'jquery';
import SimpleReactValidator from "simple-react-validator";

$.DataTable = require('datatables.net');

class OrderListPage extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            orderList: [],
            loading: false,
            count: 1,
            activePage: "1",
            countPage: "",
            orderStatusList: [],
            validFrom: "",
            validTo: "",
            orderDateList:[],
        };
        this.deleteItem = this
            .deleteItem
            .bind(this);
            this.validator = new SimpleReactValidator({
                className: "text-danger",
                validators: {
    
                    whitespace: {
                        message: "The :attribute not allowed first whitespace   characters.",
                        rule: function (val, params, validator) {
                            // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                            // params.indexOf(val) === -1
                            return (validator.helpers.testRegex(val, /[^\s\\]/) && params.indexOf(val) === -1);
                        }
                    },
                    specialChar: {
                        message: "The :attribute not allowed special   characters.",
                        rule: function (val, params, validator) {
                            // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                            // params.indexOf(val) === -1
                            return (validator.helpers.testRegex(val, /^[ A-Za-z0-9_@./#&+-]*$/i) && params.indexOf(val) === -1);
                        }
                    },
                    specialCharText: {
                        message: "The :attribute may only contain letters, dot and spaces.",
                        rule: function (val, params, validator) {
                            // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                            // params.indexOf(val) === -1
                            return (validator.helpers.testRegex(val, /^[ A-Za-z_@./#&+-]*$/i) && params.indexOf(val) === -1);
                        }
                    }
                }
            });

    }

    componentDidMount() {
        var pathname = this.props.location.pathname;

        var pageName = pathname.substr(8);

        this.setState({loading: true});
        var sessionId = sessionStorage.getItem("RoleId");
        var userRole = sessionStorage.getItem("userRole");
        if (userRole == "Merchant") {
            var ref = firebase
                .database()
                .ref("orders/")
                .orderByChild("merchantId")
                .equalTo(sessionId);
        } else {
            var ref = firebase
                .database()
                .ref("orders/");

        }

        ref.once('value', snapshot => {
            const data = [];
            console.log(snapshot.val());

            snapshot.forEach(childSnapShot => {

                const Merchants = {
