                    trackingNumber = '6245454784';
                    deliveredBy = 'Fedex';
                }

                let user = {
                    "orderStatus": orderStatusValue,
                    "username": username,
                    "to": emailId,
                    "orderId": orderRid,
                    "trackingNumber": trackingNumber,
                    "deliveredBy": deliveredBy
                };

                axios
                    .post(`http://testingmadesimple.org/training_app/api/Service/sendEmail`, user, {headers: headers})
