import React from "react";
import {Link} from "react-router-dom";

import {Form} from 'reactstrap';
import HeaderPage from "./header";
import FooterPage from "./footer";
import SidebarPage from "./sidebar";
import firebase from '../config';
import ReactQuill from "react-quill";
import 'react-quill/dist/quill.snow.css';
import SimpleReactValidator from "simple-react-validator";
import FileUploader from 'react-firebase-file-uploader';

class EditSlider extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            pageTitle: '',
            category: '',
            content: '',
            date: new Date().toLocaleString(),
            sliderUrl: '',
            theme: 'snow',
            uploadImage: '',
            categoryList: '',
            avatar: "",
            isUploading: false,
            progress: 0,
            avatarURL: "",

           
            filenames: [],
            downloadURLs: [],
            uploadProgress: 0,

        }

        this.validator = new SimpleReactValidator({
            className: "text-danger",
            validators: {

                whitespace: {
                    message: "The :attribute not allowed first whitespace   characters.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /[^\s\\]/) && params.indexOf(val) === -1);
                    }
                },
                specialChar: {
                    message: "The :attribute not allowed special   characters.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /^[ A-Za-z0-9_@./#&+-]*$/i) && params.indexOf(val) === -1);
                    }
                },
                website: {
                    message: "The Url should be example.com ",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /(http(s)?:\/\/.)?(www\.)?[-a-zA-Z0-9@:%._\+~#=]{2,256}\.[a-z]{2,6}\b([-a-zA-Z0-9@:%_\+.~#?&//=]*)/g) && params.indexOf(val) === -1);
                    }
                },
                specialCharText: {
                    message: "The :attribute may only contain letters, dot and spaces.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /^[ A-Za-z_@./#&+-]*$/i) && params.indexOf(val) === -1);
                    }
                }
            }
        });

    }
    componentWillMount() {
        this.setState({loading: true});
        var ref = firebase
            .database()
            .ref("category/");

        ref.on('value', snapshot => {
            const data = [];

            snapshot.forEach(childSnapShot => {

                const Categories = {
                    categoryId: childSnapShot
                        .key
                        .toString(),
                    category: childSnapShot
                        .val()
                        .category,

                    created_on: childSnapShot
                        .val()
                        .created_on
                };

                data.push(Categories);
            });

            this.setState({categoryList: data, loading: false});

        });

    }
    componentDidMount() {
        const {CMSID} = this.props.match.params;
        console.log(CMSID);

        var ref = firebase
            .database()
            .ref(`Slider/${CMSID}`);

        ref.on('value', snapshot => {
            var gstData = snapshot.val();
            console.log(gstData)
            this.setState({
                pageTitle: gstData.pageTitle,
                category: gstData.category,
                content: gstData.content,
                avatarURL: gstData.uploadImage,
                sliderUrl: gstData.sliderUrl,
                loading: false
            });
            //console.log(this.state.pageTitle);
        });

    }
    onChange = (event) => {

        this.setState({
            [event.target.name]: event.target.value
        });
    };

    modules = {
        toolbar: [
            [
                {
                    'header': [
                        1,
                        2,
                        3,
                        4,
                        5,
                        6,
                        false
                    ]
                }
            ],
            [
                'bold', 'italic', 'underline', 'strike', 'blockquote'
            ],
            [
                {
                    'list': 'ordered'
                }, {
                    'list': 'bullet'
                }, {
                    'indent': '-1'
                }, {
                    'indent': '+1'
                }
            ],
            [
                'link', 'image'
            ],
            ['clean'],
            [
                {
                    'color': []
                }, {
                    'background': []
                }
            ]
        ]
    };

    formats = [
        'header',
        'bold',
        'italic',
        'underline',
        'strike',
        'blockquote',
        'list',
        'bullet',
        'indent',
        'link',
        'image',
        'color',
        'background'
    ];

    handleUploadStart = () => this.setState({isUploading: true, uploadProgress: 0, avatarURL: ''});
    handleProgress = progress => this.setState({uploadProgress: progress});
        handleUploadError = error => {
        this.setState({isUploading: false});
        console.error(error);
    };
    handleUploadSuccess = filename => {
        this.setState({avatar: filename, progress: 100, isUploading: false});
        firebase
            .database()
            .ref("images")
            .child(filename)
            .getDownloadURL()
            .then(url => this.setState({avatarURL: url}));
        console.log(this.state.avatarURL);
    };
    handleSubmit = (event) => {
        if (this.validator.allValid()) {
            const {CMSID} = this.props.match.params;
            if (this.state.pageTitle && this.state.pageTitle.trim().length !== 0) {
                event.preventDefault();
                var userName = sessionStorage.getItem("userName");
                let dbCon = firebase
                    .database()
                    .ref(`/Slider/${CMSID}`);
                console.log(dbCon);
                dbCon.update({
                    pageTitle: this.state.pageTitle,
                    category: this.state.category,
                    userName: userName,
                    content: this.state.content,
                    uploadImage: this.state.avatarURL,
                    sliderUrl: this.state.sliderUrl,
                    created_on: this.state.date
                });

                this.setState({pageTitle: '', sliderUrl: '', content: ''});
                this
                    .props
                    .history
                    .push("/Slider/Updated");
            }
        } else {
            this
                .validator
                .showMessages();
            this.forceUpdate();
        }
    };

    render() {

        const divStyle = {
            'height': '300px'
        };

        return (
            <div>
                <SidebarPage/>

                <div className="all-content-wrapper">
                    <HeaderPage/>

                    <div className="breadcome-area">
                        <div className="container-fluid">
                            <div className="row">
                                <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                    <div className="breadcome-list">
                                        <div className="row">
                                            <div className="col-lg-6 col-md-6 col-sm-6 col-xs-6">
                                                <div className="breadcomb-wp">

                                                    <div className="breadcomb-ctn">
                                                        <h2>Settings</h2>
                                                    </div>
                                                </div>
                                            </div>

                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

                    <div className="single-product-tab-area mg-b-30">

                        <div className="single-pro-review-area">
                            <div className="container-fluid">
                                <div className="row">
                                    <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                        <div className="review-tab-pro-inner">

                                            <h4>Edit Slider</h4>

                                            <div className="product-tab-list tab-pane fade active in">
                                                <Form onSubmit={this.handleSubmit} id="Edit-GST">
                                                    <div className="row m-b-20">
                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                            <label>Page Title<span style={{
                color: 'red'
            }}>*</span>
                                                            </label>
                                                            <input
                                                                type="text"
                                                                className="form-control"
                                                                placeholder="Page Title*"
                                                                id="add_GST"
                                                                name="pageTitle"
                                                                onChange={this.onChange}
                                                                value={this.state.pageTitle}/> {this
                                                                .validator
                                                                .message("Page Title", this.state.pageTitle, "required|min:3|max:500")}
                                                            <div className="text-danger">
                                                                {" "}
                                                                {this.state.employer_sevice_message}
                                                            </div>
                                                        </div>

                                                    </div>
                                                    <div className="row m-b-20">
                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                            <label>Category Name<span style={{
                color: 'red'
            }}>*</span>
                                                            </label>
                                                            <select
                                                                name="select"
                                                                className="form-control pro-edt-select form-control-primary"
                                                                name="category"
                                                                onChange={this.onChange}>
                                                                <option>Select Category</option>
                                                                {this.state.categoryList && this
                                                                    .state
                                                                    .categoryList
                                                                    .map((data, index) => {

                                                                        return (
                                                                            <option value={data.category} key={index}>{data.category}</option>
                                                                        )

                                                                    })}

                                                            </select>
                                                            {this
                                                                .validator
                                                                .message("Select category", this.state.category, "required")}

                                                        </div>

                                                    </div>
                                                    <div className="row m-b-20">
                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                            <label>Slider Url<span style={{
                color: 'red'
            }}>*</span>
                                                            </label>
                                                            <input
                                                                type="text"
                                                                className="form-control"
                                                                placeholder="Slider Url*"
                                                                name="sliderUrl"
                                                                onChange={this.onChange}
                                                                value={this.state.sliderUrl}/> {this
                                                                .validator
                                                                .message("Slider Url", this.state.sliderUrl, "required|website")}

                                                        </div>

                                                    </div>

                                                    <div className="row m-b-20">
                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                            <div className="dropzone-pro">
                                                                <div id="dropzone">

                                                                    <div className="dz-message needsclick download-custom">
                                                                        <i className="fa fa-cloud-download" aria-hidden="true"></i>
                                                                        <h2>Upload Slider Image<span style={{
                color: 'red'
            }}>*</span>
                                                                        </h2>

                                                                    </div>

                                                                    {this.state.isUploading && <p>Progress: {this.state.progress}</p>}
                                                                    {this.state.avatarURL && <img
                                                                        src={this.state.avatarURL}
                                                                        style={{
                                                                        height: "200px",
                                                                        width: "250px"
                                                                    }}/>}
                                                                    <FileUploader
                                                                        accept="image/*"
                                                                        name="avatar"
                                                                        randomizeFilename
                                                                        storageRef={firebase
                                                                        .storage()
                                                                        .ref("images")}
                                                                        onUploadStart={this.handleUploadStart}
                                                                        onUploadError={this.handleUploadError}
                                                                        onUploadSuccess={this.handleUploadSuccess}
                                                                        onProgress={this.handleProgress}/> {this
                                                                        .validator
                                                                        .message("Upload Product Image", this.state.avatarURL, "required")}

                                                                </div>
                                                                
                                                            </div>

                                                        </div>
                                                    </div>
                                                    <div className="row m-b-20">
                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                            <label>Description<span style={{
                color: 'red'
            }}>*</span>
                                                            </label>
                                                            <ReactQuill
                                                                theme={this.state.theme}
                                                                value={this.state.content}
                                                                placeholder='Enter Description'
                                                                modules={this.modules}
                                                                formats={this.formats}
                                                                onChange={value => this.setState({content: value})}
                                                                className="add-new-post__editor mb-1"/> {this
                                                                .validator
                                                                .message("Page Content", this.state.content, "required|min:150")}

                                                        </div>

                                                    </div>

                                                    <div className="row m-b-20"></div>
                                                    <div className="row m-b-20"></div>
                                                    <div className="row m-b-20">

                                                        <div className="text-danger">
                                                            {" "}
                                                            {this.state.employer_sevice_message}
                                                        </div>

                                                    </div>
                                                    <div className="row m-b-20">
                                                        <div className="col-lg-6 col-md-6 col-sm-12 col-xs-12">
                                                            <div className="text-left custom-pro-edt-ds">

                                                                <button type="submit" className="btn btn-ctl-bt waves-effect waves-light">Add</button>
                                                                <Link to="/Slider">
                                                                    <button type="button" className="btn btn-ctl-bt waves-effect waves-light">Cancel
                                                                    </button>
                                                                </Link>
                                                            </div>
                                                        </div>
                                                    </div>
                                                </Form>
                                            </div>

                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
                <FooterPage/>

            </div>
        );
    }
}
export default EditSlider;
