import React from "react";
import {Link} from "react-router-dom";
import {Form} from 'reactstrap';
import HeaderPage from "./header";
import FooterPage from "./footer";
import SidebarPage from "./sidebar";
import firebase from '../config';
import SimpleReactValidator from "simple-react-validator";
//import FileUploader from "react-firebase-file-uploader";

class EditFile extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            file: '',
            name:'',
            error_message: '',
            created_on: new Date().toLocaleString(),
            validError: true,
            uploadImage: '',
            avatar: "",
            isUploading: false,
            progress: 0,
            avatarURL: "",
        }

        this.validator = new SimpleReactValidator({
            className: "text-danger",
            validators: {

                whitespace: {
                    message: "The :attribute not allowed first whitespace   characters.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /[^\s\\]/) && params.indexOf(val) === -1);
                    }
                },
                specialChar: {
                    message: "The :attribute not allowed special   characters.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /^[ A-Za-z0-9_@./#&+-]*$/i) && params.indexOf(val) === -1);
                    }
                },
                specialCharText: {
                    message: "The :attribute may only contain letters, dot and spaces.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /^[ A-Za-z_@./#&+-]*$/i) && params.indexOf(val) === -1);
                    }
                }
            }
        });

    }
    componentDidMount() {
        const {fileId} = this.props.match.params;
        console.log(fileId);

        var ref = firebase
            .database()
            .ref(`Files/${fileID}`);

        ref.on('value', snapshot => {
            // var categories = snapshot.val();
            
            // console.log(categories)

            this.setState({file: snapshot.val().file, 
                name: snapshot.val().name,
                created_on: snapshot.val().created_on,
                loading: false});
            console.log(this.state.Files);
        });

    }
    // handleUploadStart = () => this.setState({isUploading: true, progress: 0});
    // handleProgress = progress => this.setState({progress});
    // handleUploadError = error => {
    //     this.setState({isUploading: false});
    //     console.error(error);
    // };
    // handleUploadSuccess = filename => {
    //     this.setState({avatar: filename, progress: 100, isUploading: false});
    //     firebase
    //         .storage()
    //         .ref("images")
    //         .child(filename)
    //         .getDownloadURL()
    //         .then(url => this.setState({avatarURL: url}));
    //     console.log(this.state.avatarURL);
    // };
    onChange = (event) => {
        this.setState({
            [event.target.name]: event.target.value
        });

        // var ref = firebase
        //     .database()
        //     .ref('category/')
        //     .orderByChild("category")
        //     .equalTo(event.target.value);
        // ref.on('value', snapshot => {
        //     var user_exist = snapshot.numChildren();
        //     console.log(user_exist);

        //     if (user_exist > 0) {

        //         this.setState({error_message: "Category Name is already exist", validError: false});
        //     } else {
        //         this.setState({error_message: "", validError: true});

        //     }

        // })

    };

    handleSubmit = (event) => {
        event.preventDefault();
        if (this.validator.allValid() && this.state.validError == true) {
            const {fileID} = this.props.match.params;
            // var userName = sessionStorage.getItem("userName");
            if (this.state.Files && this.state.Files.trim().length !== 0) {
                event.preventDefault();
                let dbCon = firebase
                    .database()
                    .ref(`/Files/${fileID}`);
                console.log(dbCon);
                dbCon.update({file: this.state.file,name: this.state.name,  created_on: this.state.created_on});

                this
                    .props
                    .history
                    .push("/FileList/Updated");
            }
        } else {
            this
                .validator
                .showMessages();
            this.forceUpdate();
        }
    };

    render() {

        return (
            <div>
                <SidebarPage/>

                <div className="all-content-wrapper">
                    <HeaderPage/>

                    <div className="breadcome-area">
                        <div className="container-fluid">
                            <div className="row">
                                <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                    <div className="breadcome-list">
                                        <div className="row">
                                            <div className="col-lg-6 col-md-6 col-sm-6 col-xs-6">
                                                <div className="breadcomb-wp">

                                                    <div className="breadcomb-ctn">
                                                        <h2>Categories</h2>
                                                    </div>
                                                </div>
                                            </div>

                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

                    <div className="single-product-tab-area mg-b-30">

                        <div className="single-pro-review-area">
                            <div className="container-fluid">
                                <div className="row">
                                    <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                        <div className="review-tab-pro-inner">

                                            <h4>Edit Category</h4>

                                            <div className="product-tab-list tab-pane fade active in">
                                                <Form onSubmit={this.handleSubmit} id="Edit-category">

                                                <div className="row m-b-20">
