import React from "react";
import HeaderPage from "./header";
import FooterPage from "./footer";
import SidebarPage from "./sidebar";
import firebase from '../config';
import {Form} from 'reactstrap';
import FileUploader from "react-firebase-file-uploader";
import ReactNotification from "react-notifications-component";
import "react-notifications-component/dist/theme.css";
import SimpleReactValidator from "simple-react-validator";
import ReactQuill from "react-quill";
import 'react-quill/dist/quill.snow.css';
import {Link} from "react-router-dom";

class ViewMerchantDetails extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            businessName: '',
            gstValue: '',
            tanValue: '',
            signature: '',
            mailingAddress: '',
            pickupAddress: '',
            pincode: '',
            city: '',
            state: '',
            emailAddress: '',

            accountName: '',
            accountNumber: '',
            ifscCode: '',
            bankName: '',
            branchName: '',
            cityName: '',
            stateName: '',

            businessType: '',
            personalPAN: '',
            addressProof: '',
            cancelledCheque: '',
            displayName: '',
            businessDesc: '',
            productType: '',
            holidays: '',
            productNames: '',
            place: '',

            uploadImage: '',
            avatar: "",
            isUploading: false,
            progress: 0,
            avatarURL: "",
            filenames: [],
            downloadURLs: "",
            uploadProgress: 0,
            countNumber:0,
           

            date: new Date().toLocaleString()
        }

        this.validator = new SimpleReactValidator({
            className: "text-danger",
            validators: {
                passwordvalid: {
                    message: "The :attribute must be at least 6 and at most 30 with 1 numeric,1 special charac" +
                            "ter and 1 alphabet.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /^(?=.*\d)(?=.*[!@#$%^&*])(?=.*[a-z]).{6,30}$/i) && params.indexOf(val) === -1);
                    }
                },
                passwordMismatch: {
                    message: "confirm password must match with password field.",
                    rule: function (val, params, validator) {
                        return document
                            .getElementById("password_input")
                            .value === val
                            ? true
                            : false;
                    }
                },
                whitespace: {
                    message: "The :attribute not allowed first whitespace   characters.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /[^\s\\]/) && params.indexOf(val) === -1);
                    }
                },
                specialChar: {
                    message: "The :attribute not allowed special   characters.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /^[ A-Za-z0-9_@./#&+-]*$/i) && params.indexOf(val) === -1);
                    }
                },
                specialCharText: {
                    message: "The :attribute may only contain letters, dot and spaces.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /^[ A-Za-z_@./#&+-]*$/i) && params.indexOf(val) === -1);
                    }
                },

                zip: {
                    message: "Invalid Pin Code",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /^(\d{5}(\d{4})?)?$/i) && params.indexOf(val) === -1);
                    }
                },
                website: {
                    message: "The Url should be example.com ",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /(http(s)?:\/\/.)?(www\.)?[-a-zA-Z0-9@:%._\+~#=]{2,256}\.[a-z]{2,6}\b([-a-zA-Z0-9@:%_\+.~#?&//=]*)/g) && params.indexOf(val) === -1);
                    }
                },
                Fax: {
                    message: "Invalid fax number ",
                    rule: function (val, params, validator) {
                        return (validator.helpers.testRegex(val, /^([\w-\.]+)@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.)|(([\w-]+\.)+))([a-zA-Z]{2,4}|[0-9]{1,3})(\]?)$/i) && params.indexOf(val) === -1);
                    }
                }
            }
        });
    }
    componentDidMount() {
        
        const {CMSID} = this.props.match.params;
      
        this.setState({ loading: true });
        var ref = firebase.database().ref(`MerchantAccountDetails/${CMSID}`);
       
        
    
        ref.on('value', snapshot => {
            if(snapshot.numChildren()>0){

           
            var accountDetails = snapshot.val();
            console.log(accountDetails);

          
      
            this.setState({  
                businessName: accountDetails.businessName ,
                gstValue: accountDetails.gstValue,
                tanValue: accountDetails.tanValue,
                signature: accountDetails.avatarURL ,
                mailingAddress: accountDetails.mailingAddress,
                pickupAddress: accountDetails.pickupAddress,
                pincode: accountDetails.pincode,
                city: accountDetails.city,
                state: accountDetails.state,
                emailAddress: accountDetails.emailAddress,
    
                accountName: accountDetails.accountName,
                accountNumber: accountDetails.accountNumber,
                ifscCode: accountDetails.ifscCode,
                bankName: accountDetails.bankName,
                branchName: accountDetails.branchName,
                cityName: accountDetails.cityName,
                stateName: accountDetails.stateName,
    
                businessType: accountDetails.businessType,
                personalPAN: accountDetails.personalPAN,
                addressProof: accountDetails.addressProof,
                cancelledCheque: accountDetails.cancelledCheque,
                displayName: accountDetails.displayName,
                businessDesc: accountDetails.businessDesc,
                productType: accountDetails.productType,
                holidays: accountDetails.holidays,
                productNames: accountDetails.productNames,
                place: accountDetails.place,
                avatarURL: accountDetails.avatarURL,
                loading: false ,
                countNumber:accountDetails});
            }
            else{
                
            }
            
          });
          

          

        }
    onChange = (event) => {

        this.setState({
            [event.target.name]: event.target.value
        });

        console.log(event.target.name +"="+ event.target.value);
       
    };
    
    handleChange = (value) => {
        console.log(value);
        this.setState({description: value})
    };

    handleUploadStart = () => this.setState({isUploading: true, uploadProgress: 0});
    handleFrontImageUploadStart = () => this.setState({isUploading: true, uploadProgress: 0, avatarURL: ''});
    handleProgress = progress => this.setState({uploadProgress: progress});

    handleUploadError = error => {
        this.setState({
            isUploading: false
            // Todo: handle error
        });
        console.error(error);
    };
    handleUploadSuccess = filename => {
        this.setState({ avatar: filename, progress: 100, isUploading: false });
        firebase
          .storage()
          .ref("images")
          .child(filename)
          .getDownloadURL()
          .then(url => this.setState({ avatarURL: url }));
      };
    handlePanSuccess = (filename) => {

        firebase
            .storage()
            .ref("images")
            .child(filename)
            .getDownloadURL()
            .then(url => this.setState({personalPAN: url}));
    };
    handleAddressProofSuccess = (filename) => {

        firebase
