            var ref = firebase
            .database()
            .ref(`MerchantAccountDetails/${sessionId}`);
           console.log(this.state.businessName);
            ref.update({
           
                businessName: this.state.businessName ,
                gstValue: this.state.gstValue,
                tanValue: this.state.tanValue,
                signature: this.state.avatarURL ,
                mailingAddress: this.state.mailingAddress,
                pickupAddress: this.state.pickupAddress,
                pincode: this.state.pincode,
                city: this.state.city,
                state: this.state.state,
                emailAddress: this.state.emailAddress,
    
                accountName: this.state.accountName,
                accountNumber: this.state.accountNumber,
                ifscCode: this.state.ifscCode,
                bankName: this.state.bankName,
                branchName: this.state.branchName,
                cityName: this.state.cityName,
                stateName: this.state.stateName,
    
                businessType: this.state.businessType,
                personalPAN: this.state.personalPAN,
                addressProof: this.state.addressProof,
                cancelledCheque: this.state.cancelledCheque,
                displayName: this.state.displayName,
                businessDesc: this.state.businessDesc,
                productType: this.state.productType,
                holidays: this.state.holidays,
                productNames: this.state.productNames,
                place: this.state.place,
                avatarURL: this.state.avatarURL,
                created_on: this.state.date,
                sessionId: sessionId
            });
            this
                .props
                .history
                .push("/Dashboard");
        } else {
            this
                .validator
                .showMessages();
            this.forceUpdate();
        }

