import React from "react";
import HeaderPage from "./header";
import FooterPage from "./footer";
import SidebarPage from "./sidebar";
import firebase from '../config';
import {Form} from 'reactstrap';
import FileUploader from "react-firebase-file-uploader";
import SimpleReactValidator from "simple-react-validator";
import ReactQuill from "react-quill";
import 'react-quill/dist/quill.snow.css';
import {Link} from "react-router-dom";
import Select from 'react-select';

class AddFiles extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            file: '',
            name: '',
            description: '',
            permission_type: '',
            specifEmpName: '',
            FileList: [],
            isUploading: false,
            error_message: '',
            created_on: new Date().toLocaleString(),
            validError: true,
            EmployeesList: [],
            filter: '',
            selectedOption: null,
            avatar: "",
            isUploading: false,
            progress: 0,
            avatarURL: ""
        }

        this.validator = new SimpleReactValidator({
            className: "text-danger",
            validators: {
                passwordvalid: {
                    message: "The :attribute must be at least 6 and at most 30 with 1 numeric,1 special charac" +
                            "ter and 1 alphabet.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /^(?=.*\d)(?=.*[!@#$%^&*])(?=.*[a-z]).{6,30}$/i) && params.indexOf(val) === -1);
                    }
                },
                passwordMismatch: {
                    message: "confirm password must match with password field.",
                    rule: function (val, params, validator) {
                        return document
                            .getElementById("password_input")
                            .value === val
                            ? true
                            : false;
                    }
                },
                AlertquantityStock: {
                    message: "alert quantity should be less than quantity Instock.",
                    rule: function (val, params, validator) {
                        var qty = document
                            .getElementById('quantity_stock')
                            .value;

                        if (Number(qty) > Number(val)) {

                            return true;
                        } else {
                            return false;
                        }

                    }
                },
                priceCostValid: {
                    message: "Selling price should be less than original price.",
                    rule: function (val, params, validator) {
                        var qty = document
                            .getElementById('price_cost')
                            .value;

                        if (Number(qty) > Number(val)) {

                            return true;
                        } else {
                            return false;
                        }

                    }
                },
                whitespace: {
                    message: "The :attribute not allowed first whitespace   characters.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /[^\s\\]/) && params.indexOf(val) === -1);
                    }
                },
                specialChar: {
                    message: "The :attribute not allowed special   characters.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /^[ A-Za-z0-9_@./#&+-]*$/i) && params.indexOf(val) === -1);
                    }
                },
                specialCharText: {
                    message: "The :attribute may only contain letters, dot and spaces.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /^[ A-Za-z_@./#&+-]*$/i) && params.indexOf(val) === -1);
                    }
                },

                zip: {
                    message: "Invalid Pin Code",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /^(\d{5}(\d{4})?)?$/i) && params.indexOf(val) === -1);
                    }
                },
                website: {
                    message: "The Url should be example.com ",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /(http(s)?:\/\/.)?(www\.)?[-a-zA-Z0-9@:%._\+~#=]{2,256}\.[a-z]{2,6}\b([-a-zA-Z0-9@:%_\+.~#?&//=]*)/g) && params.indexOf(val) === -1);
                    }
                },
                Fax: {
                    message: "Invalid fax number ",
                    rule: function (val, params, validator) {
                        return (validator.helpers.testRegex(val, /^([\w-\.]+)@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.)|(([\w-]+\.)+))([a-zA-Z]{2,4}|[0-9]{1,3})(\]?)$/i) && params.indexOf(val) === -1);
                    }
                }
            }
        });
    } modules = {
        toolbar: [
            [
                {
                    'header': [
                        1,
                        2,
                        3,
                        4,
                        5,
                        6,
                        false
                    ]
                }
            ],
            [
                'bold', 'italic', 'underline', 'strike', 'blockquote'
            ],
            [
                {
                    'list': 'ordered'
                }, {
                    'list': 'bullet'
                }, {
                    'indent': '-1'
                }, {
                    'indent': '+1'
                }
            ],
            [
                'link', 'image'
            ],
            ['clean'],
            [
                {
                    'color': []
                }, {
                    'background': []
                }
            ]
        ]
    };

    formats = [
        'header',
        'bold',
        'italic',
        'underline',
        'strike',
        'blockquote',
        'list',
        'bullet',
        'indent',
        'link',
        'image',
        'color',
        'background'
    ];

    componentDidMount() {
        var ref = firebase
            .database()
            .ref('Employees/');
        ref.once("value", snapshot => {
            const data = [];
            console.log(snapshot.val());
            snapshot.forEach(element => {
                const usersData = {

                    value: element
                        .val()
                        .email,
                    label: element
                        .val()
                        .first_name + " " + element
                        .val()
                        .last_name + "(" + element
                        .val()
                        .email + ")"
                }
                data.unshift(usersData);
            });

            this.setState({
                EmployeesList: data
            }, () => {
                console.log(this.state.EmployeesList, 'Employees');

            });

        })
    }

    handleUploadStart = () => this.setState({ isUploading: true, progress: 0 });
  handleProgress = progress => this.setState({ progress });
  handleUploadError = error => {
    this.setState({ isUploading: false });
    console.error(error);
  };
  handleUploadSuccess = filename => {
    this.setState({ avatar: filename, progress: 100, isUploading: false });
    firebase
      .storage()
      .ref("uploadFiles")
      .child(filename)
      .getDownloadURL()
      .then(url => this.setState({ avatarURL: url }));

      //console.log(this.state.avatarURL);
  };

    handleSelect = selectedOption => {
        this.setState({selectedOption});
        console.log(`Option selected:`, selectedOption);
    };
    handleSubmit = (event) => {
        event.preventDefault();
        if (this.validator.allValid() && this.state.validError == true) {
            console.log(this.state.avatar);
            // var sessionId = sessionStorage.getItem("RoleId"); var userName =
            // sessionStorage.getItem("userName");

            let dbCon = firebase
                .database()
                .ref('/Files');
            dbCon.push({

                file: this.state.avatarURL,
                name: this.state.name,
                description: this.state.description,
                permission_type: this.state.permission_type,
                specifEmpName: this.state.selectedOption&&this.state.selectedOption.value,
                created_on: this.state.created_on
            });
            this
                .props
                .history
                .push("/FileList/Added");
        } else {
            this
                .validator
                .showMessages();
            this.forceUpdate();
        }

    };
    handleChange = (event) => {
        this.setState({
            [event.target.name]: event.target.value
        });
    };

    handleDescription=(value) => {
        this.setState({description: value})
    };

    render() {
        const {EmployeesList, selectedOption} = this.state;

        const divStyle = {
            'height': '300px'
        };
      
        return (
            <div>
                <SidebarPage/>

                <div className="all-content-wrapper">
                    <HeaderPage/>

                    <div className="breadcome-area">
                        <div className="container-fluid">
                            <div className="row">
                                <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                    <div className="breadcome-list">
                                        <div className="row">
                                            <div className="col-lg-6 col-md-6 col-sm-6 col-xs-6">
                                                <div className="breadcomb-wp">

                                                    <div className="breadcomb-ctn">
                                                        <h2>Files</h2>
                                                    </div>
                                                </div>
                                            </div>

                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

                    <div className="single-product-tab-area mg-b-30">

                        <div className="single-pro-review-area">
                            <div className="container-fluid">
                                <div className="row">
                                    <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                        <div className="review-tab-pro-inner">

                                            <h4>Add Files</h4>
                                            <div className="product-tab-list tab-pane fade active in">
                                                <Form onSubmit={this.handleSubmit} id="Add-products">

                                                    <div className="row m-b-20">
                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                            <label>Name<span style={{
                color: 'red'
            }}>*</span>
                                                            </label>
                                                            <input
                                                                type="text"
                                                                className="form-control"
                                                                placeholder="Enter file Name"
                                                                name="name"
                                                                onChange={this.handleChange}
                                                                value={this.state.name}/> {this
                                                                .validator
                                                                .message("Name", this.state.name, "required|specialCharText|whitespace|min:2|max:70")}
                                                            <div className="text-danger">
                                                                {" "}
                                                                {this.state.error_message}
                                                            </div>
                                                        </div>
                                                    </div>
                                                    <div className="row m-b-20">

                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                            <label>Description<span style={{
                color: 'red'
            }}>*</span>
                                                            </label>

                                                            <ReactQuill
                                                                theme={this.state.theme}
                                                                value={this.state.description}
                                                                modules={this.modules}
                                                                formats={this.formats}
                                                                placeholder='Enter Text'
                                                                onChange={this.handleDescription}
                                                                className="add-new-post__editor mb-1"
                                                                style={divStyle}/>
                                                            {this
                                                                .validator
                                                                .message("Description", this.state.description, "required|specialCharText|whitespace|min:2|max:200")}
                                                            <div className="text-danger">
                                                                {" "}
                                                                {this.state.error_message}
                                                            </div>
                                                        </div>
                                                    </div>

                                                    <div className="row m-b-20">
                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                            <div className="form-group">
                                                                <label className="col-lg-3 control-label">Permissions<span style={{
                color: 'red'
            }}>*</span>
                                                                </label>
                                                                <div className="col-sm-8">
                                                                    <div className="row">
                                                                        <div className="col-sm-3">
                                                                            <label className="radio-inline no-padd">
                                                                                <input
                                                                                    type="radio"
                                                                                    name="permission_type"
                                                                                    value="None"
                                                                                    onChange={this.handleChange}
                                                                                    checked={this.state.permission_type === 'None'}/>
                                                                                None
                                                                            </label>
                                                                        </div>
                                                                        <div className="col-sm-3">
                                                                            <label className="radio-inline no-padd">

                                                                                <input
                                                                                    type="radio"
                                                                                    name="permission_type"
                                                                                    value="All"
                                                                                    onChange={this.handleChange}
                                                                                    checked={this.state.permission_type === 'All'}/>
                                                                                All
                                                                            </label>
                                                                        </div>

                                                                        <div className="col-sm-3">
                                                                            <label className="radio-inline no-padd">
                                                                                <input
                                                                                    type="radio"
                                                                                    name="permission_type"
                                                                                    value="specific_empl"
                                                                                    onChange={this.handleChange}
                                                                                    checked={this.state.permission_type === 'specific_empl'}/>
                                                                                Specific Employees
                                                                            </label>
                                                                        </div>

                                                                    </div>
                                                                    {this
                                                                        .validator
                                                                        .message("permission", this.state.permission_type, "required")}
                                                                </div>
                                                            </div>

                                                        </div>

                                                    </div>
                                                    {this.state.permission_type === 'specific_empl'
                                                        ? <div className="row m-b-20">
                                                                <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                                    <div className="form-group">

                                                                        <Select
                                                                            value={selectedOption}
                                                                            onChange={this.handleSelect}
                                                                            options={EmployeesList}/>

                                                                    </div>

                                                                </div>
                                                            </div>
                                                        : ''}

                                                    <div className="row m-b-20">
                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                            <div className="dropzone-pro">
                                                                <div id="dropzone">

                                                                    <div className="dz-message needsclick download-custom">
                                                                        <i className="fa fa-cloud-download" aria-hidden="true"></i>
                                                                        <h2>Upload File<span style={{
                color: 'red'
            }}>*</span>
                                                                        </h2>

                                                                    </div>

                                                                    {this.state.isUploading && <p>Progress: {this.state.progress}</p>}
                                                                    {this.state.avatarURL && <img
                                                                        src={this.state.avatarURL}
                                                                        style={{
                                                                        height: "200px",
                                                                        width: "250px"
                                                                    }}/>}
                                                                    <FileUploader
                                                                        accept={['image/png', '.pdf', 'audio/*', '.doc']}
                                                                        name="avatar"
                                                                        randomizeFilename
                                                                        storageRef={firebase
                                                                        .storage()
                                                                        .ref("uploadFiles")}
                                                                        onUploadStart={this.handleUploadStart}
                                                                        onUploadError={this.handleUploadError}
                                                                        onUploadSuccess={this.handleUploadSuccess}
                                                                        onProgress={this.handleProgress}/> {this
                                                                        .validator
                                                                        .message("Upload  Files", this.state.avatarURL, "required")}

                                                                </div>
                                                            </div>

                                                        </div>

                                                    </div>

                                                    <div className="row">
                                                        <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                                            <div className="text-center custom-pro-edt-ds">
                                                                <button
                                                                    type="submit"
                                                                    className="btn btn-ctl-bt waves-effect waves-light m-r-10">Save
                                                                </button>
                                                                <Link to="/EmployeeList">
                                                                    <button type="button" className="btn btn-ctl-bt waves-effect waves-light">Cancel
                                                                    </button>
                                                                </Link>
                                                            </div>
                                                        </div>
                                                    </div>
                                                </Form>
                                            </div>

                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
                <FooterPage/>

            </div>
        );
    }
}

export default AddFiles;
