import React from "react";
import {Link} from "react-router-dom";
import HeaderPage from "./header";
import FooterPage from "./footer";
import SidebarPage from "./sidebar";
import firebase from '../config';
import swal from 'sweetalert';
import $ from 'jquery';
$.DataTable = require('datatables.net');

class SalesTracker extends React.Component {
    constructor(props) {
        super(props);
        this.datatable = null;
        this.state = {
            SalesTrackerList: [],
            loading: false,
            count: 1,
            activePage: "1",
            countPage: "",
            usePermList: "",
            created_on: new Date().toLocaleString(),
        };

    }
   
    componentDidMount() {
        this.setState({loading: true});
        var sessionId = sessionStorage.getItem("RoleId");
        var userName = sessionStorage.getItem("email");
        var ref = firebase
            .database()
            .ref("SalesTracker/");

        ref.on('value', snapshot => {
            const data = [];
          
            snapshot.forEach(childSnapShot => {

                const Categories = {
                    salestrackerId: childSnapShot.key.toString(),
                        prospect_Name: childSnapShot.val().prospect_Name,
                        company_Name: childSnapShot.val().company_Name,
                        city: childSnapShot.val().city,
                        contact_Number: childSnapShot.val().contact_Number,
                        email: childSnapShot.val().email,
                        source_Of_Enquiry: childSnapShot.val().source_Of_Enquiry,
                        contacted: childSnapShot.val().contacted,
                        email_Sent: childSnapShot.val().email_Sent,
                        follow_Up_Date: childSnapShot.val().follow_Up_Date,
                        follow_Up_Time: childSnapShot.val().follow_Up_Time,
                        remarks: childSnapShot.val().remarks,
                         created_on: childSnapShot.val().created_on, 
                        userName:userName,
                        sessionId: sessionId,
                   
                    
                };

                data.unshift(Categories);
            });

            this.setState({SalesTrackerList: data, countPage: data.length, loading: false});
            console.log(this.state.SalesTrackerList);
            // this.$el = $(this.el);

            this.interval = setTimeout(() => $('.paginationTable').DataTable(), 1000);
        });

    }
    componentWillUnmount() {
        //this.dataTable.destroy(true);
        clearTimeout(this.interval);
    }
    deleteItem = id => {

        swal({title: "Are you sure?", text: "Do your really want to remove?", icon: "warning", buttons: true, dangerMode: true}).then(willDelete => {
            if (willDelete) {
                console.log(id);
                var playersRef = firebase
                    .database()
                    .ref(`/SalesTracker/${id}`);
                playersRef.remove();

                this
                    .props
                    .history
                    .push("/SalesTracker/Deleted");
            } else {}
            // window.location.reload("/Home");

        });
        this.componentDidMount();

    };

    render() {

        var pathname = this
            .props
            .location
            .pathname
            .split("/");
        var alertName = pathname[2];
        return (
            <div>
                <SidebarPage/>

                <div className="all-content-wrapper">
                    <HeaderPage/>

                    <div className="breadcome-area">
                        <div className="container-fluid">
                            <div className="row">
                                <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                    <div className="breadcome-list">
                                        <div className="row">
                                            <div className="col-lg-6 col-md-6 col-sm-6 col-xs-6">
                                                <div className="breadcomb-wp">

                                                    <div className="breadcomb-ctn">
                                                        <h2>Sales Tracker</h2>

                                                    </div>

                                                </div>
                                            </div>

                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

                    <div className="product-status mg-b-30">
                        <div className="container-fluid">
                            <div className="row">
                                <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                    <div className="product-status-wrap">
                                        <h4>Sales Tracker List</h4>

                                        <div className="add-product">
                                                    <Link to='/AddLocations'>Add Sales Tracker </Link>
                                                </div>
                                          
                                        {alertName
                                            ? <div
                                                    className="alert alert-success display-none"
                                                    style={{
                                                    display: "block"
                                                }}>
                                                    <a data-dismiss="alert" href="#" aria-hidden="true" className="close">Ã—</a>
                                                    Sales Tracker  {alertName} &nbsp;
                                                    Successfully
                                                </div>
                                            : ''}
                                        <table className="table table-bordered paginationTable">
                                            <thead>
                                                <tr>
                                                    <th>S.No</th>
                                                    <th>Prospect Name</th>
                                                    <th>Company Name</th>
                                                     <th>City</th>
                                                    <th>Added By</th>
                                                    <th>Added On</th>
                                                     <th>Action</th>
                                                       

                                                </tr>
                                            </thead>
                                            <tbody>

                                                {this
                                                    .state
                                                    .locationList && this
                                                    .state
                                                    .locationList
                                                    .map((data, index) => {
                                                        console.log(data);

                                                        return (
                                                            <tr key={index}>
                                                                <td>{index + 1}</td>
                                                                <td>{data.prospect_Name} </td>
                                                                <td>{data.company_Name}</td>
                                                                <td>{data.city}</td>
                                                                <td>{data.userName}</td>
                                                                <td>{data.created_on} </td>
                                                               
                                                                <td>

                                                                            <Link to={`/EditSalesTracker/${data.salestrackerId}`}>
                                                                                <button data-toggle="tooltip" title="Edit" className="pd-setting-ed">
                                                                                    <i className="fa fa-pencil-square-o" aria-hidden="true"></i>
                                                                                </button>
                                                                            </Link>

                                                                            <button
                                                                                data-toggle="tooltip"
                                                                                title="Trash"
                                                                                className="pd-setting-ed"
                                                                                data-id={data.salestrackerId}
                                                                                onClick={this
                                                                                .deleteItem
                                                                                .bind(this, data.salestrackerId)}>
                                                                                <i className="fa fa-trash-o" aria-hidden="true"></i>
                                                                            </button>
                                                                        </td>
                                                                   

                                                            </tr>
                                                        )

                                                    })}

                                            </tbody>
                                        </table>

                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

                </div>

                <FooterPage/>

            </div>
        );
    }
}

export default SalesTracker;
