import React from "react";
import {Link} from "react-router-dom";

import {Form, Input, Button, Alert} from 'reactstrap';
import HeaderPage from "./header";
import FooterPage from "./footer";
// import HomePage from "./home";
import SidebarPage from "./sidebar";
import firebase from '../config';
import ReactNotification from "react-notifications-component";
import "react-notifications-component/dist/theme.css";
import SimpleReactValidator from "simple-react-validator";

const userRoles = [
    "dashboard",
    "orders",
    "products",
    "category",
    "subCategory",
    "invoices",
    "abandonedCart",
    "coupons",
    "merchantPayments",
    "AdminStaff",
    "merchantMangement",
    "notifications",
    "users",
    "settings",
    "Analytics",
    "sliders",
    "enquiry"
]

class AddRolePage extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            Role: '',
            dashboard: '',
            orders: '',
            products: '',
            category: '',
            subCategory: '',
            invoices: '',
            abandonedCart: '',
            coupons: '',
            merchantPayments: '',
            AdminStaff: '',
            merchantMangement: '',
            notifications: '',
            users: '',
            settings: '',
            Analytics: '',
            sliders: '',
            enquiry: '',

            date: new Date().toLocaleString()
        }

        this.notificationDOMRef = React.createRef();
        this.validator = new SimpleReactValidator({
            className: "text-danger",
            validators: {

                whitespace: {
                    message: "The :attribute not allowed first whitespace   characters.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /[^\s\\]/) && params.indexOf(val) === -1);
                    }
                },
                specialChar: {
                    message: "The :attribute not allowed special   characters.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /^[ A-Za-z0-9_@./#&+-]*$/i) && params.indexOf(val) === -1);
                    }
                },
                specialCharText: {
                    message: "The :attribute may only contain letters, dot and spaces.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /^[ A-Za-z_@./#&+-]*$/i) && params.indexOf(val) === -1);
                    }
                }
            }
        });

    }
    // componentDidMount(){   this.addNotification(); }
    onChange = (event) => {
console.log(event.target.name+" "+event.target.value);
        this.setState({
            [event.target.name]: event.target.value
        });
    };

    handleSubmit = (event) => {
        event.preventDefault();
        if (this.validator.allValid()) {
            if (this.state.Role && this.state.Role.trim().length !== 0) {
                this.addNotification();

                event.preventDefault();
                let dbCon = firebase
                    .database()
                    .ref('/Role');
                dbCon
                    .push({
                    Role: this.state.Role,
                    dashboard: this.state.dashboard,
                    orders: this.state.orders,
                    products: this.state.products,
                    category: this.state.category,
                    subCategory: this.state.subCategory,
                    invoices: this.state.invoices,
                    abandonedCart: this.state.abandonedCart,
                    coupons: this.state.coupons,
                    merchantPayments: this.state.merchantPayments,
                    AdminStaff: this.state.AdminStaff,
                    merchantMangement: this.state.merchantMangement,
                    notifications: this.state.notifications,
                    users: this.state.users,
                    settings: this.state.settings,
                    Analytics: this.state.Analytics,
                    sliders: this.state.sliders,
                    enquiry: this.state.enquiry,

                    created_on: this.state.date
                })
                    .then(response => {
                        try {
                            console.log(response);
                            this.setState({Role: ''});
                            this
                                .props
                                .history
                                .push("/RoleListPage");

                        } catch (error) {
                            console.log('no');
                        }
                    });
            }
        } else {
            this
                .validator
                .showMessages();
            this.forceUpdate();
        }

    };
    render() {

        return (
            <div>
                <SidebarPage/>

                <div className="all-content-wrapper">
                    <HeaderPage/>

                    <div className="breadcome-area">
                        <div className="container-fluid">
                            <div className="row">
                                <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                    <div className="breadcome-list">
                                        <div className="row">
                                            <div className="col-lg-6 col-md-6 col-sm-6 col-xs-6">
                                                <div className="breadcomb-wp">

                                                    <div className="breadcomb-ctn">
                                                        <h2>Role</h2>
                                                    </div>
                                                </div>
                                            </div>

                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

                    <div className="single-product-tab-area mg-b-30">

                        <div className="single-pro-review-area">
                            <div className="container-fluid">
                                <div className="row">
                                    <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                        <div className="review-tab-pro-inner">

                                            <h4>Add Role</h4>
                                            <ReactNotification ref={this.notificationDOMRef}/>

                                            <div className="product-tab-list tab-pane fade active in">
                                                <form onSubmit={this.handleSubmit} id="Add-category">

                                                    <div className="row m-b-20">
                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                            <label >Role<span style={{
                color: 'red'
            }}>*</span>
                                                            </label>
                                                            <input
                                                                type="text"
                                                                className="form-control"
                                                                placeholder="Role Value*"
                                                                id="add_Role"
                                                                name="Role"
                                                                onChange={this.onChange}
                                                                value={this.state.Role}/> {this
                                                                .validator
                                                                .message("Role Value", this.state.Role, "required|whitespace|specialChar|min:3|max:70")}

                                                        </div>

                                                    </div>
                                                    {userRoles.map((res,index)=>{
                                                        var resState= this.state+"."+res;
                                                        console.log(resState);
                                                        
                                                        return(
                                                            <div className="row" key={index}>
                                                                 <div className="form-group">
                                                        <label htmlFor="inputEmail3" className="col-sm-2 control-label">{res.charAt(0).toUpperCase() + res.slice(1)}</label>
                                                        <div className="col-sm-10">
                                                            <label className="radio-inline no-padd">
                                                                <input
                                                                    type="radio"
                                                                    name={res}
                                                                    value="1"
                                                                    onChange={this.onChange}
                                                                    checked={this.state.orders === '1'}
                                                                    className="icheck"/>
                                                                Read &nbsp;&nbsp;</label>
                                                            <label className="radio-inline no-padd">
                                                                <input
                                                                    type="radio"
                                                                    name={res}
                                                                    value="0"
                                                                    onChange={this.onChange}
                                                                    checked={this.state.orders === '0'}
                                                                    className="icheck"/>
                                                                Read & Write</label>

                                                        </div>

                                                    </div>
                                                            </div>
                                                        )

                                                    })}
                                                   
                                                   

                                                    <div className="row">
                                                        <div className="col-lg-6 col-md-6 col-sm-12 col-xs-12">
                                                            <div className="text-left custom-pro-edt-ds">

                                                                <button type="submit" className="btn btn-ctl-bt waves-effect waves-light">Add</button>
                                                                <Link to="/RoleListPage">
                                                                    <button type="button" className="btn btn-ctl-bt waves-effect waves-light">Cancel
                                                                    </button>
                                                                </Link>

                                                            </div>
                                                        </div>
                                                    </div>
                                                </form>
                                            </div>

                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
                <FooterPage/>

            </div>
        );
    }
}

export default AddRolePage;
