import React from "react";
import {Link} from "react-router-dom";
import HeaderPage from "./header";
import FooterPage from "./footer";
import SidebarPage from "./sidebar";
import firebase from '../config';
import swal from 'sweetalert';
import $ from 'jquery';
$.DataTable = require('datatables.net');

class Locations extends React.Component {
    constructor(props) {
        super(props);
        this.datatable = null;
        this.state = {
            locationList: [],
            loading: false,
            count: 1,
            activePage: "1",
            countPage: "",
            usePermList: ""
        };

    }
   
    componentDidMount() {
        this.setState({loading: true});
        var sessionId = sessionStorage.getItem("RoleId");
        var ref = firebase
            .database()
            .ref("Locations/");

        ref.on('value', snapshot => {
            const data = [];
            // console.log(snapshot); console.log(snapshot.val);

            snapshot.forEach(childSnapShot => {

                const Categories = {
                    locationId: childSnapShot
                        .key
                        .toString(),
                        location_Name: childSnapShot.val().location_Name,
                        addressl: childSnapShot.val().addressl,
                        address2: childSnapShot.val().address2,
                        land_Mark: childSnapShot.val().land_Mark,
                        city: childSnapShot.val().city,
                        pincode: childSnapShot.val().pincode,
                        contact_Person_Name: childSnapShot.val().contact_Person_Name,
                        contact_Number: childSnapShot.val().contact_Number,
                        email: childSnapShot.val().email,
                        rental_Deposit_Amount: childSnapShot.val().rental_Deposit_Amount,
                        rent_Per_Month: childSnapShot.val().rent_Per_Month,
                        electricity_Per_Month: childSnapShot.val().electricity_Per_Month,
                        electricity_Per_Month: childSnapShot.val().electricity_Per_Month,
                        additional_Expenses: childSnapShot.val().additional_Expenses,
                        notes: childSnapShot.val().notes,
                        avatarURL: childSnapShot.val().avatarURL,
                        created_on: this.state.created_on, 
                        sessionId: sessionId,
                   
                    
                };

                data.unshift(Categories);
            });

            this.setState({locationList: data, countPage: data.length, loading: false});
            console.log(this.state.locationList);
            // this.$el = $(this.el);

            this.interval = setTimeout(() => $('.paginationTable').DataTable(), 1000);
        });

    }
    componentWillUnmount() {
        //this.dataTable.destroy(true);
        clearTimeout(this.interval);
    }
    deleteItem = id => {

        swal({title: "Are you sure?", text: "Do your really want to remove?", icon: "warning", buttons: true, dangerMode: true}).then(willDelete => {
            if (willDelete) {
                console.log(id);
                var playersRef = firebase
                    .database()
                    .ref(`/Locations/${id}`);
                playersRef.remove();

                this
                    .props
                    .history
                    .push("/Locations/Deleted");
            } else {}
            // window.location.reload("/Home");

        });

    };

    render() {

        var pathname = this
            .props
            .location
            .pathname
            .split("/");
        var alertName = pathname[2];
        return (
            <div>
                <SidebarPage/>

                <div className="all-content-wrapper">
                    <HeaderPage/>

                    <div className="breadcome-area">
                        <div className="container-fluid">
                            <div className="row">
                                <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                    <div className="breadcome-list">
                                        <div className="row">
                                            <div className="col-lg-6 col-md-6 col-sm-6 col-xs-6">
                                                <div className="breadcomb-wp">

                                                    <div className="breadcomb-ctn">
                                                        <h2>Categories</h2>

                                                    </div>

                                                </div>
                                            </div>

                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

                    <div className="product-status mg-b-30">
                        <div className="container-fluid">
                            <div className="row">
                                <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                    <div className="product-status-wrap">
                                        <h4>Location List</h4>

                                        <div className="add-product">
                                                    <Link to='/AddLocations'>Add Location</Link>
                                                </div>
                                          
                                        {alertName
                                            ? <div
                                                    className="alert alert-success display-none"
                                                    style={{
                                                    display: "block"
                                                }}>
                                                    <a data-dismiss="alert" href="#" aria-hidden="true" className="close">Ã—</a>
                                                    Locations {alertName} &nbsp;
                                                    Successfully
                                                </div>
                                            : ''}
                                        <table className="table table-bordered paginationTable">
                                            <thead>
                                                <tr>
                                                    <th>S.No</th>
                                                    <th>Location Name</th>
                                                   <th>City</th>
                                                    <th>Added By</th>
                                                    <th>Added On</th>
                                                    <th>Action</th>
                                                       

                                                </tr>
                                            </thead>
                                            <tbody>

                                                {this
                                                    .state
                                                    .locationList
                                                    .map((data, index) => {
                                                        console.log(data);

                                                        return (
                                                            <tr key={index}>
                                                                <td>{index + 1}</td>
                                                                <td>{data.location_Name}</td>
                                                                <td>{data.city}</td>
                                                                <td>{data.contact_Person_Name}</td>
                                                                <td>{data.created_on}
                                                                </td>
                                                                <td>

                                                                            <Link to={`/EditLocation/${data.locationId}`}>
                                                                                <button data-toggle="tooltip" title="Edit" className="pd-setting-ed">
                                                                                    <i className="fa fa-pencil-square-o" aria-hidden="true"></i>
                                                                                </button>
                                                                            </Link>

                                                                            <button
                                                                                data-toggle="tooltip"
                                                                                title="Trash"
                                                                                className="pd-setting-ed"
                                                                                data-id={data.locationId}
                                                                                onClick={this
                                                                                .deleteItem
                                                                                .bind(this, data.locationId)}>
                                                                                <i className="fa fa-trash-o" aria-hidden="true"></i>
                                                                            </button>
                                                                        </td>
                                                                   

                                                            </tr>
                                                        )

                                                    })}

                                            </tbody>
                                        </table>

                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

                </div>

                <FooterPage/>

            </div>
        );
    }
}

export default Locations;
