import React from "react";
import { Link } from "react-router-dom";
import HeaderPage from "./header";
import FooterPage from "./footer";
// import HomePage from "./home";
import SidebarPage from "./sidebar";
import firebase from '../config';
import Pagination from "react-js-pagination";




class MerchantListPage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
        merchantList: [],
        loading: false,
        count: 1,
        activePage: "1",
        countPage: ""
    };
    this.deleteItem = this
        .deleteItem
        .bind(this);

}

componentDidMount() {
    this.setState({loading: true});
    var ref = firebase
        .database()
        .ref("merchant/");

    ref.on('value', snapshot => {
        const data = [];
        // console.log(snapshot); console.log(snapshot.val);

        snapshot.forEach(childSnapShot => {

            const Merchants = {
                merchantId: childSnapShot.key.toString(),
                merchantName: childSnapShot.val().merchantName,
                gstValue: childSnapShot.val().gstValue,
                products: childSnapShot.val().products,
                orders: childSnapShot.val().orders,
                delivery: childSnapShot.val().delivery,
                coupons: childSnapShot.val().coupons,
                contactNumber: childSnapShot.val().contactNumber,
                notification: childSnapShot.val().notification,
                status: childSnapShot.val().status,
                created_on: childSnapShot .val().created_on
            };

            data.push(Merchants);
        });
        console.log(data.length);
        this.setState({merchantList: data, countPage: data.length,loading: false});
        console.log(this.state.merchantList);
        console.log(data.length);
    });

}
deleteItem = id => {

    console.log(id);
    var playersRef = firebase
        .database()
        .ref(`/merchant/${id}`);
    playersRef.remove();

};
handlePageChange(pageNumber) {
    console.log(`active page is ${pageNumber}`);
    this.setState({ activePage: pageNumber });
  }

  render() {
    

    return (
      <div>
     <SidebarPage/>
   
    <div className="all-content-wrapper">
    <HeaderPage/>
   
          
            <div className="breadcome-area">
                <div className="container-fluid">
                    <div className="row">
                        <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                            <div className="breadcome-list">
                                <div className="row">
                                    <div className="col-lg-6 col-md-6 col-sm-6 col-xs-6">
                                        <div className="breadcomb-wp">
                                        
                                            <div className="breadcomb-ctn">
                                            <h2>Merchants</h2>
                                                </div>
                                        </div>
                                    </div>
                          
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
      
     
            <div className="product-status mg-b-30">
            <div className="container-fluid">
                <div className="row">
                    <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                        <div className="product-status-wrap">
                            <h4>Merchants List</h4>
                            <div className="add-product">
                            <Link to='/AddMerchantPage'>Add Merchant</Link>
                            </div>
                    
                            <table className="table table-bordered table-condensed paginationTable">
                            <thead>
                                <tr>
                                <th>S.no</th>
                                    <th>Merchant</th>
                                    <th>ID</th>
                                    <th>GST</th>
                                    <th>Products</th>
                                    <th>Orders</th>
                                    <th>Delivery</th>
                                    <th>Coupons</th>
                                    <th>Contact Number</th>
									<th>Notification</th>
									<th>Status</th>
									<th>Action</th>
                                </tr>
								</thead>
                                            <tbody>
                                            {this.state.merchantList.map((data, index) => {
                                                        console.log(data);

                                                        return (
                                                           
                                                                <tr key={index}>
                                                                    <td>{index + 1}</td>
                                                                    <td>{data.merchantName}</td>
                                                                    <td>{data.merchantId}</td>
                                                                    <td>{data.gstValue}</td>
                                                                    <td>{data.products}
                                                                    </td>
                                                                    <td>{data.orders}</td>
                                                                    <td>{data.delivery}</td>
                                                                    <td>{data.coupons}</td>
                                                                    <td>{data.contactNumber}</td>
                                                                    <td>{data.notification}</td>
                                                                   
                                                                    

                                                                    <td>
                                                                        <select
                                                                            name="select"
                                                                            className="form-control pro-edt-select form-control-primary active">
                                                                            <option value="opt2">Active</option>
                                                                            <option value="opt3">Delete</option>
                                                                            <option value="opt3">Pending</option>
                                                                        </select>
                                                                    </td>

                                                                    <td>
                                                                    {/* <button data-toggle="tooltip" title="View" className="pd-setting-ed"><i className="fa fa-eye" aria-hidden="true"></i></button> */}

                                                                    <Link to={`/EditMerchantPage/${data.merchantId}`}>
                                         <button data-toggle="tooltip" title="Edit" className="pd-setting-ed"><i className="fa fa-pencil-square-o" aria-hidden="true"></i></button>
                                         </Link>								
                     
                                         <button data-toggle="tooltip" title="Trash" className="pd-setting-ed"   onClick={this.deleteItem.bind(this, data.merchantId)}><i className="fa fa-trash-o" aria-hidden="true"></i></button>
                                         </td>
                                                                </tr>)
                                                                }
                                                    )}

                                           
								
                                
                                </tbody>
										
                               
							
							</table>
                            {/* <div className="custom-pagination">
                            <Pagination
                          hideNavigation
                          activePage={this.state.activePage}
                          itemsCountPerPage={3}
                          totalItemsCount={this.state.countPage}
                          pageRangeDisplayed={3}
                          onChange={this.handlePageChange.bind(this)}
                        />
                            </div> */}
                        </div>
                    </div>
                </div>
            </div>
        </div>
        </div>
         <FooterPage/>
    
      </div>
    );
  }
}

export default MerchantListPage;
