            console.log(products);

            this.setState({
                productName: products.productName,
                category: products.category,
                subCategory: products.subCategory,
                gstValue: products.gstValue,
                pincode: products.pincode,
                price: products.price,
                cancellation: products.cancellation,
                offerPrice: products.offerPrice,
                sellingPrice: products.sellingPrice,
                quantityStock: products.quantityStock,
                alertQuantity: products.alertQuantity,
                packagingCost: products.packagingCost,
                downloadURLs: products.uploadImage,
                avatarURL: products.frontImage,
                content: products.description,
                loading: false
            }, () => {
                this.setSubCategoryList(this.state.category)
            });

        });
        {
