import React from "react";
import {Link} from "react-router-dom";
import FileUploader from "react-firebase-file-uploader";

import {Form} from 'reactstrap';
import HeaderPage from "./header";
import FooterPage from "./footer";
import SidebarPage from "./sidebar";
import firebase from '../config';
import SimpleReactValidator from "simple-react-validator";

class EditSubCategoryPage extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            category: '',
            subCategory: '',
            categoryList: [],
            error_message: '',
            date: new Date().toLocaleString(),
            validError: true,
            uploadImage: '',
            avatar: "",
            isUploading: false,
            progress: 0,
            avatarURL: ""
        }

        this.validator = new SimpleReactValidator({
            className: "text-danger",
            validators: {

                whitespace: {
                    message: "The :attribute not allowed first whitespace   characters.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /[^\s\\]/) && params.indexOf(val) === -1);
                    }
                },
                specialChar: {
                    message: "The :attribute not allowed special   characters.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /^[ A-Za-z0-9_@./#&+-]*$/i) && params.indexOf(val) === -1);
                    }
                },
                specialCharText: {
                    message: "The :attribute may only contain letters, dot and spaces.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /^[ A-Za-z_@./#&+-]*$/i) && params.indexOf(val) === -1);
                    }
                }
            }
        });

    }
    componentDidMount() {
        const {categoryID} = this.props.match.params;
        console.log(categoryID);

        var ref = firebase
            .database()
            .ref(`sub_category/${categoryID}`);

        ref.on('value', snapshot => {
            var categories = snapshot.val();
            // console.log(categories)

            this.setState({category: categories.category, avatarURL: categories.subCategoryLogo, subCategory: categories.subCategory, loading: false});
            console.log(this.state.category);
        });

    }
    componentWillMount() {
        this.setState({loading: true});
        var ref = firebase
            .database()
            .ref("category/");

        ref.on('value', snapshot => {
            const data = [];

            snapshot.forEach(childSnapShot => {

                const Categories = {
                    categoryId: childSnapShot
                        .key
                        .toString(),
                    category: childSnapShot
                        .val()
                        .category,

                    created_on: childSnapShot
                        .val()
                        .created_on
                };

                data.push(Categories);
            });

            this.setState({categoryList: data, loading: false});
            console.log(this.state.categoryList);
        });

    }
    onChange = (event) => {

        this.setState({
            [event.target.name]: event.target.value
        });
    };
    subCategoryChange = (event) => {
        this.setState({
            [event.target.name]: event.target.value
        });

        var ref = firebase
            .database()
            .ref('sub_category/')
            .orderByChild("subCategory")
            .equalTo(event.target.value);
        ref.on('value', snapshot => {
            var user_exist = snapshot.numChildren();
            console.log(user_exist);

            if (user_exist > 0) {

                this.setState({error_message: "Sub Category Name is already exist", validError: false});
            } else {
                this.setState({error_message: "", validError: true});

            }

        })

    };
    handleUploadStart = () => this.setState({isUploading: true, progress: 0});
    handleProgress = progress => this.setState({progress});
    handleUploadError = error => {
        this.setState({isUploading: false});
        console.error(error);
    };
    handleUploadSuccess = filename => {
        this.setState({avatar: filename, progress: 100, isUploading: false});
        firebase
            .storage()
            .ref("images")
            .child(filename)
            .getDownloadURL()
            .then(url => this.setState({avatarURL: url}));
        console.log(this.state.avatarURL);
    };
    handleSubmit = (event) => {
        event.preventDefault();
        if (this.validator.allValid() && this.state.validError == true) {
            const {categoryID} = this.props.match.params;
            var userName = sessionStorage.getItem("userName");
            if (this.state.category && this.state.category.trim().length !== 0) {
                event.preventDefault();
                let dbCon = firebase
                    .database()
                    .ref(`/sub_category/${categoryID}`);
                console.log(dbCon);
                dbCon.update({category: this.state.category, subCategory: this.state.subCategory, subCategoryLogo: this.state.avatarURL, userName: userName, created_on: this.state.date});

                this
                    .props
                    .history
                    .push("/SubCategoryListPage/Updated");
            }
        } else {
            this
                .validator
                .showMessages();
            this.forceUpdate();
        }
    };

    render() {

        return (
            <div>
                <SidebarPage/>

                <div className="all-content-wrapper">
                    <HeaderPage/>

                    <div className="breadcome-area">
                        <div className="container-fluid">
                            <div className="row">
                                <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                    <div className="breadcome-list">
                                        <div className="row">
                                            <div className="col-lg-6 col-md-6 col-sm-6 col-xs-6">
                                                <div className="breadcomb-wp">

                                                    <div className="breadcomb-ctn">
                                                        <h2>Categories</h2>
                                                    </div>
                                                </div>
                                            </div>

                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

                    <div className="single-product-tab-area mg-b-30">

                        <div className="single-pro-review-area">
                            <div className="container-fluid">
                                <div className="row">
                                    <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                        <div className="review-tab-pro-inner">

                                            <h4>Edit Sub Category</h4>

                                            <div className="product-tab-list tab-pane fade active in">
                                                <Form onSubmit={this.handleSubmit} id="Edit-category">

                                                    <div className="row m-b-20">
                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                            <label>Category Name<span style={{
                color: 'red'
            }}>*</span>
                                                            </label>
                                                            <select
                                                             
                                                                className="form-control pro-edt-select form-control-primary"
                                                                name="category"
                                                                onChange={this.onChange}>
                                                               
                                                                {this.state.categoryList && this
                                                                    .state
                                                                    .categoryList
                                                                    .map((data, index) => {

                                                                        return (
                                                                            <option value={data.category} key={index} selected={data.category == this.state.category}>{data.category}</option>
                                                                        )

                                                                    })}
                                                              

                                                            </select>
                                                            {this
                                                                    .validator
                                                                    .message("Select category", this.state.category, "required")}

                                                        </div>
                                                    </div>
                                                    <div className="row m-b-20">
                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                            <label>Sub Category Name<span style={{
                color: 'red'
            }}>*</span>
                                                            </label>
                                                            <input
                                                                type="text"
                                                                className="form-control"
                                                                placeholder="Sub Category Name*"
                                                                name="subCategory"
                                                                onChange={this.subCategoryChange}
                                                                value={this.state.subCategory}/> {this
                                                                .validator
                                                                .message("Sub Category Name", this.state.subCategory, "required|whitespace|specialChar|min:3|max:20")}
                                                            <div className="text-danger">
                                                                {" "}
                                                                {this.state.error_message}
                                                            </div>
                                                        </div>

                                                    </div>
                                                    <div className="row m-b-20">
                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                            <div className="dropzone-pro">
                                                                <div id="dropzone">

                                                                    <div className="dz-message needsclick download-custom">
                                                                        <i className="fa fa-cloud-download" aria-hidden="true"></i>
                                                                        <h2>Upload Sub Category Logo<span style={{
                color: 'red'
            }}>*</span>
                                                                        </h2>

                                                                    </div>

                                                                    {this.state.isUploading && <p>Progress: {this.state.progress}</p>}
                                                                    {this.state.avatarURL && <img
                                                                        src={this.state.avatarURL}
                                                                        style={{
                                                                        height: "200px",
                                                                        width: "250px"
                                                                    }}/>}
                                                                    <FileUploader
                                                                        accept="image/*"
                                                                        name="avatar"
                                                                        randomizeFilename
                                                                        storageRef={firebase
                                                                        .storage()
                                                                        .ref("images")}
                                                                        onUploadStart={this.handleUploadStart}
                                                                        onUploadError={this.handleUploadError}
                                                                        onUploadSuccess={this.handleUploadSuccess}
                                                                        onProgress={this.handleProgress}/> {this
                                                                        .validator
                                                                        .message("Upload Product Image", this.state.avatarURL, "required")}

                                                                </div>

                                                            </div>

                                                        </div>
                                                    </div>

                                                    <div className="row">
                                                        <div className="col-lg-6 col-md-6 col-sm-12 col-xs-12">
                                                            <div className="text-left custom-pro-edt-ds">
                                                                <button type="submit" className="btn btn-ctl-bt waves-effect waves-light">Save
                                                                </button>
                                                                <Link to="/SubCategoryListPage">
                                                                    <button type="button" className="btn btn-ctl-bt waves-effect waves-light">Cancel
                                                                    </button>
                                                                </Link>

                                                            </div>
                                                        </div>
                                                    </div>
                                                </Form>
                                            </div>

                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
                <FooterPage/>

            </div>
        );
    }
}
export default EditSubCategoryPage;
