import React from "react";
import { Link } from "react-router-dom";
import { Breadcrumb, BreadcrumbItem } from 'reactstrap';

import HeaderPage from "./header";
import FooterPage from "./footer";
// import HomePage from "./home";
import SidebarPage from "./sidebar";
import firebase from '../config';

import $ from 'jquery';
$.DataTable = require('datatables.net');

class Users extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
        gstList: [],
        loading: false,
        count: 1,
        activePage: "1",
        countPage: ""
      };
      this.deleteItem = this.deleteItem.bind(this);
      this.changeStatus = this.changeStatus.bind(this);

  }

  componentDidMount() {
    this.setState({ loading: true });
    var ref = firebase.database().ref("users/").orderByChild("Role").equalTo("Users");

    
    var refOrder = firebase
            .database()
            .ref("orders/");

    ref.on('value', snapshot => {


        var Users = snapshot.val();
    console.log(Users);
    
        const data = [];
        snapshot.forEach(childSnapShot => {
            const GSTData = {
                cmsId: childSnapShot.key.toString(),
                firstName: childSnapShot.val().first_name,
                lastName: childSnapShot.val().last_name,
                email: childSnapShot.val().email_id,
                status: childSnapShot.val().status,
                createdAt: childSnapShot.val().created_on,
                updatedAt: childSnapShot.val().updatedAt,
                };

           
            refOrder.orderByChild("sessionId").equalTo(childSnapShot.key.toString()).on('value', snap => {
                GSTData.orders=snap.numChildren();
            });
           
          
  
          data.push(GSTData);
        });
        setTimeout(() => {
            this.setState({ gstList:data,countPage: data.length,loading: false });
          }, 1000)
  
      
          this.interval = setTimeout(() => $('.paginationTable').DataTable(), 1000);
        });
    }
    componentWillUnmount() {
        
        clearTimeout(this.interval);
      }
    deleteItem = id => {
//         console.log(id);
//         firebase.auth().delete(id).then((result) => {

//             console.log(result);
//     // console.log(id);
//     // var playersRef = firebase.database().ref(`/users/${id}`);
//     // playersRef.remove();
// })
// var playersRef = firebase.auth().ref(id);
// playersRef.remove();
// admin.auth().deleteUser(id)
//   .then(function() {
//     console.log('Successfully deleted user');
//   })
//   .catch(function(error) {
//     console.log('Error deleting user:', error);
//   });
    };

    changeStatus = id => {
       
        //var playersRef = firebase.database().ref(`/Register/${id}`);
       // playersRef.remove();
    
        };

    handlePageChange(pageNumber) {
        console.log(`active page is ${pageNumber}`);
        this.setState({ activePage: pageNumber });
    }


  render() {
    

    return (
      <div>
     <SidebarPage/>
   
    <div className="all-content-wrapper">
    <HeaderPage/>
   
          
            <div className="breadcome-area">
                <div className="container-fluid">
                    <div className="row">
                        <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                            <div className="breadcome-list">
                                <div className="row">
                                    <div className="col-lg-6 col-md-6 col-sm-6 col-xs-6">
                                        <div className="breadcomb-wp">
                                        
                                            <div className="breadcomb-ctn">
                                            <h2>Users</h2>
                                                </div>
                                        </div>
                                    </div>
                          
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
      
     
            <div className="product-status mg-b-30">
            <div className="container-fluid">
                <div className="row">
                    <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                        <div className="product-status-wrap">
                            <h4>Users</h4>
                         
                            <table className="table table-bordered paginationTable">
                            <thead>
                                <tr>
                                    <th>S.No</th>
                                    <th>First Name</th>
                                    <th>Last Name</th>
                                   
                                    <th>Orders</th>
                                    <th>Status</th>
                                    {/* <th>City</th>
                                    <th>Addresses</th> */}
									<th>Created on</th>
                                <th>Action</th>
                                </tr>
                                </thead>
                                <tbody>
                                 
                                    
                                    {this.state.gstList && this.state.gstList.map((data,index) =>{
                                        console.log(data.orders);
                                       
                                        return(
                                         <tr key={index}>
                                         <td>{index+1}</td>
                                         <td>{data.firstName}</td>
                                         <td>{data.lastName}	</td>
                                        
                                         <td>  <Link to={`/ViewOrders/${data.cmsId}`}>{data.orders}</Link>		</td>
                                         {/* <td>{data.city}</td>
                                         <td>{data.Addresses}	</td> */}
                                         <td>
                                                                    <select
                                                                        name="status"
                                                                        onChange={this.onChange}
                                                                        id={data.cmsId}
                                                                        className="form-control pro-edt-select form-control-primary active">
                                                                        <option
                                                                            value="Active"
                                                                            selected={data.status == "Active"
                                                                            ? true
                                                                            : false}>Active</option>
                                                                        <option
                                                                            value="InActive"
                                                                            selected={data.status == "InActive"
                                                                            ? true
                                                                            : false}>InActive</option>

                                                                    </select>
                                                                </td>
                                         <td>{data.createdAt}	</td>
                                         <td>									
                                         {/* <button data-toggle="tooltip" title="Status" className="pd-setting-ed" data-id={data.cmsId}  onClick={this.changeStatus.bind(this, data.cmsId)}><i className='fas fa-thumbs-up'></i>{this.state.status}</button> */}
                                     
                                         <Link to={`/viewUser/${data.cmsId}`}>
                                         <button data-toggle="tooltip" title="Edit" className="pd-setting-ed"><i className="fa fa fa-eye" aria-hidden="true"></i></button>
                                         </Link>								
                     
                                         {/* <button data-toggle="tooltip" title="Trash" className="pd-setting-ed" data-id={data.cmsId}  onClick={this.deleteItem.bind(this, data.cmsId)}><i className="fa fa-trash-o" aria-hidden="true"></i></button> */}
                                         </td>
                                         
                                         </tr>)

                                    } )}
								

                   
                    </tbody>
                    </table>
                    {/* <div className="custom-pagination">
                            <Pagination
                          hideNavigation
                          activePage={this.state.activePage}
                          itemsCountPerPage={3}
                          totalItemsCount={this.state.countPage}
                          pageRangeDisplayed={3}
                          onChange={this.handlePageChange.bind(this)}
                        />
                            </div> */}
                        </div>
                    </div>
                </div>
            </div>
        </div> 
            
        </div>
        
         <FooterPage/>
    
      </div>
    );
  }
}

export default Users;
