import React from "react";
import { Form } from 'reactstrap';

import HeaderPage from "./header";
import FooterPage from "./footer";
// import HomePage from "./home";
import SidebarPage from "./sidebar";
import firebase from '../config';

import ReactNotification from "react-notifications-component";
import "react-notifications-component/dist/theme.css";
import SimpleReactValidator from "simple-react-validator";


class AddServicePage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
        serviceProviderName:'',
        trackingURL:'',
        date:new Date().toLocaleString(),

    }
    this.addNotification = this.addNotification.bind(this);
        this.notificationDOMRef = React.createRef();
    this.validator = new SimpleReactValidator({
      className: "text-danger",
      validators: {

          whitespace: {
              message: "The :attribute not allowed first whitespace   characters.",
              rule: function (val, params, validator) {
                  // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                  // params.indexOf(val) === -1
                  return (validator.helpers.testRegex(val, /[^\s\\]/) && params.indexOf(val) === -1);
              }
          },
          specialChar: {
              message: "The :attribute not allowed special   characters.",
              rule: function (val, params, validator) {
                  // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                  // params.indexOf(val) === -1
                  return (validator.helpers.testRegex(val, /^[ A-Za-z0-9_@./#&+-]*$/i) && params.indexOf(val) === -1);
              }
          },
          website: {
            message: "The Url should be example.com ",
            rule: function(val, params, validator) {
              //return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) && params.indexOf(val) === -1
              return (
                validator.helpers.testRegex(
                  val,
                  /(http(s)?:\/\/.)?(www\.)?[-a-zA-Z0-9@:%._\+~#=]{2,256}\.[a-z]{2,6}\b([-a-zA-Z0-9@:%_\+.~#?&//=]*)/g
                ) && params.indexOf(val) === -1
              );
            }
          },
          specialCharText: {
              message: "The :attribute may only contain letters, dot and spaces.",
              rule: function (val, params, validator) {
                  // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                  // params.indexOf(val) === -1
                  return (validator.helpers.testRegex(val, /^[ A-Za-z_@./#&+-]*$/i) && params.indexOf(val) === -1);
              }
          }
      }
  });

  }
  onChange = (event) => {
      
    this.setState({ [event.target.name]: event.target.value });
  };

  handleSubmit = (event) => {
    //   console.log(this.state.trackingURL);
    event.preventDefault();

    if (this.validator.allValid()) {
    if (this.state.serviceProviderName && this.state.serviceProviderName.trim().length !== 0) {
      this.addNotification();

        let dbCon = firebase.database().ref('/serviceProvider');
        dbCon.push({
            serviceProviderName: this.state.serviceProviderName,
            trackingURL: this.state.trackingURL,
            created_on: this.state.date
        });

      
      this.setState({
        serviceProviderName:'',
        trackingURL:'',
      });
      this.props.history.push("/ServiceProviderListPage");
    }
  } else {
    this
        .validator
        .showMessages();
    this.forceUpdate();
  }
  };
  addNotification() {
    this.notificationDOMRef.current.addNotification({
      title: "Success",
      message: "Succesfully added!",
      type: "success",
      insert: "bottom",
      container: "top-center",
      animationIn: ["animated", "fadeIn"],
      animationOut: ["animated", "fadeOut"],
      dismiss: { duration: 2000 },
      dismissable: { click: true }
    });
  }
  render() {
    

    return (
      <div>
     <SidebarPage/>
   
    <div className="all-content-wrapper">
    <HeaderPage/>
   
          
            <div className="breadcome-area">
                <div className="container-fluid">
                    <div className="row">
                        <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                            <div className="breadcome-list">
                                <div className="row">
                                    <div className="col-lg-6 col-md-6 col-sm-6 col-xs-6">
                                        <div className="breadcomb-wp">
                                        
                                            <div className="breadcomb-ctn">
                                            <h2>Service Provider</h2>
                                                </div>
                                        </div>
                                    </div>
                          
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
      
     
            <div className="single-product-tab-area mg-b-30">
          
            <div className="single-pro-review-area">
                <div className="container-fluid">
                    <div className="row">
                        <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                            <div className="review-tab-pro-inner">
                   
				   <h4>Add Service Provider</h4>
           <ReactNotification ref={this.notificationDOMRef} />
				   
                            
                            <div className="product-tab-list tab-pane fade active in">
                            <Form
      onSubmit={this.handleSubmit}
        id="Add-service"
      >
                            
                            <div className="row m-b-20">
                            <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                            <label>Service Provider Name<span style={{color: 'red' }}>*</span></label>
                            <input type="text" className="form-control" placeholder="Service Provider Name*"  id="add_service"
                            name="serviceProviderName" onChange={ this.onChange } value={ this.state.serviceProviderName } required=""/>
 {this.validator.message(
                  "Service Provider Name",
                  this.state.serviceProviderName,
                  "required|specialCharText|whitespace|min:2|max:70"
                )}
                          
                            </div>
                            </div>
                            <div className="row m-b-20">
                            <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                            <label>trackingURL<span style={{color: 'red' }}>*</span></label>
                         <input type="url" className="form-control" placeholder="trackingURL*"  id="add_tracking"
                         name="trackingURL" onChange={ this.onChange } value={ this.state.trackingURL } required="" />
            {this.validator.message(
                  "trackingURL",
                  this.state.trackingURL,
                  "website|min:3|max:255"
                )}             
                       
                         </div>

                            </div>

                                        <div className="row">
                                            <div className="col-lg-6 col-md-6 col-sm-12 col-xs-12">
                                                <div className="text-left custom-pro-edt-ds">
                                                    <button type="submit" className="btn btn-ctl-bt waves-effect waves-light">Add
														</button>
                                                 
                                                </div>
                                            </div>
                                        </div>
                                        </Form>
                                    </div>
                                 

			
								
						
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
        </div>
         <FooterPage/>
    
      </div>
    );
  }
}

export default AddServicePage;
