import React from "react";
import { Link } from "react-router-dom";
import FadeIn from "react-fade-in";
import axios from "axios";
import HeaderPage from "./header";
import FooterPage from "./footer";
// import HomePage from "./home";
import SidebarPage from "./sidebar";
import firebase from '../config';
import Pagination from "react-js-pagination";



class EmailContentListPage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
        emailContentList: [],
        loading: false,
        count: 1,
        activePage: "1",
        countPage: ""
      };
      this.deleteItem = this.deleteItem.bind(this);

  }

  componentDidMount() {
    this.setState({ loading: true });
    var ref = firebase.database().ref("emailContent/");


    ref.on('value', snapshot => {
        const data = [];
        // console.log(snapshot);
        // console.log(snapshot.val);

        snapshot.forEach(childSnapShot => {
           
          const EmailData = {
            emailContentId: childSnapShot.key.toString(),
            emailContentName: childSnapShot.val().emailContentName,
            emailDesc: childSnapShot.val().emailDesc,
            created_on: childSnapShot.val().created_on,
          };
  
          data.push(EmailData);
        });
  
        this.setState({ emailContentList:data, countPage: data.length,loading: false });
        console.log(this.state.emailContentList);
      });
}
    deleteItem = id => {
        

    console.log(id);
    var playersRef = firebase.database().ref(`/emailContent/${id}`);
    playersRef.remove();

    };
    handlePageChange(pageNumber) {
        console.log(`active page is ${pageNumber}`);
        this.setState({ activePage: pageNumber });
    }

  render() {
    

    return (
      <div>
     <SidebarPage/>
   
    <div className="all-content-wrapper">
    <HeaderPage/>
   
          
            <div className="breadcome-area">
                <div className="container-fluid">
                    <div className="row">
                        <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                            <div className="breadcome-list">
                                <div className="row">
                                    <div className="col-lg-6 col-md-6 col-sm-6 col-xs-6">
                                        <div className="breadcomb-wp">
                                        
                                            <div className="breadcomb-ctn">
                                            <h2>Merchants</h2>
                                                </div>
                                        </div>
                                    </div>
                          
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
      
     
            <div className="product-status mg-b-30">
            <div className="container-fluid">
                <div className="row">
                    <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                        <div className="product-status-wrap">
                            <h4>Email Content List</h4>
                            <div className="add-product">
                                <Link to='/AddEmailContentPage'>Add Email Content</Link>
                            </div>
                            <table className="table table-bordered paginationTable">
                            <thead>
                                <tr>
                                    <th>S.No</th>
                                    <th>Name</th>
                                    <th>Description</th>
									 <th>Created on</th>
                                <th>Action</th>
                                </tr>
                                </thead>
                                <tbody>
                                 
                                    
                                    {this.state.emailContentList && this.state.emailContentList.map((data,index) =>{
                                        console.log(data);
                                       
                                        return(
                                         <tr key={index}>
                                         <td>{index+1}</td>
                                         <td>{data.emailContentName}</td>
                                         <td>{data.emailDesc}</td>
                                         <td>{data.created_on}	</td>
                                         <td>									
                                            
                                         <Link to={`/EditEmailContentPage/${data.emailContentId}`}>
                                         <button data-toggle="tooltip" title="Edit" className="pd-setting-ed"><i className="fa fa-pencil-square-o" aria-hidden="true"></i></button>
                                         </Link>								
                     
                                         <button data-toggle="tooltip" title="Trash" className="pd-setting-ed" onClick={this.deleteItem.bind(this, data.emailContentId)}><i className="fa fa-trash-o" aria-hidden="true"></i></button>
                                         </td>
                                         
                                         </tr>)

                                    } )}
								

                   
                    </tbody>
                    </table>
                    {/* <div className="custom-pagination">
                            <Pagination
                          hideNavigation
                          activePage={this.state.activePage}
                          itemsCountPerPage={3}
                          totalItemsCount={this.state.countPage}
                          pageRangeDisplayed={3}
                          onChange={this.handlePageChange.bind(this)}
                        />
                            </div> */}
                        </div>
                    </div>
                </div>
            </div>
        </div> 
            
        </div>
        
         <FooterPage/>
    
      </div>
    );
  }
}

export default EmailContentListPage;
