import React from "react";
import { Link } from "react-router-dom";
import HeaderPage from "./header";
import FooterPage from "./footer";
// import HomePage from "./home";
import SidebarPage from "./sidebar";
import firebase from '../config';
import swal from 'sweetalert';
import $ from 'jquery';
$.DataTable = require( 'datatables.net' );

class websiteEnquiries extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
        gstList: [],
        loading: false,
        count: 1,
        activePage: "1",
        countPage: ""
      };
      this.deleteItem = this.deleteItem.bind(this);

  }

  componentDidMount() {
    this.setState({ loading: true });
    var ref = firebase.database().ref("websiteEnquiries/");


    ref.on('value', snapshot => {
        const data = [];
        // console.log(snapshot);
        // console.log(snapshot.val);

        snapshot.forEach(childSnapShot => {
            let content= childSnapShot.val().req_message;
            const toShow = content.substring(0,100)+"...";
          const GSTData = {
            cmsId: childSnapShot.key.toString(),
            name: childSnapShot.val().name,
            subject: childSnapShot.val().subject,
            email: childSnapShot.val().email_id,
            message: toShow,
            created_on: childSnapShot.val().created_on,
          };
  
          data.push(GSTData);
        });
  
        this.setState({ gstList:data,countPage: data.length,loading: false });
        this.interval = setTimeout(() => $('.paginationTable').DataTable(), 1000);
    });
}
componentWillUnmount() {
    clearTimeout(this.interval);
  }
  deleteItem = id => {
    
    swal({title: "Are you sure?", text: "Do your really want to remove?", icon: "warning", buttons: true, dangerMode: true}).then(willDelete => {
      if (willDelete) {
        console.log(id);
        var playersRef = firebase.database().ref(`/websiteEnquiries/${id}`);
        playersRef.remove();
        
  
      } else {}
     
     
  });
  
    };
 

    handlePageChange(pageNumber) {
        console.log(`active page is ${pageNumber}`);
        this.setState({ activePage: pageNumber });
    }


  render() {
    

    return (
      <div>
     <SidebarPage/>
   
    <div className="all-content-wrapper">
    <HeaderPage/>
   
          
            <div className="breadcome-area">
                <div className="container-fluid">
                    <div className="row">
                        <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                            <div className="breadcome-list">
                                <div className="row">
                                    <div className="col-lg-6 col-md-6 col-sm-6 col-xs-6">
                                        <div className="breadcomb-wp">
                                        
                                            <div className="breadcomb-ctn">
                                            <h2>Website Enquiries</h2>
                                                </div>
                                        </div>
                                    </div>
                          
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
      
     
            <div className="product-status mg-b-30">
            <div className="container-fluid">
                <div className="row">
                    <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                        <div className="product-status-wrap">
                            <h4>Website Enquiries</h4>
                            <div className="add-product">
                               
                            </div>
                            <table className="table table-bordered paginationTable">
                            <thead>
                                <tr>
                                    <th>S.No</th>
                                    <th>Name</th>
                                    <th>Email</th>
                                    <th>Subject</th>
                                    <th>Message</th>
									 <th>Created on</th>
                                <th>Action</th>
                                </tr>
                                </thead>
                                <tbody>
                                 
                                    
                                    {this.state.gstList && this.state.gstList.map((data,index) =>{
                                        console.log(data);
                                       
                                        return(
                                         <tr key={index}>
                                         <td>{index+1}</td>
                                         <td>{data.name}</td>
                                         <td>{data.email}</td>
                                         <td>{data.subject}</td>
                                         <td>{data.message}</td>
                                         <td>{data.created_on}	</td>
                                         <td>									
                                            
                                         <Link to={`/viewEnquiries/${data.cmsId}`}>
                                         <button data-toggle="tooltip" title="View" className="pd-setting-ed"><i className="fa fa-eye" aria-hidden="true"></i></button>
                                         </Link>								
                     
                                         <button data-toggle="tooltip" title="Trash" className="pd-setting-ed" data-id={data.cmsId}  onClick={this.deleteItem.bind(this, data.cmsId)}><i className="fa fa-trash-o" aria-hidden="true"></i></button>
                                         </td>
                                         
                                         </tr>)

                                    } )}
								

                   
                    </tbody>
                    </table>
                    
                        </div>
                    </div>
                </div>
            </div>
        </div> 
            
        </div>
        
         <FooterPage/>
    
      </div>
    );
  }
}

export default websiteEnquiries;
