import React from "react";
import {Link} from "react-router-dom";
import FadeIn from "react-fade-in";
import axios from "axios";
import HeaderPage from "./header";
import FooterPage from "./footer";
// import HomePage from "./home";
import SidebarPage from "./sidebar";
import firebase from '../config';
import moment from 'moment';
import { CSVLink, CSVDownload } from "react-csv";

import swal from 'sweetalert';
import $ from 'jquery';
$.DataTable = require('datatables.net');

class OrderListPage extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            orderList: [],
            loading: false,
            count: 1,
            activePage: "1",
            countPage: "",
            orderStatusList: []
        };
        this.deleteItem = this
            .deleteItem
            .bind(this);

    }

    componentDidMount() {
        var pathname = this.props.location.pathname;

        var pageName = pathname.substr(8);

        this.setState({loading: true});
        var sessionId = sessionStorage.getItem("RoleId");
        var userRole = sessionStorage.getItem("userRole");
        if (userRole == "Merchant") {
            var ref = firebase
                .database()
                .ref("orders/")
                .orderByChild("merchantId")
                .equalTo(sessionId);
        } else {
            var ref = firebase
                .database()
                .ref("orders/");

        }

        ref.once('value', snapshot => {
            const data = [];
            console.log(snapshot.val());

            snapshot.forEach(childSnapShot => {

                const Merchants = {
                    fireorderId: childSnapShot
                        .key
                        .toString(),
                    orderId: childSnapShot
                        .val()
                        .orderId,
                    customerName: childSnapShot
                        .val()
                        .first_name + " " + childSnapShot
                        .val()
                        .last_name,
                    items: childSnapShot
                        .val()
                        .cartDataItems
                        .length,
                    status: childSnapShot
                        .val()
                        .status,
                    created_on: childSnapShot
                        .val()
                        .created_on,
                    emailId: childSnapShot
                        .val()
                        .email_id
                };

                data.push(Merchants);
            });
            if(pageName!="All"){
                var sortedKeys = data.filter(res => {
                    console.log(res);
    
                    return res.status == pageName;
                });
            }else{
                var sortedKeys = data;
            }

           
            console.log(sortedKeys);

            this.setState({orderList: sortedKeys, countPage: data.length, loading: false});
            console.log(this.state.orderList);
             this.interval = setTimeout(() => $('.paginationTable').DataTable(), 1000);
        });

        var ref = firebase
            .database()
            .ref("orderStatus");

        ref.on('value', snapshot => {
            const data = [];

            snapshot.forEach(childSnapShot => {

                const orderStatus = {
                    categoryId: childSnapShot
                        .key
                        .toString(),
                    orderStatus: childSnapShot
                        .val()
                        .orderStatus
                };

                data.push(orderStatus);
            });

            this.setState({orderStatusList: data, loading: false});
            console.log(this.state.orderStatusList);
        });

    }
    componentWillUnmount() {
        //this.dataTable.destroy(true);
        clearTimeout(this.interval);
    }
    deleteItem = id => {

        console.log(id);
        var playersRef = firebase
            .database()
            .ref(`/orders/${id}`);
        playersRef.remove();

    };
    onChange = (event) => {

        console.log(event.target.value);
        console.log(event.target.id);
        var orderId = event.target.id;
        var orderStatusValue = event.target.value;
        var orderRid = event
            .target
            .getAttribute('data-rid');
        var emailId = event
            .target
            .getAttribute('data-email');
        var name = event
            .target
            .getAttribute('data-name');
        var username = name
            ? name
            : emailId;

        swal({title: "Are you sure?", text: "Do your really want to  change the status?", icon: "warning", buttons: true, dangerMode: true}).then(willChange => {
            if (willChange) {
                let dbCon = firebase
                    .database()
                    .ref(`/orders/${orderId}`);
                let trackingNumber = '';
                let deliveredBy = '';
                var headers = {
                    'Content-Type': 'application/json'
                }

                if (orderStatusValue == 'Dispatched') {
                    trackingNumber = '6245454784';
                    deliveredBy = 'Fedex';
                }

                let user = {
                    "orderStatus": orderStatusValue,
                    "username": username,
                    "to": emailId,
                    "orderId": orderRid,
                    "trackingNumber": trackingNumber,
                    "deliveredBy": deliveredBy
                };

                axios
                    .post(`http://testingmadesimple.org/training_app/api/Service/sendEmail`, user, {headers: headers})
                    .then(res => {
                        console.log(res);
                        console.log(res);
                    });
                dbCon.update({status: orderStatusValue});
            } else {
                // window
                //     .location
                //     .reload();
            }
        });
    }

    handlePageChange(pageNumber) {
        console.log(`active page is ${pageNumber}`);
        this.setState({activePage: pageNumber});
    }
    render() {
        const pathName = this.props.location.pathname;

        const ordersPath = pathName.substr(8);
      
        return (
            <div>
                <SidebarPage/>

                <div className="all-content-wrapper">
                    <HeaderPage/>

                    <div className="breadcome-area">
                        <div className="container-fluid">
                            <div className="row">
                                <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                    <div className="breadcome-list">
                                        <div className="row">
                                            <div className="col-lg-6 col-md-6 col-sm-6 col-xs-6">
                                                <div className="breadcomb-wp">

                                                    <div className="breadcomb-ctn">
                                                        <h2>Orders</h2>
                                                    </div>
                                                </div>
                                            </div>

                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

                    <div className="product-status mg-b-30">
                        <div className="container-fluid">
                            <div className="row">
                                <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                    <div className="product-status-wrap">
                                        <h4> {ordersPath} Orders List</h4>
                                        <div className="col-md-6">
                                       
                                                            <label className="col-sm-3 control-label">From</label>
                                                            <div className="col-sm-3">
                                                                <input
                                                                    name="validFrom"
                                                                    id="validFrom"
                                                                    size={16}
                                                                    type="date"
                                                                   
                                                                    min={moment(this.state.date).format("YYYY-MM-DD")}
                                                                    onChange={this.onChange}
                                                                    value={this.state.validFrom}
                                                                    className="form-control"/>
                                                                <span
                                                                    style={{
                                                                    color: 'red'
                                                                }}/>
                                                            </div>
                                                       
                                                      
                                                            <label className="col-sm-3 control-label">To</label>
                                                            <div className="col-sm-3">
                                                                <input
                                                                    name="validTo"
                                                                    id="validTo"
                                                                    className="form-control"
                                                                    size={16}
                                                                    type="date"
                                                                    min={moment(this.state.validFrom).format("YYYY-MM-DD")}
                                                                    onChange={this.onChange}
                                                                    value={this.state.validTo}/>
                                                                <span
                                                                    style={{
                                                                    color: 'red'
                                                                }}/>
                                                            </div>
                                                       
                                                        </div>
                                        <CSVLink data={this.state.orderList}>Download me</CSVLink>;

{/* <CSVDownload data={this.state.orderList} target="_blank" />; */}
                                        {/* <div className="add-product">
                            <Link to='/AddMerchantPage'>Add Orders</Link>
                            </div> */}
                                        <table className="table table-bordered paginationTable">
                                            <thead>
                                                <tr>
                                                    <th>S.no</th>
                                                    <th>CustomerName</th>
                                                    <th>Order ID</th>
                                                    <th>Items(Count)</th>
                                                    <th>Status</th>
                                                    <th>Order Date</th>
                                                    <th>Action</th>
                                                </tr>
                                            </thead>
                                            <tbody>
                                                {this.state.orderList && this
                                                    .state
                                                    .orderList
                                                    .map((data, index) => {

                                                        return (

                                                            <tr key={index}>
                                                                <td>{index + 1}</td>
                                                                {/* <td>{data.customerName ===NaN ?"":data.customerName}</td> */}
                                                                <td>{data.customerName && data.customerName}</td>
                                                                <td>{data.orderId}</td>
                                                                <td>{data.items}</td>
                                                                <td>
                                                                    <select
                                                                        name="select"
                                                                        className="form-control pro-edt-select form-control-primary"
                                                                        name="orderStatus"
                                                                        value={data.orderStatus}
                                                                        id={data.fireorderId}
                                                                        data-rid={data.orderId}
                                                                        data-email={data.emailId && data.emailId}
                                                                        data-name={data.customerName && data.customerName}
                                                                        onChange={this.onChange}>
                                                                        <option value={data.status}>{data.status}</option>
                                                                        {this
                                                                            .state
                                                                            .orderStatusList
                                                                            .map((data, index) => {

                                                                                return (
                                                                                    <option value={data.orderStatus} key={index}>{data.orderStatus}</option>
                                                                                )

                                                                            })}

                                                                    </select>

                                                                </td>

                                                                <td>{data.created_on}</td>

                                                                <td>
                                                                    {/* <button data-toggle="tooltip" title="View" className="pd-setting-ed"><i className="fa fa-eye" aria-hidden="true"></i></button> */}

                                                                    <Link to={`/ViewInvoice/${data.fireorderId}`}>
                                                                        <button data-toggle="tooltip" title="Edit" className="pd-setting-ed">
                                                                            <i className="fa fa-eye" aria-hidden="true"></i>
                                                                        </button>
                                                                    </Link>

                                                                </td>
                                                            </tr>
                                                        )
                                                    })}

                                            </tbody>

                                        </table>
                                        {/* <div className="custom-pagination">

                                 <Pagination
                          hideNavigation
                          activePage={this.state.activePage}
                          itemsCountPerPage={10}
                          totalItemsCount={this.state.countPage}
                          pageRangeDisplayed={50}
                          onChange={this.handlePageChange.bind(this)}
                        />
                            </div> */}
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
                <FooterPage/>

            </div>
        );
    }
}

export default OrderListPage;
