import React from "react";
import HeaderPage from "./header";
import FooterPage from "./footer";
import SidebarPage from "./sidebar";
import firebase from '../config';
import {Form} from 'reactstrap';
import FileUploader from "react-firebase-file-uploader";
import SimpleReactValidator from "simple-react-validator";
import ReactQuill from "react-quill";
import 'react-quill/dist/quill.snow.css';
import {Link} from "react-router-dom";

class AddProductPage extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            productName: '',
            category: '',
            subCategory: '',
            gstValue: '',
            pincode: '',
            price: '',
            offerPrice: '',
            sellingPrice: '',
            quantityStock: '',
            packagingCost: '',
            alertQuantity: '',
            uploadImage: '',
            description: '',
            categoryList: [],
            subcategoryList: [],
            GSTList: [],
            avatar: "",
            isUploading: false,
            progress: 0,
            avatarURL: "",
            filenames: [],
            downloadURLs: "",
            uploadProgress: 0,
            cancellation:"",
            error_message:'',
            date:new Date().toLocaleString(),
            validError:true,
        }
       
       
        this.validator = new SimpleReactValidator({
            className: "text-danger",
            validators: {
                passwordvalid: {
                    message: "The :attribute must be at least 6 and at most 30 with 1 numeric,1 special charac" +
                            "ter and 1 alphabet.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /^(?=.*\d)(?=.*[!@#$%^&*])(?=.*[a-z]).{6,30}$/i) && params.indexOf(val) === -1);
                    }
                },
                passwordMismatch: {
                    message: "confirm password must match with password field.",
                    rule: function (val, params, validator) {
                        return document
                            .getElementById("password_input")
                            .value === val
                            ? true
                            : false;
                    }
                },
                AlertquantityStock: {
                    message: "alert quantity should be less than quantity Instock.",
                    rule: function (val, params, validator) {
                        var qty=document.getElementById('quantity_stock').value;
                        
                        if(Number(qty)>Number(val)) {
                            
                            return true;
                        } else{
                            return false;
                        }
                       
                    }
                },
                priceCostValid: {
                    message: "Selling price should be less than original price.",
                    rule: function (val, params, validator) {
                        var qty=document.getElementById('price_cost').value;
                        
                        if(Number(qty)>Number(val)) {
                            
                            return true;
                        } else{
                            return false;
                        }
                       
                    }
                },
                whitespace: {
                    message: "The :attribute not allowed first whitespace   characters.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /[^\s\\]/) && params.indexOf(val) === -1);
                    }
                },
                specialChar: {
                    message: "The :attribute not allowed special   characters.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /^[ A-Za-z0-9_@./#&+-]*$/i) && params.indexOf(val) === -1);
                    }
                },
                specialCharText: {
                    message: "The :attribute may only contain letters, dot and spaces.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /^[ A-Za-z_@./#&+-]*$/i) && params.indexOf(val) === -1);
                    }
                },

                zip: {
                    message: "Invalid Pin Code",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /^(\d{5}(\d{4})?)?$/i) && params.indexOf(val) === -1);
                    }
                },
                website: {
                    message: "The Url should be example.com ",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /(http(s)?:\/\/.)?(www\.)?[-a-zA-Z0-9@:%._\+~#=]{2,256}\.[a-z]{2,6}\b([-a-zA-Z0-9@:%_\+.~#?&//=]*)/g) && params.indexOf(val) === -1);
                    }
                },
                Fax: {
                    message: "Invalid fax number ",
                    rule: function (val, params, validator) {
                        return (validator.helpers.testRegex(val, /^([\w-\.]+)@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.)|(([\w-]+\.)+))([a-zA-Z]{2,4}|[0-9]{1,3})(\]?)$/i) && params.indexOf(val) === -1);
                    }
                }
            }
        });
    }

   

    componentDidMount() {
        this.setState({loading: true});
        var ref = firebase
            .database()
            .ref("category/");

        ref.on('value', snapshot => {
            const data = [];

            snapshot.forEach(childSnapShot => {

                const Categories = {
                    categoryId: childSnapShot
                        .key
                        .toString(),
                    category: childSnapShot
                        .val()
                        .category,
                    created_on: childSnapShot
                        .val()
                        .created_on
                };

                data.push(Categories);
            });

            this.setState({categoryList: data, loading: false});
            console.log(this.state.categoryList);
        });

        var GstData = firebase
            .database()
            .ref("GST/");

        GstData.on('value', snapshot => {
            const data = [];

            snapshot.forEach(childSnapShot => {

                const GSTList = {
                    gstId: childSnapShot
                        .key
                        .toString(),
                    gst: childSnapShot
                        .val()
                        .gst,
                    created_on: childSnapShot
                        .val()
                        .created_on
                };

                data.push(GSTList);
            });

            this.setState({GSTList: data, loading: false});
            console.log(this.state.GSTList);
        });
    }

    onChange = (event) => {

        this.setState({
            [event.target.name]: event.target.value
        });
    };
    productNameChange  = (event) => {
        this.setState({
            [event.target.name]: event.target.value
        });
       
           
            
            var ref = firebase
            .database()
            .ref('products/').orderByChild("productName").equalTo(event.target.value);
            ref.on('value', snapshot => {
                var  user_exist = snapshot.numChildren();
                console.log(user_exist);
           
            if(user_exist>0){
               
               
                this.setState({error_message: "Product Name is already exist",validError:false});
            }else{
                this.setState({error_message: "",validError:true});
               
            }
           
        })
    
       
    };
    setCategoryName = (e) => {
        console.log(e.target.value);

        let selectedCategoryName = e.target.value;

        var ref = firebase
            .database()
            .ref("sub_category")
            .orderByChild("category")
            .equalTo(selectedCategoryName);

        ref.on('value', snapshot => {

            const data = [];

            snapshot.forEach(childSnapShot => {

                const GSTList = {
                    subCategoryId: childSnapShot
                        .key
                        .toString(),
                    subCategory: childSnapShot
                        .val()
                        .subCategory
                };

                data.push(GSTList);
            });

            this.setState({subcategoryList: data,category:selectedCategoryName, loading: false});
            console.log(this.state.subcategoryList);
        });
    

    };
    handleChange = (value) => {
        console.log(value);
        this.setState({description: value})
    };

    handleUploadStart = () => 
    {
     
        this.setState({isUploading: true, uploadProgress: 0});

    }
    handleFrontImageUploadStart = () => this.setState({isUploading: true, uploadProgress: 0, avatarURL: ''});
    handleProgress = progress => this.setState({uploadProgress: progress});

    handleUploadError = error => {
        this.setState({
            isUploading: false
            // Todo: handle error
        });
        console.error(error);
    };
    handleFrontImageUploadSuccess = filename => {
        this.setState({avatar: filename, progress: 100, isUploading: false});
        firebase
            .storage()
            .ref("images")
            .child(filename)
            .getDownloadURL()
            .then(url => this.setState({avatarURL: url}));
    };
    handleUploadSuccess = async filename => {
        const downloadURL = await firebase
            .storage()
            .ref("images")
            .child(filename)
            .getDownloadURL();

        this.setState(oldState => ({
            filenames: [
                ...oldState.filenames,
                filename
            ],
            downloadURLs: [
                ...oldState.downloadURLs,
                downloadURL
            ],
            uploadProgress: 100,
            isUploading: false
        }));
    };

    handleSubmit = (event) => {
        event.preventDefault();
        if (this.validator.allValid() && this.state.validError == true) {
            this.addNotification();
            console.log(this.state.avatar);
            var sessionId = sessionStorage.getItem("RoleId");
            var userName = sessionStorage.getItem("userName");

            let dbCon = firebase
                .database()
                .ref('/products');
            dbCon.push({
                productName: this.state.productName,
                userName: userName,
                category: this.state.category,
                subCategory: this.state.subCategory,
                gstValue: this.state.gstValue,
                pincode: this.state.pincode,
                price: this.state.price,
                cancellation:this.state.cancellation,
                status: "InActive",
                stock:"Available",
                offerPrice: this.state.offerPrice,
                sellingPrice: this.state.sellingPrice,
                quantityStock: this.state.quantityStock,
                packagingCost: this.state.packagingCost,
                alertQuantity: this.state.alertQuantity,
                uploadImage: this.state.downloadURLs,
                frontImage: this.state.avatarURL,
                description: this.state.description,
                created_on: this.state.date,
                sessionId: sessionId
            });
            this
                .props
                .history
                .push("/ProductListPage/Created");
        } else {
            this
                .validator
                .showMessages();
            this.forceUpdate();
        }

    };

    render() {

