            <div>
                <SidebarPage/>

                <div className="all-content-wrapper">
                    <HeaderPage/>

                    <div className="breadcome-area">
                        <div className="container-fluid">
                            <div className="row">
                                <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                    <div className="breadcome-list">
                                        <div className="row">
                                            <div className="col-lg-6 col-md-6 col-sm-6 col-xs-6">
                                                <div className="breadcomb-wp">

                                                    <div className="breadcomb-ctn">
                                                        <h2>Products</h2>
                                                    </div>
                                                </div>
                                            </div>

                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

                    <div className="single-product-tab-area mg-b-30">

                        <div className="single-pro-review-area">
                            <div className="container-fluid">
                                <div className="row">
                                    <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                        <div className="review-tab-pro-inner">

                                            <h4>Add Products</h4>
                                            <div className="product-tab-list tab-pane fade active in">
                                                <Form onSubmit={this.handleSubmit} id="Add-products">

                                                    <div className="row m-b-20">
                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                            <label>Product Name<span style={{color: 'red' }}>*</span></label>
                                                            <input
                                                                type="text"
                                                                className="form-control"
                                                                placeholder=""
                                                                name="productName"
                                                                onChange={this.productNameChange}
                                                                value={this.state.productName}/> {this
                                                                .validator
                                                                .message("Product Name", this.state.productName, "required|whitespace|min:2|max:70")}
                                                        <div className="text-danger">
                                                                {" "}
                                                                {this.state.error_message}
                                                            </div> </div>

                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                            <label>Select Category<span style={{color: 'red' }}>*</span></label>
                                                            <select
                                                              
                                                                className="form-control pro-edt-select form-control-primary"
                                                                name="category"
                                                                onChange={this.setCategoryName}>
                                                                <option>Select Category</option>
                                                                {this
                                                                    .state
                                                                    .categoryList
                                                                    .map((data, index) => {

                                                                        return (
                                                                            <option value={data.category} key={index}>{data.category}</option>
                                                                        )
                                                                        {
                                                                            this
                                                                                .validator
                                                                                .message("Select category", data.category, "required")
                                                                        }

                                                                    })}

                                                            </select>
                                                        </div>
                                                    </div>

                            

                                                    <div className="row m-b-20">

                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                            <label>Select Sub Category<span style={{color: 'red' }}>*</span></label>
                                                            <select
                                                              
                                                                className="form-control pro-edt-select form-control-primary"
                                                                name="subCategory"
                                                                onChange={this.onChange}>
                                                                <option>Select Sub Category</option>
                                                                {this.state.subcategoryList && this
                                                                    .state
                                                                    .subcategoryList
                                                                    .map((data, index) => {

                                                                        return (
                                                                            <option value={data.subCategory} key={index}>{data.subCategory}</option>
                                                                        )
                                                                        {
                                                                            this
                                                                                .validator
                                                                                .message("Select Sub category", data.subCategory, "required")
                                                                        }

                                                                    })}

                                                            </select>
                                                        </div>
                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                            <label>GST<span style={{color: 'red' }}>*</span></label>

                                                            <select
                                                                name="select"
                                                                className="form-control pro-edt-select form-control-primary"
                                                                name="gstValue"
                                                                onChange={this.onChange}>
                                                                <option>Select GST</option>
                                                                {this.state.GSTList && this
                                                                    .state
                                                                    .GSTList
                                                                    .map((data, index) => {

                                                                        return (
                                                                            <option value={data.gst} key={index}>{data.gst}</option>
                                                                        )
                                                                        {
                                                                            this
                                                                                .validator
                                                                                .message("Select GST", data.gst, "required")
                                                                        }
                                                                    })}

                                                            </select>

                                                        </div>

                                                    </div>
                                                   

                                                    <div className="row m-b-20">
                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                            <label>Price<span style={{color: 'red' }}>*</span></label>
                                                            <input
                                                                type="text"
                                                                className="form-control"
                                                                placeholder=""
                                                                name="price"
                                                                id="price_cost"
                                                                onChange={this.onChange}
                                                                value={this.state.price}/> {this
                                                                .validator
                                                                .message("Price Value", this.state.price, "required|numeric|min:1|max:5")}

                                                        </div>
                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                            <label>Selling Price<span style={{color: 'red' }}>*</span></label>
                                                            <input
                                                                type="text"
                                                                className="form-control"
                                                                placeholder=""
                                                                name="sellingPrice"
                                                                onChange={this.onChange}
                                                                value={this.state.sellingPrice}/> {this
                                                                .validator
                                                                .message(" Selling Price", this.state.sellingPrice, "required|priceCostValid|numeric|min:1|max:5")}

                                                        </div>
                                                      
                                                    </div>

                                                    <div className="row m-b-20">
                   
                                                    <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                            <label>Pin Code<span style={{color: 'red' }}>*</span></label>
                                                            <input
                                                                type="text"
                                                                className="form-control"
                                                                placeholder=""
                                                                name="pincode"
                                                                onChange={this.onChange}
                                                                value={this.state.pincode}/> {this
                                                                .validator
                                                                .message("Pin Code", this.state.pincode, "required|numeric|min:5|max:6")}

                                                        </div>
                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                            <label>Quantity Instock<span style={{color: 'red' }}>*</span></label>
                                                            <input
                                                                type="text"
                                                                className="form-control"
                                                                placeholder=""
                                                                name="quantityStock"
                                                                onChange={this.onChange}
                                                                id="quantity_stock"
                                                                value={this.state.quantityStock}/> {this
                                                                .validator
                                                                .message(" Quantity Price", this.state.quantityStock, "required|numeric|min:1|max:5")}

                                                        </div>
                                                    </div>

                                                    <div className="row m-b-20">
                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                            <label>Packaging Cost<span style={{color: 'red' }}>*</span></label>
                                                            <input
                                                                type="text"
                                                                className="form-control"
                                                                placeholder=""
                                                                name="packagingCost"
                                                                onChange={this.onChange}
                                                                value={this.state.packagingCost}/> {this
                                                                .validator
                                                                .message("Packaging Cost", this.state.packagingCost, "required|numeric|min:1|max:5")}

                                                        </div>
                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                            <label>Alert Quantity<span style={{color: 'red' }}>*</span></label>
                                                            <input
                                                                type="text"
                                                                className="form-control"
                                                                placeholder=""
                                                                name="alertQuantity"
                                                                onChange={this.onChange}
                                                                value={this.state.alertQuantity}/> {this
                                                                .validator
                                                                .message("Alert Quantity", this.state.alertQuantity, "required|AlertquantityStock|numeric")}

                                                        </div>

                                                    </div>
                                                    <div className="row m-b-20">
                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">

                                                            <div className="dz-message needsclick download-custom">
                                                                
                                                                <i className="fa fa-cloud-download" aria-hidden="true"></i>
                                                                <h2>Front Image Upload <span className="size_clr">(512x512px)</span><span style={{color: 'red' }}>*</span></h2>

                                                            </div>
                                                            {this.state.isUploading && <p>Progress: {this.state.progress}</p>}
                                                            {/* {this.state.avatarURL && <img src={this.state.avatarURL} />} */}
                                                            {this.state.avatarURL && <img
                                                                src={this.state.avatarURL}
                                                                style={{
                                                                height: "200px",
                                                                width: "200px"
                                                            }}/>}
                                                            <FileUploader
                                                                accept="image/*"
                                                                name="avatar"
                                                                randomizeFilename
                                                                storageRef={firebase
                                                                .storage()
                                                                .ref("images")}
                                                                onUploadStart={this.handleFrontImageUploadStart}
                                                                onUploadError={this.handleUploadError}
                                                                onUploadSuccess={this.handleFrontImageUploadSuccess}
                                                                onProgress={this.handleProgress}
                                                               
                                                                
                                                                /> {this
                                                                .validator
                                                                .message("Front Image Upload", this.state.avatarURL, "required")}

                                                        </div>
                                                       
                                                        
                                                    <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                        <label>Select Cancellation Product<span style={{color: 'red' }}>*</span></label>
                                                        <select
                                                            name="cancellation"
                                                            onChange={this.onChange}
                                                            className="form-control pro-edt-select form-control-primary">
                                                                <option>Select Cancellation</option>
                                                            <option value="Yes">Yes</option>
                                                            <option value="No">No</option>
                                                            {
                                                                            this
                                                                                .validator
                                                                                .message("Select Cancellation", this.state.cancellation, "required")
                                                                        }
                                                        </select>

                                                    </div>


                                               
                                                    </div>

                                                    <div className="row m-b-20">
                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                            <div className="dropzone-pro">
                                                                <div id="dropzone">

                                                                    <div className="dz-message needsclick download-custom">
                                                                        <i className="fa fa-cloud-download" aria-hidden="true"></i>
                                                                        <h2>Upload multiple Product Images<span className="size_clr"> (512x512px)</span><span style={{color: 'red' }}>*</span></h2>

                                                                    </div>

                                                                    {this.state.isUploading && <p>Filenames: {this
                                                                            .state
                                                                            .filenames
                                                                            .join(", ")}</p>}
                                                                    {/* {this.state.avatarURL && <img src={this.state.avatarURL}  style={{height:"200px",width:"250px"}}/>} */}
                                                                    <div>
                                                                        {this.state.downloadURLs&&this.state.downloadURLs
                                                                            .map((downloadURL, i) => {
                                                                                return <img
                                                                                    key={i}
                                                                                    src={downloadURL}
                                                                                    style={{
                                                                                    height: "200px",
                                                                                    width: "250px"
                                                                                }}/>;
                                                                            })}
                                                                    </div>
                                                                    <FileUploader
                                                                        accept="image/*"
                                                                        name="image-uploader-multiple"
                                                                        randomizeFilename
                                                                        storageRef={firebase
                                                                        .storage()
                                                                        .ref("images")}
                                                                        onUploadStart={this.handleUploadStart}
                                                                        onUploadError={this.handleUploadError}
                                                                        onUploadSuccess={this.handleUploadSuccess}
                                                                        onProgress={this.handleProgress}
                                                                        multiple/> {this
                                                                        .validator
                                                                        .message("Upload Product Image", this.state.downloadURLs, "required")}

                                                                </div>
                                                            </div>

                                                        </div>

                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                            <label>Description<span style={{color: 'red' }}>*</span></label>

                                                            {/* <textarea
                                                            className="form-control"
                                                            style={{
                                                            height: "90px"
                                                        }}
                                                            name="description"
                                                            onChange={this.onChange}
                                        s                    value={this.state.description}></textarea> */}

                                                            <ReactQuill
                                                                theme={this.state.theme}
                                                                value={this.state.description}
                                                                name="description"
                                                                placeholder='Enter Text'
                                                                onChange={this.handleChange}
                                                                className="add-new-post__editor mb-1"/> {this
                                                                .validator
                                                                .message("Description", this.state.description, "required")}

                                                        </div>
                                                    </div>

                                                    <div className="row">
                                                        <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                                            <div className="text-center custom-pro-edt-ds">
                                                                <button
                                                                    type="submit"
                                                                    className="btn btn-ctl-bt waves-effect waves-light m-r-10">Save
                                                                </button>
                                                                <Link to="/ProductListPage">
                                                                    <button type="button" className="btn btn-ctl-bt waves-effect waves-light">Cancel
                                                                    </button>
                                                                </Link>
                                                            </div>
                                                        </div>
                                                    </div>
                                                </Form>
                                            </div>

                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
                <FooterPage/>

            </div>
        );
    }
}

export default AddProductPage;
