import React from "react";
import HeaderPage from "./header";
import FooterPage from "./footer";
import SidebarPage from "./sidebar";
import firebase from '../config';
import {Form} from 'reactstrap';
import FileUploader from "react-firebase-file-uploader";
import ReactNotification from "react-notifications-component";
import "react-notifications-component/dist/theme.css";
import SimpleReactValidator from "simple-react-validator";
import ReactQuill from "react-quill";
import 'react-quill/dist/quill.snow.css';
import {Link} from "react-router-dom";

class AddAccountDetails extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            businessName: '',
            gstValue: '',
            tanValue: '',
            signature: '',
            mailingAddress: '',
            pickupAddress: '',
            pincode: '',
            city: '',
            state: '',
            emailAddress: '',
           
            uploadImage: '',
            avatar: "",
            isUploading: false,
            progress: 0,
            avatarURL: "",
            filenames: [],
            downloadURLs: "",
            uploadProgress: 0,
            cancellation:"",

            date: new Date().toLocaleString()
        }
        this.handleChange = this
            .handleChange
            .bind(this);
        this.addNotification = this
            .addNotification
            .bind(this);
        this.notificationDOMRef = React.createRef();
        this.validator = new SimpleReactValidator({
            className: "text-danger",
            validators: {
                passwordvalid: {
                    message: "The :attribute must be at least 6 and at most 30 with 1 numeric,1 special charac" +
                            "ter and 1 alphabet.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /^(?=.*\d)(?=.*[!@#$%^&*])(?=.*[a-z]).{6,30}$/i) && params.indexOf(val) === -1);
                    }
                },
                passwordMismatch: {
                    message: "confirm password must match with password field.",
                    rule: function (val, params, validator) {
                        return document
                            .getElementById("password_input")
                            .value === val
                            ? true
                            : false;
                    }
                },
                whitespace: {
                    message: "The :attribute not allowed first whitespace   characters.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /[^\s\\]/) && params.indexOf(val) === -1);
                    }
                },
                specialChar: {
                    message: "The :attribute not allowed special   characters.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /^[ A-Za-z0-9_@./#&+-]*$/i) && params.indexOf(val) === -1);
                    }
                },
                specialCharText: {
                    message: "The :attribute may only contain letters, dot and spaces.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /^[ A-Za-z_@./#&+-]*$/i) && params.indexOf(val) === -1);
                    }
                },

                zip: {
                    message: "Invalid Pin Code",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /^(\d{5}(\d{4})?)?$/i) && params.indexOf(val) === -1);
                    }
                },
                website: {
                    message: "The Url should be example.com ",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /(http(s)?:\/\/.)?(www\.)?[-a-zA-Z0-9@:%._\+~#=]{2,256}\.[a-z]{2,6}\b([-a-zA-Z0-9@:%_\+.~#?&//=]*)/g) && params.indexOf(val) === -1);
                    }
                },
                Fax: {
                    message: "Invalid fax number ",
                    rule: function (val, params, validator) {
                        return (validator.helpers.testRegex(val, /^([\w-\.]+)@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.)|(([\w-]+\.)+))([a-zA-Z]{2,4}|[0-9]{1,3})(\]?)$/i) && params.indexOf(val) === -1);
                    }
                }
            }
        });
    }

   

   

    onChange = (event) => {

        this.setState({
            [event.target.name]: event.target.value
        });
    };
    setCategoryName = (e) => {
        console.log(e.target.value);

        let selectedCategoryName = e.target.value;

        var ref = firebase
            .database()
            .ref("sub_category")
            .orderByChild("category")
            .equalTo(selectedCategoryName);

        ref.on('value', snapshot => {

            const data = [];

            snapshot.forEach(childSnapShot => {

                const GSTList = {
                    subCategoryId: childSnapShot
                        .key
                        .toString(),
                    subCategory: childSnapShot
                        .val()
                        .subCategory
                };

                data.push(GSTList);
            });

            this.setState({subcategoryList: data,category:selectedCategoryName, loading: false});
            console.log(this.state.subcategoryList);
        });
    

    };
    handleChange = (value) => {
        console.log(value);
        this.setState({description: value})
    };

    handleUploadStart = () => this.setState({isUploading: true, uploadProgress: 0});
    handleFrontImageUploadStart = () => this.setState({isUploading: true, uploadProgress: 0, avatarURL: ''});
    handleProgress = progress => this.setState({uploadProgress: progress});

    handleUploadError = error => {
        this.setState({
            isUploading: false
            // Todo: handle error
        });
        console.error(error);
    };
    handleFrontImageUploadSuccess = filename => {
        this.setState({avatar: filename, progress: 100, isUploading: false});
        firebase
            .storage()
            .ref("images")
            .child(filename)
            .getDownloadURL()
            .then(url => this.setState({avatarURL: url}));
    };
    handleUploadSuccess = async filename => {
        const downloadURL = await firebase
            .storage()
            .ref("images")
            .child(filename)
            .getDownloadURL();

        this.setState(oldState => ({
            filenames: [
                ...oldState.filenames,
                filename
            ],
            downloadURLs: [
                ...oldState.downloadURLs,
                downloadURL
            ],
            uploadProgress: 100,
            isUploading: false
        }));
    };

    handleSubmit = (event) => {
        event.preventDefault();
        if (this.validator.allValid()) {
            this.addNotification();
            console.log(this.state.avatar);
            var sessionId = sessionStorage.getItem("RoleId");
            var userName = sessionStorage.getItem("userName");

            let dbCon = firebase
                .database()
                .ref('/products');
            dbCon.push({
                productName: this.state.productName,
                userName: userName,
                category: this.state.category,
                subCategory: this.state.subCategory,
                gstValue: this.state.gstValue,
                pincode: this.state.pincode,
                price: this.state.price,
                cancellation:this.state.cancellation,
                status: "InActive",
                stock:"Available",
                offerPrice: this.state.offerPrice,
                sellingPrice: this.state.sellingPrice,
                quantityStock: this.state.quantityStock,
                packagingCost: this.state.packagingCost,
                alertQuantity: this.state.alertQuantity,
                uploadImage: this.state.downloadURLs,
                frontImage: this.state.avatarURL,
                description: this.state.description,
                created_on: this.state.date,
                sessionId: sessionId
            });
            this
                .props
                .history
                .push("/ProductListPage");
        } else {
            this
                .validator
                .showMessages();
            this.forceUpdate();
        }

    };

    render() {

        return (
            <div>
                <SidebarPage/>

                <div className="all-content-wrapper">
                    <HeaderPage/>

                    <div className="breadcome-area">
                        <div className="container-fluid">
                            <div className="row">
                                <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                    <div className="breadcome-list">
                                        <div className="row">
                                            <div className="col-lg-6 col-md-6 col-sm-6 col-xs-6">
                                                <div className="breadcomb-wp">

                                                    <div className="breadcomb-ctn">
                                                        <h2>Products</h2>
                                                    </div>
                                                </div>
                                            </div>

                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

                    <div className="single-product-tab-area mg-b-30">

                        <div className="single-pro-review-area">
                            <div className="container-fluid">
                                <div className="row">
                                    <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                        <div className="review-tab-pro-inner">

                                            <h4>Add Products</h4>
                                            <ReactNotification ref={this.notificationDOMRef}/>
                                            <div className="product-tab-list tab-pane fade active in">
                                                <Form onSubmit={this.handleSubmit} id="Add-products">

                                                    <div className="row m-b-20">
                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                        <label>Business name</label>
                                                            <input
                                                                type="text"
                                                                className="form-control"
                                                                placeholder=""
                                                                name="productName"
                                                                onChange={this.onChange}
                                                                value={this.state.productName}/> {this
                                                                .validator
                                                                .message("Product Name", this.state.productName, "required|whitespace|min:2|max:70")}
                                                        </div>

                                                        
                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                            <label>GST</label>

                                                            <input
                                                                type="text"
                                                                className="form-control"
                                                                placeholder=""
                                                                name="gst"
                                                                onChange={this.onChange}
                                                                value={this.state.gst}/> {this
                                                                .validator
                                                                .message("gst", this.state.gst, "required|whitespace|min:2|max:70")}

                                                        </div>
                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                            <label>GST</label>

                                                            <input
                                                                type="text"
                                                                className="form-control"
                                                                placeholder=""
                                                                name="gst"
                                                                onChange={this.onChange}
                                                                value={this.state.gst}/> {this
                                                                .validator
                                                                .message("gst", this.state.gst, "required|whitespace|min:2|max:70")}

                                                        </div>

                                                    </div>
                                                    <div className="row m-b-20">
                                                    <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                        <label>Select Cancellation Product</label>
                                                        <select
                                                            name="cancellation"
                                                            onChange={this.onChange}
                                                            className="form-control pro-edt-select form-control-primary">
                                                                <option>Select Cancellation</option>
                                                            <option value="Yes">Yes</option>
                                                            <option value="No">No</option>
                                                            {
                                                                            this
                                                                                .validator
                                                                                .message("Select Cancellation", this.state.cancellation, "required")
                                                                        }
                                                        </select>

                                                    </div>


                                                </div>

                                                    <div className="row m-b-20">
                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                            <label>Price</label>
                                                            <input
                                                                type="text"
                                                                className="form-control"
                                                                placeholder=""
                                                                name="price"
                                                                onChange={this.onChange}
                                                                value={this.state.price}/> {this
                                                                .validator
                                                                .message("Price Value", this.state.price, "required|numeric|min:1|max:5")}

                                                        </div>

                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                            <label>Offer Price</label>
                                                            <input
                                                                type="text"
                                                                className="form-control"
                                                                placeholder=""
                                                                name="offerPrice"
                                                                onChange={this.onChange}
                                                                value={this.state.offerPrice}/> {this
                                                                .validator
                                                                .message(" Offer Price", this.state.offerPrice, "numeric|min:1|max:5")}

                                                        </div>
                                                    </div>

                                                    <div className="row m-b-20">
                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                            <label>Selling Price</label>
                                                            <input
                                                                type="text"
                                                                className="form-control"
                                                                placeholder=""
                                                                name="sellingPrice"
                                                                onChange={this.onChange}
                                                                value={this.state.sellingPrice}/> {this
                                                                .validator
                                                                .message(" Selling Price", this.state.sellingPrice, "required|numeric|min:1|max:5")}

                                                        </div>

                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                            <label>Quantity Instock</label>
                                                            <input
                                                                type="text"
                                                                className="form-control"
                                                                placeholder=""
                                                                name="quantityStock"
                                                                onChange={this.onChange}
                                                                value={this.state.quantityStock}/> {this
                                                                .validator
                                                                .message(" Quantity Price", this.state.quantityStock, "required|numeric|min:1|max:5")}

                                                        </div>
                                                    </div>

                                                    <div className="row m-b-20">
                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                            <label>Packaging Cost</label>
                                                            <input
                                                                type="text"
                                                                className="form-control"
                                                                placeholder=""
                                                                name="packagingCost"
                                                                onChange={this.onChange}
                                                                value={this.state.packagingCost}/> {this
                                                                .validator
                                                                .message("Packaging Cost", this.state.packagingCost, "required|numeric|min:1|max:5")}

                                                        </div>
                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                            <label>Alert Quantity</label>
                                                            <input
                                                                type="text"
                                                                className="form-control"
                                                                placeholder=""
                                                                name="alertQuantity"
                                                                onChange={this.onChange}
                                                                value={this.state.alertQuantity}/> {this
                                                                .validator
                                                                .message("Alert Quantity", this.state.alertQuantity, "required|numeric|min:1|max:5")}

                                                        </div>

                                                    </div>
                                                    <div className="row m-b-20">
                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">

                                                            <div className="dz-message needsclick download-custom">
                                                                <i className="fa fa-cloud-download" aria-hidden="true"></i>
                                                                <h2>Front Image Upload</h2>

                                                            </div>
                                                            {this.state.isUploading && <p>Progress: {this.state.progress}</p>}
                                                            {/* {this.state.avatarURL && <img src={this.state.avatarURL} />} */}
                                                            {this.state.avatarURL && <img
                                                                src={this.state.avatarURL}
                                                                style={{
                                                                height: "200px",
                                                                width: "250px"
                                                            }}/>}
                                                            <FileUploader
                                                                accept="image/*"
                                                                name="avatar"
                                                                randomizeFilename
                                                                storageRef={firebase
                                                                .storage()
                                                                .ref("images")}
                                                                onUploadStart={this.handleFrontImageUploadStart}
                                                                onUploadError={this.handleUploadError}
                                                                onUploadSuccess={this.handleFrontImageUploadSuccess}
                                                                onProgress={this.handleProgress}/> {this
                                                                .validator
                                                                .message("Front Image Upload", this.state.avatarURL, "required")}

                                                        </div>
                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                            <label>Pin Code</label>
                                                            <input
                                                                type="text"
                                                                className="form-control"
                                                                placeholder=""
                                                                name="pincode"
                                                                onChange={this.onChange}
                                                                value={this.state.pincode}/> {this
                                                                .validator
                                                                .message("Pin Code", this.state.pincode, "required|zip|min:5|max:6")}

                                                        </div>

                                                    </div>

                                                    <div className="row m-b-20">
                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                            <div className="dropzone-pro">
                                                                <div id="dropzone">

                                                                    <div className="dz-message needsclick download-custom">
                                                                        <i className="fa fa-cloud-download" aria-hidden="true"></i>
                                                                        <h2>Upload multiple Product Images</h2>

                                                                    </div>

                                                                    {this.state.isUploading && <p>Filenames: {this
                                                                            .state
                                                                            .filenames
                                                                            .join(", ")}</p>}
                                                                    {/* {this.state.avatarURL && <img src={this.state.avatarURL}  style={{height:"200px",width:"250px"}}/>} */}
                                                                    <div>
                                                                        {this.state.downloadURLs&&this.state.downloadURLs
                                                                            .map((downloadURL, i) => {
                                                                                return <img
                                                                                    key={i}
                                                                                    src={downloadURL}
                                                                                    style={{
                                                                                    height: "200px",
                                                                                    width: "250px"
                                                                                }}/>;
                                                                            })}
                                                                    </div>
                                                                    <FileUploader
                                                                        accept="image/*"
                                                                        name="image-uploader-multiple"
                                                                        randomizeFilename
                                                                        storageRef={firebase
                                                                        .storage()
                                                                        .ref("images")}
                                                                        onUploadStart={this.handleUploadStart}
                                                                        onUploadError={this.handleUploadError}
                                                                        onUploadSuccess={this.handleUploadSuccess}
                                                                        onProgress={this.handleProgress}
                                                                        multiple/> {this
                                                                        .validator
                                                                        .message("Upload Product Image", this.state.downloadURLs, "required")}

                                                                </div>
                                                            </div>

                                                        </div>

                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                            <label>Description</label>

                                                            {/* <textarea
                                                            className="form-control"
                                                            style={{
                                                            height: "90px"
                                                        }}
                                                            name="description"
                                                            onChange={this.onChange}
                                        s                    value={this.state.description}></textarea> */}

                                                            <ReactQuill
                                                                theme={this.state.theme}
                                                                value={this.state.description}
                                                                name="description"
                                                                placeholder='Enter Text'
                                                                onChange={this.handleChange}
                                                                className="add-new-post__editor mb-1"/> {this
                                                                .validator
                                                                .message("Description", this.state.description, "required")}

                                                        </div>
                                                    </div>

                                                    <div className="row">
                                                        <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                                            <div className="text-center custom-pro-edt-ds">
                                                                <button
                                                                    type="submit"
                                                                    className="btn btn-ctl-bt waves-effect waves-light m-r-10">Save
                                                                </button>
                                                                <Link to="/ProductListPage">
                                                                    <button type="button" className="btn btn-ctl-bt waves-effect waves-light">Cancel
                                                                    </button>
                                                                </Link>
                                                            </div>
                                                        </div>
                                                    </div>
                                                </Form>
                                            </div>

                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
                <FooterPage/>

            </div>
        );
    }
}

export default AddAccountDetails;
