import React from "react";
import {Link} from "react-router-dom";
import { Breadcrumb, BreadcrumbItem } from 'reactstrap';

import HeaderPage from "./header";
import FooterPage from "./footer";
import SidebarPage from "./sidebar";
import firebase from '../config';
import Doc from './DocService';
import PdfContainer from './PdfContainer';

class ViewInvoice extends React.Component {
    constructor(props) {
        super(props);
        this.state = {

            invoiceList: {},
            shipping: {},
            cartDataItems: [],
            date: new Date().toLocaleString(),
            ShippingValue: '',

            totalSellingPrice: ''
        }

    }
    componentDidMount() {

      

        const {orderID} = this.props.match.params;
        console.log(orderID);
        let thiss = this;
        var ref = firebase
            .database()
            .ref(`orders/${orderID}`);
            ref.on('value', childSnapShot => {

            console.log(childSnapShot);
            const orders = {
                orderId: orderID,
                cartDataItems: childSnapShot
                    .val()
                    .cartDataItems,
                invoiceId: childSnapShot
                    .val()
                    .orderId,
                customerName: childSnapShot
                    .val()
                    .name,
                items: childSnapShot
                    .val()
                    .items,
                shipping: childSnapShot
                    .val()
                    .shipping,
                status: childSnapShot
                    .val()
                    .status,
                subTotal: childSnapShot
                    .val()
                    .subTotal,
                totalDiscount: childSnapShot
                    .val()
                    .totalDiscount,
                totalGST: childSnapShot
                    .val()
                    .totalGST,
                totalAmount: childSnapShot
                    .val()
                    .totalAmount,
                GrandTotal: parseFloat(childSnapShot.val().totalAmount) + parseFloat(childSnapShot.val().totalDiscount) + parseFloat(childSnapShot.val().totalGST),
                created_on: childSnapShot
                    .val()
                    .created_on
            };

            thiss.setState({
                invoiceList: orders,
                shipping: childSnapShot
                    .val()
                    .shipping,
                cartDataItems: childSnapShot
                    .val()
                    .cartDataItems
            }, () => {
                console.log(thiss.state.invoiceList, 'viewInvoice');
            });

        });

        this.shippingCost();
    }
    shippingCost() {
        this.setState({loading: false});
        var ref = firebase
            .database()
            .ref(`ShippingCharges`);
        const data = [];
    
        ref.on('value', snapshot => {
    
            console.log(snapshot.val());
            snapshot.forEach(childSnapShot => {
                const GSTData = {
                    cmsId: childSnapShot
                        .key
                        .toString(),
                    ShippingValue: childSnapShot
                        .val()
                        .ShippingValue
                };
                data.push(GSTData);
            });
    
            this.setState({ShippingValue: data[0].ShippingValue, loading: true});
            console.log(this.state.ShippingValue);
        });
    }
   
    createPdf = (html) => Doc.createPdf(html);
    render() {
        let totalcart = 0;
        let PackingCost = 0;
        var packingCostVal = 0;
        return (
            <div>
                <SidebarPage/>

                <div className="all-content-wrapper">
                    <HeaderPage/>

                    <div className="breadcome-area">
                        <div className="container-fluid">
                            <div className="row">
                                <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                    <div className="breadcome-list">
                                        <div className="row">
                                            <div className="col-lg-6 col-md-6 col-sm-6 col-xs-6">
                                                <div className="breadcomb-wp">

                                                    <div className="breadcomb-ctn">
                                                        <h2>Invoice
                                                        </h2>
                                                    </div>
                                                </div>
                                            </div>

                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                    <Breadcrumb>
        <BreadcrumbItem><Link to="/users">Users</Link></BreadcrumbItem>
       
        <BreadcrumbItem active>User Invoice</BreadcrumbItem>
      </Breadcrumb>
      <PdfContainer createPdf={this.createPdf}>
     
      
<div id="singlePage">
                    <div className="table-responsive" >
                        <table
                            className="table"
                            cellPadding="0"
                            cellSpacing="0"
                            style={{
                            border: "4px solid #813802",
                            borderCollapse: "separate"
                        }}>

                            <tbody>

                                <tr>

                                    <td
                                        colSpan="2"
                                        width="100%"
                                        style={{
                                        textAlign: "center",
                                        padding: "15px",
                                        borderBottom: "solid 1px #dedede"
                                    }}>

                                        <h1>Invoice</h1>
                                        <h3>Order ID : {this.state.invoiceList.invoiceId}</h3>
                                    </td>

                                </tr>

                                <tr>
                                    <td
                                        width="50%"
                                        style={{
                                        color: "#000",
                                        padding: "15px",
                                        borderBottom: "solid 1px #dedede"
                                    }}>
                                        <h6>Shipping Address</h6>
                                        <p>
                                            <b>{this.state.shipping.firstName +" "+this.state.shipping.lastName }</b>
                                        </p>
                                        <p>
                                            <b>{this.state.shipping.address}</b>
                                        </p>
                                        <p>
                                            <b>{this.state.shipping.cityName}</b>,
                                            <b>{this.state.shipping.stateName}</b>
                                        </p>
                                        <p>
                                            <b>{this.state.shipping.pincode}</b>,
                                            <b>{this.state.shipping.countryName}</b>
                                        </p>
                                        <p
                                            style={{
                                            color: "#000"
                                        }}>Landmark :{this.state.shipping.landMark}</p>
                                    </td>
                                </tr>

                                <tr>
                                    <td
                                        colSpan="2"
                                        style={{
                                        color: "#000",
                                        padding: "15px",
                                        borderBottom: "solid 1px #dedede"
                                    }}>

                                        <div class="table-content table-responsive">

                                            <table class="table table-bordered">
                                                <thead>
                                                    <tr>
                                                        <th class="li-product-thumbnail">S.No</th>
                                                        {/* <th class="li-product-thumbnail">Image</th> */}
                                                        <th class="cart-product-name">Name</th>
                                                        <th class="li-product-price">Unit Price</th>
                                                        <th class="li-product-quantity">Quantity</th>
                                                        <th className="li-product-quantity">GST</th>
                                                        <th class="li-product-subtotal">Ordered On</th>
                                                        <th class="li-product-subtotal">Sub Total</th>

                                                    </tr>
                                                </thead>
                                                <tbody>

                                                    {this.state.cartDataItems && this
                                                        .state
                                                        .cartDataItems
                                                        .map((data, index) => {
                                                            var gstCaluculate = (data.sellingPrice * data.gstValue / 100).toFixed(2);

                                                        var totalGSTVal = ((data.quantity) * (data.sellingPrice)) + ((data.quantity) * gstCaluculate);
                                                        packingCostVal = data.quantity * data.packagingCost;
                                                        console.log(totalGSTVal);
                                                        totalcart += (totalGSTVal);
                                                        PackingCost += packingCostVal;

                                                            return (
                                                                <tr key={index}>
                                                                    <td>{index + 1}</td>
                                                                    {/* <td class="img-product"><img src={data.uploadImage} width="70px"/></td> */}
                                                                    <td class="">{data.productName}</td>
                                                                    <td>
                                                                        <span class="amount">
                                                                            <i class="fa fa-inr" aria-hidden="true"></i>{data.sellingPrice}</span>
                                                                    </td>
                                                                    <td class="quantity">{data.quantity}</td>
                                                                    <td className="product-subtotal">
                                                                    <span className="amount">
                                                                        <i className="fa fa-inr" aria-hidden="true"></i>
                                                                        {parseFloat((data.quantity) * gstCaluculate).toFixed(2)}
                                                                        ({(data.gstValue)}%)
                                                                    </span>
                                                                </td>
                                                                    <td>{data.created_on}</td>
                                                                    <td>
                                                                    
                                                                    {parseFloat(((data.quantity) * (data.sellingPrice)) + ((data.quantity) * gstCaluculate)).toFixed(2)}</td>
                                                                </tr>

                                                            )
                                                        })}

                                                </tbody>
                                            </table>

                                        </div>

                                    </td>
                                </tr>

                                <tr>
                                    <td
                                        align="right"
                                        style={{
                                        color: "#000",
                                        padding: "15px",
                                        borderBottom: "solid 1px #dedede"
                                    }}
                                        colSpan="2">
                                        <p>Subtotal: Rs.{parseFloat(totalcart).toFixed(2)}
                                        </p>
                                        <p>Shipping charges: Rs.{parseInt(this.state.ShippingValue).toFixed(2)}</p>
                                        
                                        <h6>Order Total: Rs.{(totalcart + parseInt(this.state.ShippingValue)).toFixed(2)}</h6>
                                    </td>

                                </tr>

                                <tr>
                                    <td
                                        colSpan="2"
                                        style={{
                                        textAlign: "left",
                                        padding: "15px"
                                    }}>
                                        <p><img src="./images/logo2.png" alt=""/></p>
                                        <p><b>Gootes Essen India Pvt Ltd </b><br/>
                                            <b>Address:</b>
                                            17, 7th a cross, ganesh nagar layout,<br/> virupakshapura,Bangalore, Karnataka <br/>
                                            India 560097<br/>
                                            <b>Email:</b>
                                            support@gootes.com<br/>
                                            <b>Monday - Friday:</b>
                                            9:30 a.m. - 6:30 p.m.</p>
                                    </td>
                                </tr>
                            </tbody>

                        </table>

                    </div>
                </div>
                </PdfContainer>
                </div>
                <FooterPage/>

            </div>
        );
    }
}
