import SidebarPage from "./sidebar";
import firebase from '../config';
import SimpleReactValidator from "simple-react-validator";
//import FileUploader from "react-firebase-file-uploader";

class AddLocations extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            location_Name: '',
            adddress1:'',
            adddress2:'',
            land_Mark: '',
            city: '',
            pincode: '',
            contact_Person_Name: '',
            contact_Number: '',
            email:'',
            upload_Image:'',
            google_Map:'',
            rental_Deposit_Amount:'',
            rent_Per_Month:'',
            electricity_Per_Month:'',
            wifi_Per_Month:'',
            additional_Expenses:'',
            notes:'',
            isUploading: false,


            error_message: '',
            date: new Date().toLocaleString(),
            validError: true,
            avatar: "",
            progress: 0,
            avatarURL: "",
            employer_sevice_message:'',
            
          
          
           
