import React from "react";
import { Link } from "react-router-dom";
//import FadeIn from "react-fade-in";
//import axios from "axios";
import HeaderPage from "./header";
import FooterPage from "./footer";
// import HomePage from "./home";
import SidebarPage from "./sidebar";
import firebase from '../config';
//import Pagination from "react-js-pagination";
//import swal from 'sweetalert';




class EmployeeList extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
        first_name: '',
            last_name: '',
            email: '',
            designation: '',
            mobile_number: '',
            department: '',
            location: '',
            uploadImage: '',
            address: '',
            phone_emergency:'',
            reporting_manager:'',
            EmployeesList: [],
           
            isUploading: false,
            error_message: '',
            date: new Date().toLocaleString(),
            validError: true,
        loading: false,
        count: 1,
        activePage: "1",
        countPage: "",
        EmployeesList: [],
    };
   

}

componentDidMount(){
    var ref=firebase.database().ref('Employees/');
  ref.once("value",snapshot =>{
   const data =[];
    console.log(snapshot.val());
    snapshot.forEach(element => {
      const usersData={
        employeeID: element.key.toString(),
        
        first_name:element.val().first_name,
        last_name:element.val().last_name,
        email:element.val().email,
        designation:element.val().designation,
        mobile_number:element.val().mobile_number,
        department:element.val().department,
        location:element.val().location,
        address:element.val().address,
        reporting_manager:element.val().reporting_manager,
        date:element.val().date,

       
      }
      data.unshift(usersData);
    });
   
    this.setState({Employees:data},()=>{
      console.log(this.state.Employees,'Employees');
    });
  })
  }

handlePageChange(pageNumber) {
    console.log(`active page is ${pageNumber}`);
    this.setState({ activePage: pageNumber });
  }
  render() {
    

    return (
      <div>
     <SidebarPage/>
   
    <div className="all-content-wrapper">
    <HeaderPage/>
   
          
            <div className="breadcome-area">
                <div className="container-fluid">
                    <div className="row">
                        <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                            <div className="breadcome-list">
                                <div className="row">
                                    <div className="col-lg-6 col-md-6 col-sm-6 col-xs-6">
                                        <div className="breadcomb-wp">
                                        
                                            <div className="breadcomb-ctn">
                                            <h2>Employees</h2>
                                                </div>
                                        </div>
                                    </div>
                          
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
      
     
            <div className="product-status mg-b-30">
            <div className="container-fluid">
                <div className="row">
                    <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                        <div className="product-status-wrap">
                            <h4>Employees List</h4>
                            <div className="add-product">
                            <Link to='/AddMerchantPage'>Add Employee</Link>
                            </div>
                            <table className="table table-bordered paginationTable">
                            <thead>
                                <tr>
                                    <th>S.no</th>
                                    <th>First Name</th>
                                    <th>Last Name</th>
                                    <th>Email</th>
									<th>Designation</th>
                                    <th>Mobile Number</th>
                                    <th>DepartMent</th>
                                    <th>Location</th>
                                    <th>Address</th>
                                    <th>Reporting Manager</th>
									<th>Date</th>
                                    <th>Action</th>
                                </tr>
								</thead>
                                            <tbody>
                                            {this.state.Employees && this.state.Employees.map((data, index) => {
                                                       

                                                        return (
                                                           
                                                                <tr key={index}>
                                                                    <td>{index + 1}</td>
                                                                    <td>{data.first_name}</td>
                                                                    <td>{data.last_name}</td>
                                                                    <td>{data.email}</td>
                                                                    <td>{data.designation}</td>
                                                                    <td>{data.mobile_number}</td>
                                                                    <td>{data.department}</td>
                                                                    <td>{data.location}</td>
                                                                    <td>{data.address}</td>
                                                                    <td>{data.reporting_manager}</td>
                                                                    <td>{data.date}</td>

                                                                    <td>
                                                                    {/* <button data-toggle="tooltip" title="View" className="pd-setting-ed"><i className="fa fa-eye" aria-hidden="true"></i></button> */}

                                                                    <Link to={`/ViewInvoice/${data.orderId}`}>
                                         <button data-toggle="tooltip" title="Edit" className="pd-setting-ed"><i className="fa fa-pencil-square-o" aria-hidden="true"></i>&nbsp;View</button>
                                         </Link>								
                     
                                         </td>
                                                                </tr>)
                                                                }
                                                    )}

                                           
								
                                
                                </tbody>
										
                               
							
							</table>
                            {/* <div className="custom-pagination">
							
                                 <Pagination
                          hideNavigation
                          activePage={this.state.activePage}
                          itemsCountPerPage={10}
                          totalItemsCount={this.state.countPage}
                          pageRangeDisplayed={50}
                          onChange={this.handlePageChange.bind(this)}
                        />
                            </div> */}
                        </div>
                    </div>
                </div>
            </div>
        </div>
        </div>
         <FooterPage/>
    
      </div>
    );
  }
}

export default EmployeeList;
