import React from "react";
import {Link} from "react-router-dom";
import HeaderPage from "./header";
import FooterPage from "./footer";
import SidebarPage from "./sidebar";
import firebase from '../config';
import swal from 'sweetalert';
import $ from 'jquery';
$.DataTable = require('datatables.net');

class DepartmentList extends React.Component {
    constructor(props) {
        super(props);
        this.datatable = null;
        this.state = {
            departmentList: [],
            loading: false,
            count: 1,
            activePage: "1",
            countPage: "",
            usePermList: ""
        };

    }
   
    componentDidMount() {
        this.setState({loading: true});
        var ref = firebase
            .database()
            .ref("departments/");

        ref.on('value', snapshot => {
            const data = [];
            // console.log(snapshot); console.log(snapshot.val);

            snapshot.forEach(childSnapShot => {

                const Categories = {
                    locationId: childSnapShot
                        .key
                        .toString(),
                   
                    department: childSnapShot
                        .val()
                        .department,
                    created_on: childSnapShot
                        .val()
                        .created_on
                };

                data.unshift(Categories);
            });

            this.setState({departmentList: data, countPage: data.length, loading: false});
            console.log(this.state.departmentList);
            // this.$el = $(this.el);

            this.interval = setTimeout(() => $('.paginationTable').DataTable(), 1000);
        });

    }
    componentWillUnmount() {
        //this.dataTable.destroy(true);
        clearTimeout(this.interval);
    }
    deleteItem = id => {

        swal({title: "Are you sure?", text: "Do your really want to remove?", icon: "warning", buttons: true, dangerMode: true}).then(willDelete => {
            if (willDelete) {
                console.log(id);
                var playersRef = firebase
                    .database()
                    .ref(`/departments/${id}`);
                playersRef.remove();

                this
                    .props
                    .history
                    .push("/DepartmentList/Deleted");
            } else {}
            // window.location.reload("/Home");

        });

    };

    render() {

        var pathname = this
            .props
            .location
            .pathname
            .split("/");
        var alertName = pathname[2];
        return (
            <div>
                <SidebarPage/>

                <div className="all-content-wrapper">
                    <HeaderPage/>

                    <div className="breadcome-area">
                        <div className="container-fluid">
                            <div className="row">
                                <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                    <div className="breadcome-list">
                                        <div className="row">
                                            <div className="col-lg-6 col-md-6 col-sm-6 col-xs-6">
                                                <div className="breadcomb-wp">

                                                    <div className="breadcomb-ctn">
                                                        <h2>Departments</h2>

                                                    </div>

                                                </div>
                                            </div>

                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

                    <div className="product-status mg-b-30">
                        <div className="container-fluid">
                            <div className="row">
                                <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                    <div className="product-status-wrap">
                                        <h4>Departments List</h4>

                                        <div className="add-product">
                                                    <Link to='/AddDepartment'>Add Department</Link>
                                                </div>
                                          
                                        {alertName
                                            ? <div
                                                    className="alert alert-success display-none"
                                                    style={{
                                                    display: "block"
                                                }}>
                                                    <a data-dismiss="alert" href="#" aria-hidden="true" className="close">Ã—</a>
                                                    Department {alertName}
                                                    Successfully
                                                </div>
                                            : ''}
                                        <table className="table table-bordered paginationTable">
                                            <thead>
                                                <tr>
                                                    <th>S.No</th>
                                                    <th>Department Name</th>
                                                   
                                                    <th>Created On</th>
                                                   <th>Action</th>
                                                      

                                                </tr>
                                            </thead>
                                            <tbody>

                                                {this
                                                    .state
                                                    .departmentList
                                                    .map((data, index) => {
                                                   

                                                        return (
                                                            <tr key={index}>
                                                                <td>{index + 1}</td>
                                                                <td>{data.department}</td>
                                                              
                                                                <td>{data.created_on}
                                                                </td>
                                                               <td>

                                                               <Link to={`/EditDepartment/${data.locationId}`}>
                                                                                <button data-toggle="tooltip" title="Edit" className="pd-setting-ed">
                                                                                    <i className="fa fa-pencil-square-o" aria-hidden="true"></i>
                                                                                </button>
                                                                            </Link>

                                                                            <button
                                                                                data-toggle="tooltip"
                                                                                title="Trash"
                                                                                className="pd-setting-ed"
                                                                                data-id={data.locationId}
                                                                                onClick={this
                                                                                .deleteItem
                                                                                .bind(this, data.locationId)}>
                                                                                <i className="fa fa-trash-o" aria-hidden="true"></i>
                                                                            </button>
                                                                        </td>
                                                                   

                                                            </tr>
                                                        )

                                                    })}

                                            </tbody>
                                        </table>

                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

                </div>

                <FooterPage/>

            </div>
        );
    }
}

export default DepartmentList;
