import React from "react";
import {Form} from 'reactstrap';
import {Link} from "react-router-dom";
import HeaderPage from "./header";
import FooterPage from "./footer";
import SidebarPage from "./sidebar";
import firebase from '../config';
import SimpleReactValidator from "simple-react-validator";
//import FileUploader from "react-firebase-file-uploader";

class AddLocations extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            location_Name: '',
            adddress1:'',
            adddress2:'',
            land_Mark: '',
            city: '',
            pincode: '',
            contact_Person_Name: '',
            contact_Person_Phone: '',
            email:'',
            upload_Photo:'',
            google_Map:'',
            rental_Deposit_Amount:'',
            rent_Per_Month:'',
            electricity_Per_Month:'',
            wifi_Per_Month:'',
            additional_Expenses:'',
            notes:'',
            
            error_message: '',
            date: new Date().toLocaleString(),
            validError: false,
           
        }

        this.validator = new SimpleReactValidator({
            className: "text-danger",
            validators: {

                whitespace: {
                    message: "The :attribute not allowed first whitespace   characters.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /[^\s\\]/) && params.indexOf(val) === -1);
                    }
                },
                specialChar: {
                    message: "The :attribute not allowed special   characters.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /^[ A-Za-z0-9_@./#&+-]*$/i) && params.indexOf(val) === -1);
                    }
                },
                specialCharText: {
                    message: "The :attribute may only contain letters, dot and spaces.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /^[ A-Za-z_@./#&+-]*$/i) && params.indexOf(val) === -1);
                    }
                }
            }
        });

    }

    onChange = (event) => {
        console.log(this.state.validError);
        this.setState({
            [event.target.name]: event.target.value
        });

        var ref = firebase
            .database()
            .ref('Locations/')
            .orderByChild("location")
            .equalTo(event.target.value);
        ref.on('value', snapshot => {
            var user_exist = snapshot.numChildren();
            console.log(user_exist);
            console.log(this.state.validError);

            if (user_exist > 0 && this.state.validError!=true) {

                this.setState({error_message: "Locations Name is already exist", validError: false});
            } else {
                this.setState({error_message: "", validError: true});

            }

        })

    };
   
    handleSubmit = (event) => {
        event.preventDefault();
        if (this.validator.allValid() && this.state.validError == true) {
         

                var sessionId = sessionStorage.getItem("RoleId");
               

                // let dbCon = firebase.firestore().collection('/category');

                let dbCon = firebase
                    .database()
                    .ref('/Locations');
                dbCon
                    .push({location: this.state.location, 
                        created_on: this.state.date, sessionId: sessionId})
                    .then(response => {
                        try {
                            console.log(response);

                            this
                                .props
                                .history
                                .push("/Locations/Created");

                        } catch (error) {
                            console.log(error.config);
                        }
                    });

            
        } else {
            this
                .validator
                .showMessages();
            this.forceUpdate();
        }

    };

    render() {

        return (
            <div>
                <SidebarPage/>

                <div className="all-content-wrapper">
                    <HeaderPage/>

                    <div className="breadcome-area">
                        <div className="container-fluid">
                            <div className="row">
                                <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                    <div className="breadcome-list">
                                        <div className="row">
                                            <div className="col-lg-6 col-md-6 col-sm-6 col-xs-6">
                                                <div className="breadcomb-wp">

                                                    <div className="breadcomb-ctn">
                                                        <h2>Location</h2>

                                                    </div>

                                                </div>
                                            </div>

                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

                    <div className="single-product-tab-area mg-b-30">

                        <div className="single-pro-review-area">
                            <div className="container-fluid">
                                <div className="row">
                                    <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                        <div className="review-tab-pro-inner">

                                            <h4>Add Location</h4>

                                            <div className="product-tab-list tab-pane fade active in">

                                            <Form onSubmit={this.handleSubmit} id="Add-products">

<div className="row m-b-20">
    <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
        <label>Location Name<span style={{color: 'red'}}>*</span>
        </label>
        <input
            type="text"
            className="form-control"
            placeholder=""
            name="location_Name"
            onChange={this.handleChange}
            value={this.state.location_Name}/> {this
            .validator
            .message("Location Name", this.state.location_Name, "required|specialCharText|whitespace|min:2|max:70")}
        <div className="text-danger">
            {" "}
            {this.state.error_message}
        </div>
    </div>
    <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
        <label>Address 1<span style={{color: 'red'}}>*</span>
        </label>
        <textarea
            type="text"
            className="form-control"
            placeholder=""
            name="addressl"
            onChange={this.handleChange}
           
            value={this.state.addressl}/> {this
            .validator
            .message("Address 1", this.state.addressl, "required|min:5|max:150")}

    </div>
    <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
        <label>Address 2<span style={{color: 'red'}}>*</span>
        </label>
        <textarea
            type="text"
            className="form-control"
            placeholder=""
            name="address2"
            onChange={this.handleChange}
           
            value={this.state.address2}/> {this
            .validator
            .message("Address 2", this.state.address2, "required|min:5|max:150")}

    </div>
    <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
        <label>Land Mark<span style={{color: 'red'}}>*</span>
        </label>
        <input
            type="text"
            className="form-control"
            placeholder=""
            name="land_Mark"
            onChange={this.handleChange}
            value={this.state.land_Mark}/> {this
                .validator
                .message("Land Mark", this.state.land_Mark, "required|min:2|max:70")}
            <div className="text-danger">
                {" "}
                {this.state.error_message}
        </div>
    </div>
    <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
        <label>City<span style={{color: 'red'}}>*</span>
        </label>
        <input
            type="text"
            className="form-control"
            placeholder=""
            name="city"
            onChange={this.handleChange}
            value={this.state.city}/> {this
                .validator
                .message("City ", this.state.city, "required|min:2|max:70")}
            <div className="text-danger">
                {" "}
                {this.state.error_message}
        </div>
    </div>
    <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
        <label>Pincode<span style={{color: 'red'}}>*</span>
        </label>
        <input
            type="text"
            className="form-control"
            placeholder=""
            name="pincode"
            onChange={this.handleChange}
            value={this.state.pincode}/> {this
                .validator
                .message("Pincode ", this.state.pincode, "required|min:2|max:70")}
            <div className="text-danger">
                {" "}
                {this.state.error_message}
        </div>
    </div>
    <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
        <label>Contact Person Name<span style={{color: 'red'}}>*</span>
        </label>
        <input
            type="text"
            className="form-control"
            placeholder=""
            name="contact_Person_Name"
            onChange={this.handleChange}
            value={this.state.contact_Person_Name}/> {this
            .validator
            .message("Contact Person Name", this.state.contact_Person_Name, "required|specialCharText|whitespace|min:2|max:70")}
        <div className="text-danger">
            {" "}
            {this.state.error_message}
        </div>
    </div>
    <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
        <label>E-mail Address<span style={{color: 'red'}}>*</span>
        </label>
        <input
            type="text"
            className="form-control"
            placeholder=""
            name="email"
            id="price_cost"
            onChange={this.handleChange}
            value={this.state.email}/> {this
            .validator
            .message("E-mail Address", this.state.email, "required|email")}

    </div>
   
    {/* <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
        <label>Department<span style={{color: 'red' }}>*</span></label>

        <select
            name="select"
            className="form-control pro-edt-select form-control-primary"
            name="department"
            onChange={this.handleChange}>
            <option>Select Department</option>
            {this.state.departments && this
                .state
                .departments
                .map((data, index) => {

                    return(
                        <option value={data.department} key={index}>{data.department}</option>
                      )
             } )}
            </select>
            
             {this.validator.message("Department", this.state.department, "required")}

      

    </div>

  

    <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
        <label>Designation<span style={{color: 'red' }}>*</span></label>

        <select
            name="select"
            className="form-control pro-edt-select form-control-primary"
            name="designation"
            onChange={this.handleChange}>
            <option>Select Designation</option>
            {this.state.designations && this
                .state
                .designations
                .map((data, index) => {

                    return(
                        <option value={data.designation} key={index}>{data.designation}</option>
                      )
             } )}
            </select>
            
             {this.validator.message("Select Designation", this.state.designation, "required")}

    </div>



        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
        <label>City<span style={{color: 'red' }}>*</span></label>

        <select
            name="select"
            className="form-control pro-edt-select form-control-primary"
            name="city"
            onChange={this.handleChange}>
            <option>Select City</option>
            {this.state.City && this
                .state
                .City
                .map((data, index) => {

                    return(
                        <option value={data.city} key={index}>{data.city}</option>
                      )
             } )}
            </select>
            
             {this.validator.message("City", this.state.city, "required")}

      

    </div> */}
    <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
        <label>Mobile Number<span style={{
color: 'red'
}}>*</span>
        </label>
        <input
            type="text"
            className="form-control"
            placeholder=""
            name="mobile_number"
            onChange={this.handleChange}
            value={this.state.mobile_number}/> {this
            .validator
            .message(" Mobile Number", this.state.mobile_number, "required||numeric|max:10")}

    </div>

 
     <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
        <label>Phone Number (For emergency conatact)<span style={{
color: 'red'
}}>*</span>
        </label>
        <input
            type="text"
            className="form-control"
            placeholder=""
            name="phone_emergency"
            onChange={this.handleChange}
            id="phone_emergency"
            value={this.state.phone_emergency}/> {this
            .validator
            .message(" Phone Number", this.state.phone_emergency, "required|numeric|min:1|max:10")}

    </div>

    <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
        <label>Reporting Manager<span style={{
color: 'red'
}}>*</span>
        </label>
           <select
            className="form-control"
            placeholder=""
            name="reporting_manager"
            value={this.state.reporting_manager}
            onChange={this.handleChange}>
            <option value="" label="Select" selected="selected">Select</option>
            

            <option value="1" label="1">1</option>
            <option value="2" label="2">2</option>
            <option value="3" label="3">3</option>
           
        </select>
          {this
            .validator
            .message(" Reporting Manager", this.state.reporting_manager, "required")}

    </div>
    <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
        <label>Address<span style={{
color: 'red'
}}>*</span>
        </label>
        <textarea
            type="text"
            className="form-control"
            placeholder=""
            name="address"
            onChange={this.handleChange}
           
            value={this.state.address}/> {this
            .validator
            .message("Addresss", this.state.address, "required|min:5|max:150")}

    </div>
    {/* <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
        <label>Password<span style={{ color: 'red'}}>*</span>
        </label>
        <input
         type="Password"
         name="password"
         value=""
         className="form-control"
      
        
           />
    </div> */}
   
</div>

 <div className="row m-b-20">
 <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
        <div className="dropzone-pro">
            <div id="dropzone">

                <div className="dz-message needsclick download-custom">
                    <i className="fa fa-cloud-download" aria-hidden="true"></i>
                    <h2>Upload Photo<span style={{color: 'red' }}>*</span></h2>

                </div>

                {this.state.isUploading && <p>Progress: {this.state.progress}</p>}
                {this.state.avatarURL && <img
                    src={this.state.avatarURL}
                    style={{
                    height: "200px",
                    width: "250px"
                }}/>}
                <FileUploader
                    accept="image/*"
                    name="avatar"
                    randomizeFilename
                    storageRef={firebase
                    .storage()
                    .ref("images")}
                    onUploadStart={this.handleUploadStart}
                    onUploadError={this.handleUploadError}
                    onUploadSuccess={this.handleUploadSuccess}
                    onProgress={this.handleProgress}/> {this
                    .validator
                    .message("Upload  Image", this.state.avatarURL, "required")}

            </div>
        </div>

    </div>

  

</div> 



<div className="row">
    <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
        <div className="text-center custom-pro-edt-ds">
            <button
                type="submit"
                className="btn btn-ctl-bt waves-effect waves-light m-r-10">Save
            </button>
            <Link to="/EmployeeList">
                <button type="button" className="btn btn-ctl-bt waves-effect waves-light">Cancel
                </button>
            </Link>
        </div>
    </div>
</div>
</Form>
                                            </div>

                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
                <FooterPage/>

            </div>
        );
    }
}

export default AddLocations;
