import React from "react";

import {Form} from 'reactstrap';
import HeaderPage from "./header";
import FooterPage from "./footer";
import SidebarPage from "./sidebar";
import firebase from '../config';
import ReactQuill from "react-quill";
import 'react-quill/dist/quill.snow.css';

import ReactNotification from "react-notifications-component";
import "react-notifications-component/dist/theme.css";
import SimpleReactValidator from "simple-react-validator";

class ViewEnquiries extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            pageTitle: '',
            date: new Date().toLocaleString(),
            content: '',
            theme: 'snow'

        }
       


    }
    componentDidMount() {
        const {CMSID} = this.props.match.params;
        console.log(CMSID);

        var ref = firebase
            .database()
            .ref(`websiteEnquiries/${CMSID}`);

        ref.on('value', snapshot => {
            var gstData = snapshot.val();
            console.log(gstData)
            this.setState({name: gstData.name,
                subject: gstData.subject,
                email: gstData.email_id,
                message: gstData.req_message,
                created_on: gstData.created_on, loading: false});
            //console.log(this.state.pageTitle);
        });

    }
  
    

    

    render() {

        const divStyle = {
            'height': '300px'
        };

        return (
            <div>
                <SidebarPage/>

                <div className="all-content-wrapper">
                    <HeaderPage/>

                    <div className="breadcome-area">
                        <div className="container-fluid">
                            <div className="row">
                                <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                    <div className="breadcome-list">
                                        <div className="row">
                                            <div className="col-lg-6 col-md-6 col-sm-6 col-xs-6">
                                                <div className="breadcomb-wp">

                                                    <div className="breadcomb-ctn">
                                                        <h2>Website Enquiries</h2>
                                                    </div>
                                                </div>
                                            </div>

                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

                    <div className="single-product-tab-area mg-b-30">

                        <div className="single-pro-review-area">
                            <div className="container-fluid">
                                <div className="row">
                                    <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                        <div className="review-tab-pro-inner">

                                            <h4>View Enquirie</h4>

                                            <div className="product-tab-list tab-pane fade active in">
                                              

                                            <table class="tablesaw table table-striped table-bordered table-hover" data-tablesaw-mode="stack">
			<tbody>
				<tr>
					<td><strong> Name</strong></td>
					<td>  {this.state.name} </td>					
				</tr>
				<tr>
					<td><strong>Email</strong></td>
					<td>  {this.state.email} </td>					
				</tr>
				
				<tr>
					<td><strong>Subject</strong></td>
					<td>  {this.state.subject} </td>					
				</tr>
				<tr>
					<td><strong>Message</strong></td>
					<td>  {this.state.message} </td>					
				</tr>
				
				<tr>
					<td><strong>Created On</strong></td>
					<td>  {this.state.created_on} 					</td>					
				</tr>
                
			</tbody>
		</table>
                                                
                                                
                                            </div>

                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
                <FooterPage/>

            </div>
        );
    }
}
export default ViewEnquiries;
