import React from "react";
import { Link } from "react-router-dom";
import HeaderPage from "./header";
import FooterPage from "./footer";
// import HomePage from "./home";
import SidebarPage from "./sidebar";
import firebase from '../config';
import Pagination from "react-js-pagination";

class Staff extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
        gstList: [],
        loading: false,
        count: 1,
        activePage: "1",
        countPage: ""
      };
      this.deleteItem = this.deleteItem.bind(this);

  }

  componentDidMount() {
    this.setState({ loading: true });
    var ref = firebase.database().ref("staff/");

    ref.on('value', snapshot => {
        const data = [];
        snapshot.forEach(childSnapShot => {
           
          const GSTData = {
            cmsId: childSnapShot.key.toString(),
            fullName: childSnapShot.val().fullName,
            username: childSnapShot.val().username,
            password: childSnapShot.val().password,
            status: childSnapShot.val().status,
            createdAt: childSnapShot.val().createdAt,
            updatedAt: childSnapShot.val().updatedAt,
          };
  
          data.unshift(GSTData);
        });
  
        this.setState({ gstList:data,countPage: data.length,loading: false });
        console.log(this.state.gstList);
      });
}
    deleteItem = id => {
        

    console.log(id);
    var playersRef = firebase.database().ref(`/SEOPages/${id}`);
    playersRef.remove();

    };

    handlePageChange(pageNumber) {
        console.log(`active page is ${pageNumber}`);
        this.setState({ activePage: pageNumber });
    }


  render() {
    

    return (
      <div>
     <SidebarPage/>
   
    <div className="all-content-wrapper">
    <HeaderPage/>
   
          
            <div className="breadcome-area">
                <div className="container-fluid">
                    <div className="row">
                        <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                            <div className="breadcome-list">
                                <div className="row">
                                    <div className="col-lg-6 col-md-6 col-sm-6 col-xs-6">
                                        <div className="breadcomb-wp">
                                        
                                            <div className="breadcomb-ctn">
                                            <h2>Admin Staff</h2>
                                                </div>
                                        </div>
                                    </div>
                          
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
      
     
            <div className="product-status mg-b-30">
            <div className="container-fluid">
                <div className="row">
                    <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                        <div className="product-status-wrap">
                            <h4>Admin Staff</h4>
                            <div className="add-product">
                                <Link to='/AddStaff'>Add Staff</Link>
                            </div>
                            <table className="table table-bordered paginationTable">
                            <thead>
                                <tr>
                                    <th>S.No</th>
                                    <th>Full Name</th>
                                    <th>Email</th>
                                    <th>Username</th>
                                    <th>Status</th>
									 <th>Created on</th>
                                <th>Action</th>
                                </tr>
                                </thead>
                                <tbody>
                                 
                                    
                                    {this.state.gstList && this.state.gstList.map((data,index) =>{
                                        console.log(data);
                                       
                                        return(
                                         <tr key={index}>
                                         <td>{index+1}</td>
                                         <td>{data.fullName}</td>
                                         <td>{data.email}	</td>
                                         <td>{data.username}</td>
                                         <td>{data.status}	</td>
                                         <td>{data.created_on}	</td>
                                         <td>									
                                            
                                         <Link to={`/EditStaff/${data.cmsId}`}>
                                         <button data-toggle="tooltip" title="Edit" className="pd-setting-ed"><i className="fa fa-pencil-square-o" aria-hidden="true"></i></button>
                                         </Link>								
                     
                                         <button data-toggle="tooltip" title="Trash" className="pd-setting-ed" data-id={data.cmsId}  onClick={this.deleteItem.bind(this, data.cmsId)}><i className="fa fa-trash-o" aria-hidden="true"></i></button>
                                         </td>
                                         
                                         </tr>)

                                    } )}
								

                   
                    </tbody>
                    </table>
                    {/* <div className="custom-pagination">
                            <Pagination
                          hideNavigation
                          activePage={this.state.activePage}
                          itemsCountPerPage={3}
                          totalItemsCount={this.state.countPage}
                          pageRangeDisplayed={3}
                          onChange={this.handlePageChange.bind(this)}
                        />
                            </div> */}
                        </div>
                    </div>
                </div>
            </div>
        </div> 
            
        </div>
        
         <FooterPage/>
    
      </div>
    );
  }
}

export default Staff;
